f5bffd6','Hanno','perl_parse needs null terminated parameter list.','perl-core.c','321','60','2','2','MODIFY','@@ -41,7 +41,7 @@ GSList *perl_scripts;\n PerlInterpreter *my_perl;\n \n static int print_script_errors;\n-static char *perl_args[] = {\"\", \"-e\", \"0\"};\n+static char *perl_args[] = {\"\", \"-e\", \"0\", NULL};\n \n #define IS_PERL_SCRIPT(file) \\\n 	(strlen(file) > 3 && g_strcmp0(file+strlen(file)-3, \".pl\") == 0)\n@@ -123,7 +123,7 @@ void perl_scripts_init(void)\n 	my_perl = perl_alloc();\n 	perl_construct(my_perl);\n \n-	perl_parse(my_perl, xs_init, G_N_ELEMENTS(perl_args), perl_args, NULL);\n+	perl_parse(my_perl, xs_init, G_N_ELEMENTS(perl_args)-1, perl_args, NULL);\n #if PERL_STATIC_LIBS == 1\n 	perl_eval_pv(\"Irssi::Core::->boot_Irssi_Core(0.9);\", TRUE);\n #endif\n','false','false','none'),('irssi','1.0.0','c8dafe2a76f199f35e24440ca85796454717965e','Joseph Bisch','add suppress_printf_fallback\n\nthere are some cases (such as fuzzing with fe-fuzz) where suppressing\nprintf output may be desirable.','printtext.c','400','72','2','0','MODIFY','@@ -446,7 +446,9 @@ static void sig_print_text(TEXT_DEST_REC *dest, const char *text)\n \n 	if (dest->window == NULL) {\n                 str = strip_codes(text);\n+#ifndef SUPPRESS_PRINTF_FALLBACK\n 		printf(\"NO WINDOWS: %s\\n\", str);\n+#endif\n                 g_free(str);\n                 return;\n 	}\n','false','false','not suppress_printf_fallback'),('irssi','1.0.0','6221ce520644efe4095bfecbc29f11c9c6962e0f','Joseph Bisch','fix glib-2.0.m4 so that $pkg_config doesn\'t break configure script\n\nif $pkg_config contains a space, then the test may not work, so\nsurround with quotation mark characters. (for example,\npkg_config=\"pkg-config --static\" breaks configure before this commit).','glib-2.0.m4','199','0','1','1','MODIFY','@@ -43,7 +43,7 @@ AC_ARG_ENABLE(glibtest, [  --disable-glibtest      do not try to compile and run\n   min_glib_version=ifelse([$1], ,2.0.0,$1)\n   AC_MSG_CHECKING(for GLIB - version >= $min_glib_version)\n \n-  if test x$PKG_CONFIG != xno ; then\n+  if test \"x$PKG_CONFIG\" != xno ; then\n     ## don\'t try to run the test against uninstalled libtool libs\n     if $PKG_CONFIG --uninstalled $pkg_config_args; then\n 	  echo \"Will use uninstalled version of GLib found in PKG_CONFIG_PATH\"\n','true','false','none'),('irssi','1.0.0','bc1ff509d2a9dc5592e7c2cb996d0c32cde026ff','ailin-nemui','sync mail.pl','mail.pl','388','0','13','14','MODIFY','@@ -30,6 +30,7 @@ $VERSION = \"2.92\";\n # Check /mailbox help for help.\n \n use Irssi::TextUI;\n+use Irssi;\n \n my $maildirmode = 0; # maildir=1, file(spools)=0\n my $old_is_not_new = 0; \n@@ -37,7 +38,7 @@ my $extprog;\n my ($last_refresh_time, $refresh_tag);\n \n # for mbox caching\n-my $last_size, $last_mtime, $last_mailcount, $last_mode;\n+my ($last_size, $last_mtime, $last_mailcount, $last_mode);\n \n # list of mailboxes\n my %mailboxes = (); \n@@ -101,8 +102,9 @@ sub mbox_count {\n   my $old_is_not_new=Irssi::settings_get_bool(\'mail_oldnotnew\');\n \n   if ($extprog ne \"\") {\n-     $total = `$extprog`;\n-     chomp $unread;\n+     my $total = `$extprog`;\n+     chomp $total;\n+     ($read, $unread) = split \' \', $total, 2;\n   } else {\n     if (!$maildirmode) {\n       if (-f $mailfile) {\n@@ -115,8 +117,7 @@ sub mbox_count {\n 	$last_size = $size;\n 	$last_mtime = $mtime;\n \n-	my $f = gensym;\n-	return 0 if (!open($f, \"<\", $mailfile));\n+	return 0 if (!open(my $f, \"<\", $mailfile));\n \n 	# count new mails only\n 	my $internal_removed = 0;\n@@ -205,7 +206,7 @@ sub mail {\n   my $total = 0;\n \n   # check all mailboxes for new email\n-  foreach $name (keys(%mailboxes)) {\n+  foreach my $name (keys(%mailboxes)) {\n     my $box = $mailboxes{$name};\n     # replace \"~/\" at the beginning by the user\'s home dir\n     $box =~ s/^~\\//$ENV{\'HOME\'}\\//;\n@@ -233,7 +234,7 @@ sub mail {\n     # Show this only if there are any new, unread messages.\n     if (Irssi::settings_get_bool(\'mail_show_message\') &&\n         $unread > $new_mails_in_box{$name}) {\n-      $new_mails = $unread - $new_mails_in_box{$name};\n+      my $new_mails = $unread - $new_mails_in_box{$name};\n       if ($nummailboxes == 1) {\n         Irssi::print(\"You have $new_mails new message\" . ($new_mails != 1 ? \"s.\" : \".\"), MSGLEVEL_CRAP);\n       } else {\n@@ -263,11 +264,9 @@ sub add_mailboxes {\n   my $boxstring = $_[0];\n   my @boxes = split(/,/, $boxstring);\n \n-  foreach $dbox(@boxes) {\n-    my $name = $dbox;\n-    $name = substr($dbox, 0, index($dbox, \'=\'));\n-    my $box = $dbox;\n-    $box = substr($dbox, index($dbox, \'=\') + 1, length($dbox));\n+  foreach my $dbox(@boxes) {\n+    my $name = substr($dbox, 0, index($dbox, \'=\'));\n+    my $box = substr($dbox, index($dbox, \'=\') + 1, length($dbox));\n     addmailbox($name, $box);\n   }\n }\n@@ -306,7 +305,7 @@ sub delmailbox {\n sub update_settings_string {\n   my $setting;\n \n-  foreach $name (keys(%mailboxes)) {\n+  foreach my $name (keys(%mailboxes)) {\n     $setting .= $name . \"=\" . $mailboxes{$name} . \",\";\n   }\n \n@@ -345,7 +344,7 @@ sub cmd_showmailboxes {\n     return;\n   }\n   Irssi::print(\"Mailboxes:\", MSGLEVEL_CRAP);\n-  foreach $box (keys(%mailboxes)) {\n+  foreach my $box (keys(%mailboxes)) {\n     Irssi::print(\"$box: \" . $mailboxes{$box}, MSGLEVEL_CRAP);\n   }\n }\n','false','false','none'),('irssi','1.0.0','876c1dd93e06b9e627918f142f547e3deea682be','ailin-nemui','implement break_wide\n\nfor more pleasant east asian mixed display','gui-windows.c','218','51','7','3','MODIFY','@@ -49,6 +49,7 @@ static GUI_WINDOW_REC *gui_window_init(WINDOW_REC *window,\n 					   settings_get_int(\"indent\"),\n 					   !settings_get_bool(\"indent_always\"),\n 					   get_default_indent_func());\n+	textbuffer_view_set_break_wide(gui->view, settings_get_bool(\"break_wide\"));\n 	if (parent->active == window)\n 		textbuffer_view_set_window(gui->view, parent->screen_win);\n 	return gui;\n@@ -201,12 +202,14 @@ void gui_windows_reset_settings(void)\n \n 	for (tmp = windows; tmp != NULL; tmp = tmp->next) {\n 		WINDOW_REC *rec = tmp->data;\n-                GUI_WINDOW_REC *gui = WINDOW_GUI(rec);\n+		GUI_WINDOW_REC *gui = WINDOW_GUI(rec);\n \n-                textbuffer_view_set_default_indent(gui->view,\n+		textbuffer_view_set_break_wide(gui->view, settings_get_bool(\"break_wide\"));\n+\n+		textbuffer_view_set_default_indent(gui->view,\n 						   settings_get_int(\"indent\"),\n 						   !settings_get_bool(\"indent_always\"),\n-                                                   get_default_indent_func());\n+						   get_default_indent_func());\n \n 		textbuffer_view_set_scroll(gui->view,\n 					   gui->use_scroll ? gui->scroll :\n@@ -281,6 +284,7 @@ void gui_windows_init(void)\n         settings_add_bool(\"lookandfeel\", \"autostick_split_windows\", TRUE);\n 	settings_add_int(\"lookandfeel\", \"indent\", 10);\n 	settings_add_bool(\"lookandfeel\", \"indent_always\", FALSE);\n+	settings_add_bool(\"lookandfeel\", \"break_wide\", FALSE);\n 	settings_add_bool(\"lookandfeel\", \"scroll\", TRUE);\n \n 	window_create_override = -1;\n','false','false','none'),('irssi','1.0.0','876c1dd93e06b9e627918f142f547e3deea682be','ailin-nemui','implement break_wide\n\nfor more pleasant east asian mixed display','textbuffer-view.c','1043','251','11','1','MODIFY','@@ -307,7 +307,7 @@ view_update_line_cache(TEXT_BUFFER_VIEW_REC *view, LINE_REC *line)\n 			continue;\n 		}\n \n-		if (!view->utf8 && char_width > 1) {\n+		if (view->break_wide && char_width > 1) {\n 			last_space = xpos;\n 			last_space_ptr = next_ptr;\n 			last_color = color; last_fg24 = fg24; last_bg24 = bg24;\n@@ -665,6 +665,16 @@ void textbuffer_view_set_default_indent(TEXT_BUFFER_VIEW_REC *view,\n 	view->default_indent_func = indent_func;\n }\n \n+/* Enable breaking of wide chars */\n+void textbuffer_view_set_break_wide(TEXT_BUFFER_VIEW_REC *view,\n+				    gboolean break_wide)\n+{\n+	if (view->break_wide != break_wide) {\n+		view->break_wide = break_wide;\n+		view_reset_cache(view);\n+	}\n+}\n+\n static void view_unregister_indent_func(TEXT_BUFFER_VIEW_REC *view,\n 					INDENT_FUNC indent_func)\n {\n','false','false','none'),('irssi','1.0.0','876c1dd93e06b9e627918f142f547e3deea682be','ailin-nemui','implement break_wide\n\nfor more pleasant east asian mixed display','textbuffer-view.h','82','0','3','0','MODIFY','@@ -59,6 +59,7 @@ struct _TEXT_BUFFER_VIEW_REC {\n 	unsigned int longword_noindent:1;\n 	unsigned int scroll:1; /* scroll down automatically when at bottom */\n 	unsigned int utf8:1; /* use UTF8 in this view */\n+	unsigned int break_wide:1; /* Break wide chars in this view */\n \n 	TEXT_BUFFER_CACHE_REC *cache;\n 	int ypos; /* cursor position - visible area is 0..height-1 */\n@@ -97,6 +98,8 @@ void textbuffer_view_set_default_indent(TEXT_BUFFER_VIEW_REC *view,\n 					int longword_noindent,\n 					INDENT_FUNC indent_func);\n void textbuffer_views_unregister_indent_func(INDENT_FUNC indent_func);\n+void textbuffer_view_set_break_wide(TEXT_BUFFER_VIEW_REC *view,\n+				    gboolean break_wide);\n \n void textbuffer_view_set_scroll(TEXT_BUFFER_VIEW_REC *view, int scroll);\n void textbuffer_view_set_utf8(TEXT_BUFFER_VIEW_REC *view, int utf8);\n','false','false','none'),('irssi','1.0.0','697dd19d887c58930c76de68268d58f2251904d6','LemonBoy','check whether the client certificate is expired.\n\nright now we only warn the user, the connection keeps going.\nfixes #211','network-openssl.c','643','139','29','7','MODIFY','@@ -420,16 +420,38 @@ static GIOChannel *irssi_ssl_get_iochannel(GIOChannel *handle, int port, SERVER_\n \n 	if (mycert && *mycert) {\n 		char *scert = NULL, *spkey = NULL;\n+		FILE *fp;\n 		scert = convert_home(mycert);\n 		if (mypkey && *mypkey)\n 			spkey = convert_home(mypkey);\n-		ERR_clear_error();\n-		if (! SSL_CTX_use_certificate_file(ctx, scert, SSL_FILETYPE_PEM))\n-			g_warning(\"Loading of client certificate \'%s\' failed: %s\", mycert, ERR_reason_error_string(ERR_get_error()));\n-		else if (! SSL_CTX_use_PrivateKey_file(ctx, spkey ? spkey : scert, SSL_FILETYPE_PEM))\n-			g_warning(\"Loading of private key \'%s\' failed: %s\", mypkey ? mypkey : mycert, ERR_reason_error_string(ERR_get_error()));\n-		else if (! SSL_CTX_check_private_key(ctx))\n-			g_warning(\"Private key does not match the certificate\");\n+\n+		if ((fp = fopen(scert, \"r\"))) {\n+			X509 *cert;\n+			/* Let\'s parse the certificate by hand instead of using\n+			 * SSL_CTX_use_certificate_file so that we can validate\n+			 * some parts of it. */\n+			cert = PEM_read_X509(fp, NULL, get_pem_password_callback, (void *)mypass);\n+			if (cert != NULL) {\n+				/* Only the expiration date is checked right now */\n+				if (X509_cmp_current_time(X509_get_notAfter(cert))  <= 0 ||\n+				    X509_cmp_current_time(X509_get_notBefore(cert)) >= 0)\n+					g_warning(\"The client certificate is expired\");\n+\n+				ERR_clear_error();\n+				if (! SSL_CTX_use_certificate(ctx, cert))\n+					g_warning(\"Loading of client certificate \'%s\' failed: %s\", mycert, ERR_reason_error_string(ERR_get_error()));\n+				else if (! SSL_CTX_use_PrivateKey_file(ctx, spkey ? spkey : scert, SSL_FILETYPE_PEM))\n+					g_warning(\"Loading of private key \'%s\' failed: %s\", mypkey ? mypkey : mycert, ERR_reason_error_string(ERR_get_error()));\n+				else if (! SSL_CTX_check_private_key(ctx))\n+					g_warning(\"Private key does not match the certificate\");\n+\n+				X509_free(cert);\n+			} else\n+				g_warning(\"Loading of client certificate \'%s\' failed: %s\", mycert, ERR_reason_error_string(ERR_get_error()));\n+\n+			fclose(fp);\n+		} else\n+			g_warning(\"Could not find client certificate \'%s\'\", scert);\n 		g_free(scert);\n 		g_free(spkey);\n 	}\n','false','false','none'),('irssi','1.0.0','19c51789967a2f63da033e60f6ef08848b9cd144','LemonBoy','prevent a memory leak during the processing of the sasl response.\n\nwe also get rid of an allocation in the process of doing so.','sasl.c','199','35','10','4','MODIFY','@@ -174,10 +174,16 @@ static gboolean sasl_reassemble_incoming(IRC_SERVER_REC *server, const char *fra\n 		*decoded = g_string_new_len(\"\", 0);\n 	} else {\n 		gsize dec_len;\n-		gchar *tmp;\n-\n-		tmp = (gchar *) g_base64_decode(enc_req->str, &dec_len);\n-		*decoded = g_string_new_len(tmp, dec_len);\n+		gint state = 0;\n+		guint save = 0;\n+\n+		/* Since we\'re not going to use the enc_req GString anymore we\n+		 * can perform the decoding in place. */\n+		dec_len = g_base64_decode_step(enc_req->str, enc_req->len,\n+					       (guchar *)enc_req->str,\n+					       &state, &save);\n+		/* A copy of the data is made when the GString is created. */\n+		*decoded = g_string_new_len(enc_req->str, dec_len);\n 	}\n \n 	g_string_free(enc_req, TRUE);\n','false','false','none'),('irssi','1.0.0','73e8a065bd24092a03c0938eb6c93fec3ee0f4d9','LemonBoy','support openssl 1.1.0.\n\n- x509_get_notbefore becomes x509_get0_notbefore\n- x509_get_notafter becomes x509_get0_notafter\n- asn1_string_data becomes asn1_string_get0_data (and drops the const)\n- the whole library is now initialized by openssl_init_ssl\n\ncloses #597','network-openssl.c','633','137','18','1','MODIFY','@@ -32,6 +32,17 @@\n #include <openssl/ssl.h>\n #include <openssl/err.h>\n \n+/* OpenSSL 1.1.0 introduced some backward-incompatible changes to the api */\n+#if (OPENSSL_VERSION_NUMBER >= 0x10100000L) && !defined(LIBRESSL_VERSION_NUMBER)\n+/* The two functions below could be already defined if OPENSSL_API_COMPAT is\n+ * below the 1.1.0 version so let\'s do a clean start */\n+#undef  X509_get_notBefore\n+#undef  X509_get_notAfter\n+#define X509_get_notBefore(x)     X509_get0_notBefore(x)\n+#define X509_get_notAfter(x)      X509_get0_notAfter(x)\n+#define ASN1_STRING_data(x)       ASN1_STRING_get0_data(x)\n+#endif\n+\n /* ssl i/o channel object */\n typedef struct\n {\n@@ -352,13 +363,19 @@ static GIOFuncs irssi_ssl_channel_funcs = {\n \n static gboolean irssi_ssl_init(void)\n {\n+#if (OPENSSL_VERSION_NUMBER >= 0x10100000L) && !defined(LIBRESSL_VERSION_NUMBER)\n+	if (!OPENSSL_init_ssl(OPENSSL_INIT_SSL_DEFAULT, NULL)) {\n+		g_error(\"Could not initialize OpenSSL\");\n+		return FALSE;\n+	}\n+#else\n 	SSL_library_init();\n 	SSL_load_error_strings();\n 	OpenSSL_add_all_algorithms();\n+#endif\n 	ssl_inited = TRUE;\n \n 	return TRUE;\n-\n }\n \n static int get_pem_password_callback(char *buffer, int max_length, int rwflag, void *pass)\n','false','false','none'),('irssi','1.0.0','7c49ed2d17d2325d7bceef632ac38b66a44e8b63','ailin-nemui','merge pull request #602 from mcdutchie/master\n\nmake irssi --with-perl build with separate object directory','Makefile.PL.in','6','0','1','1','MODIFY','@@ -1,4 +1,4 @@\n-use ExtUtils::MakeMaker;our $AM_DEFAULT_VERBOSITY=\'@AM_DEFAULT_VERBOSITY@\';require \"../Makefile_silent.pm\";\n+use ExtUtils::MakeMaker;our $AM_DEFAULT_VERBOSITY=\'@AM_DEFAULT_VERBOSITY@\';require \"@top_srcdir@/src/perl/Makefile_silent.pm\";\n \n WriteMakefile(\'NAME\' => \'Irssi\',\n               \'LIBS\' => \'\',\n','false','false','none'),('irssi','1.0.0','7c49ed2d17d2325d7bceef632ac38b66a44e8b63','ailin-nemui','merge pull request #602 from mcdutchie/master\n\nmake irssi --with-perl build with separate object directory','Makefile.PL.in','7','0','1','1','MODIFY','@@ -1,4 +1,4 @@\n-use ExtUtils::MakeMaker;our $AM_DEFAULT_VERBOSITY=\'@AM_DEFAULT_VERBOSITY@\';require \"../Makefile_silent.pm\";\n+use ExtUtils::MakeMaker;our $AM_DEFAULT_VERBOSITY=\'@AM_DEFAULT_VERBOSITY@\';require \"@top_srcdir@/src/perl/Makefile_silent.pm\";\n \n WriteMakefile(\'NAME\' => \'Irssi::Irc\',\n               \'LIBS\' => \'\',\n','false','false','none'),('irssi','1.0.0','7c49ed2d17d2325d7bceef632ac38b66a44e8b63','ailin-nemui','merge pull request #602 from mcdutchie/master\n\nmake irssi --with-perl build with separate object directory','Makefile.PL.in','7','0','1','1','MODIFY','@@ -1,4 +1,4 @@\n-use ExtUtils::MakeMaker;our $AM_DEFAULT_VERBOSITY=\'@AM_DEFAULT_VERBOSITY@\';require \"../Makefile_silent.pm\";\n+u