ver);\n 	signal_add(\"server sendmsg\", (SIGNAL_FUNC) sig_server_sendmsg);\n \n 	command_set_options(\"connect\", \"4 6 !! -network ssl +ssl_cert +ssl_pkey +ssl_pass ssl_verify +ssl_cafile +ssl_capath +ssl_ciphers +ssl_pinned_cert +ssl_pinned_pubkey tls +tls_cert +tls_pkey +tls_pass tls_verify +tls_cafile +tls_capath +tls_ciphers +tls_pinned_cert +tls_pinned_pubkey +host noproxy -rawlog noautosendcmd\");\n@@ -515,6 +509,5 @@ void chat_commands_deinit(void)\n 	command_unbind(\"foreach channel\", (SIGNAL_FUNC) cmd_foreach_channel);\n 	command_unbind(\"foreach query\", (SIGNAL_FUNC) cmd_foreach_query);\n \n-        signal_remove(\"default command server\", (SIGNAL_FUNC) sig_default_command_server);\n 	signal_remove(\"server sendmsg\", (SIGNAL_FUNC) sig_server_sendmsg);\n }\n','false','false','none'),('irssi','1.0.1','9cae98e6421cb454ecc9b3b48d06cce5a042bb6a','LemonBoy','execute what\'s left in the input queue when the timeout expires.\n\nsimilar to how vim behaves.','keyboard.c','675','147','45','1','MODIFY','@@ -35,6 +35,7 @@\n \n GSList *keyinfos;\n static GHashTable *keys, *default_keys;\n+static int key_timeout;\n \n /* A cache of some sort for key presses that generate a single char only.\n    If the key isn\'t used, used_keys[key] is zero. */\n@@ -48,7 +49,8 @@ static int key_config_frozen;\n \n struct _KEYBOARD_REC {\n 	char *key_state; /* the ongoing key combo */\n-        void *gui_data; /* GUI specific data sent in \"key pressed\" signal */\n+	guint timer_tag; /* used to check when a pending combo has expired */\n+	void *gui_data; /* GUI specific data sent in \"key pressed\" signal */\n };\n \n /* Creates a new \"keyboard\" - this is used only for keeping track of\n@@ -60,6 +62,7 @@ KEYBOARD_REC *keyboard_create(void *data)\n \n 	rec = g_new0(KEYBOARD_REC, 1);\n 	rec->gui_data = data;\n+	rec->timer_tag = 0;\n \n 	signal_emit(\"keyboard created\", 1, rec);\n         return rec;\n@@ -68,6 +71,11 @@ KEYBOARD_REC *keyboard_create(void *data)\n /* Destroys a keyboard */\n void keyboard_destroy(KEYBOARD_REC *keyboard)\n {\n+	if (keyboard->timer_tag > 0) {\n+		g_source_remove(keyboard->timer_tag);\n+		keyboard->timer_tag = 0;\n+	}\n+\n 	signal_emit(\"keyboard destroyed\", 1, keyboard);\n \n         g_free_not_null(keyboard->key_state);\n@@ -592,6 +600,25 @@ static int key_states_search(const unsigned char *combo,\n         return 0;\n }\n \n+static gboolean key_timeout_expired(KEYBOARD_REC *keyboard)\n+{\n+	KEY_REC *rec;\n+\n+	keyboard->timer_tag = 0;\n+\n+	/* So, the timeout has expired with the input queue full, let\'s see if\n+	 * what we\'ve got is bound to some action. */\n+	rec = g_tree_lookup(key_states, keyboard->key_state);\n+	/* Drain the queue anyway. */\n+	g_free_and_null(keyboard->key_state);\n+\n+	if (rec != NULL) {\n+		(void)key_emit_signal(keyboard, rec);\n+	}\n+\n+	return FALSE;\n+}\n+\n int key_pressed(KEYBOARD_REC *keyboard, const char *key)\n {\n 	KEY_REC *rec;\n@@ -601,6 +628,11 @@ int key_pressed(KEYBOARD_REC *keyboard, const char *key)\n 	g_return_val_if_fail(keyboard != NULL, FALSE);\n 	g_return_val_if_fail(key != NULL && *key != \'\\0\', FALSE);\n \n+	if (keyboard->timer_tag > 0) {\n+		g_source_remove(keyboard->timer_tag);\n+		keyboard->timer_tag = 0;\n+	}\n+\n 	if (keyboard->key_state == NULL && key[1] == \'\\0\' &&\n 	    !used_keys[(int) (unsigned char) key[0]]) {\n 		/* fast check - key not used */\n@@ -625,6 +657,13 @@ int key_pressed(KEYBOARD_REC *keyboard, const char *key)\n 	if (g_tree_lookup(key_states, combo) != rec) {\n 		/* key combo continues.. */\n 		keyboard->key_state = combo;\n+		/* respect the timeout if specified by the user */\n+		if (key_timeout > 0) {\n+			keyboard->timer_tag =\n+				g_timeout_add(key_timeout,\n+					      (GSourceFunc) key_timeout_expired,\n+					      keyboard);\n+		}\n                 return 0;\n 	}\n \n@@ -870,6 +909,9 @@ static void read_keyboard_config(void)\n 		key_config_read(tmp->data);\n \n         key_configure_thaw();\n+\n+	/* any positive value other than 0 enables the timeout (in ms). */\n+	key_timeout = settings_get_int(\"key_timeout\");\n }\n \n void keyboard_init(void)\n@@ -883,6 +925,8 @@ void keyboard_init(void)\n         key_config_frozen = 0;\n 	memset(used_keys, 0, sizeof(used_keys));\n \n+	settings_add_int(\"misc\", \"key_timeout\", 0);\n+\n 	key_bind(\"command\", \"Run any command\", NULL, NULL, (SIGNAL_FUNC) sig_command);\n 	key_bind(\"key\", \"Specify name for key binding\", NULL, NULL, (SIGNAL_FUNC) sig_key);\n 	key_bind(\"multi\", \"Run multiple commands\", NULL, NULL, (SIGNAL_FUNC) sig_multi);\n','false','false','none'),('irssi','1.0.1','db85ab7c90f23e00cd444728f6a5b3d1f63254df','LemonBoy','quote the filename when dcc requests are auto accepted.\n\npr #453 forces the user to quote the filenames given to /dcc commands\nwhen they contain spaces but the autoget functionality didn\'t get\nupdated so the filename was always passed without quotes.\n\ncloses #656.','dcc-autoget.c','51','18','1','1','MODIFY','@@ -70,7 +70,7 @@ static void sig_dcc_request(GET_DCC_REC *dcc, const char *nickaddr)\n 	file = dcc_get_download_path(dcc->arg);\n 	str = g_strdup_printf(settings_get_bool(\"dcc_autoresume\") &&\n 			      stat(file, &statbuf) == 0 ?\n-			      \"RESUME %s %s\" : \"GET %s %s\",\n+			      \"RESUME %s \\\"%s\\\"\" : \"GET %s \\\"%s\\\"\",\n 			      dcc->nick, dcc->arg);\n 	signal_emit(\"command dcc\", 2, str, dcc->server);\n         g_free(file);\n','false','false','none'),('irssi','1.0.1','027acffb4208d7e6ba8e229cbf6c3dae6f5dabaf','LemonBoy','handle file names with quotes.\n\nlet\'s repurpose escape_string and make it more flexible by letting us\nchoose the characters to escape.','misc.c','707','237','11','9','MODIFY','@@ -724,18 +724,20 @@ int expand_escape(const char **data)\n 	}\n }\n \n-/* Escape all \'\"\', \"\'\" and \'\\\' chars with \'\\\' */\n-char *escape_string(const char *str)\n+/* Escape all the characters in `what\' with a backslash */\n+char *escape_string(const char *str, const char *what)\n {\n-	char *ret, *p;\n+	const char *p;\n+	char *ret;\n \n-	p = ret = g_malloc(strlen(str)*2+1);\n-	while (*str != \'\\0\') {\n-		if (*str == \'\"\' || *str == \'\\\'\' || *str == \'\\\\\')\n-			*p++ = \'\\\\\';\n-		*p++ = *str++;\n+	ret = g_malloc(strlen(str) * 2 + 1);\n+	for (p = str; *p != \'\\0\'; p++, ret++) {\n+		if (strchr(what, *p) != NULL) {\n+			*ret++ = \'\\\\\';\n+		}\n+		*ret = *p;\n 	}\n-	*p = \'\\0\';\n+	*ret = \'\\0\';\n \n 	return ret;\n }\n','false','false','none'),('irssi','1.0.1','027acffb4208d7e6ba8e229cbf6c3dae6f5dabaf','LemonBoy','handle file names with quotes.\n\nlet\'s repurpose escape_string and make it more flexible by letting us\nchoose the characters to escape.','misc.h','47','0','2','2','MODIFY','@@ -88,8 +88,8 @@ char *stristr_full(const char *data, const char *key);\n char *ascii_strup(char *str);\n char *ascii_strdown(char *str);\n \n-/* Escape all \'\"\', \"\'\" and \'\\\' chars with \'\\\' */\n-char *escape_string(const char *str);\n+/* Escape all the characters in `what\' with a backslash */\n+char *escape_string(const char *str, const char *what);\n \n /* convert all low-ascii (<32) to ^<A..> combinations */\n char *show_lowascii(const char *str);\n','false','false','none'),('irssi','1.0.1','027acffb4208d7e6ba8e229cbf6c3dae6f5dabaf','LemonBoy','handle file names with quotes.\n\nlet\'s repurpose escape_string and make it more flexible by letting us\nchoose the characters to escape.','chat-completion.c','954','227','1','1','MODIFY','@@ -1113,7 +1113,7 @@ static void event_text(const char *data, SERVER_REC *server, WI_ITEM_REC *item)\n 	/* the nick is quoted in case it contains \'-\' character. also\n 	   spaces should work too now :) The nick is also escaped in case\n 	   it contains \'\\\' characters */\n-	target = escape_string(window_item_get_target(item));\n+	target = escape_string(window_item_get_target(item), \"\\\"\'\\\\\");\n 	str = g_strdup_printf(IS_CHANNEL(item) ? \"-channel \\\"%s\\\" %s\" :\n 			      IS_QUERY(item) ? \"-nick \\\"%s\\\" %s\" : \"%s %s\",\n 			      target, line);\n','false','false','none'),('irssi','1.0.1','027acffb4208d7e6ba8e229cbf6c3dae6f5dabaf','LemonBoy','handle file names with quotes.\n\nlet\'s repurpose escape_string and make it more flexible by letting us\nchoose the characters to escape.','dcc-autoget.c','54','18','6','2','MODIFY','@@ -23,6 +23,7 @@\n #include \"masks.h\"\n #include \"settings.h\"\n #include \"servers.h\"\n+#include \"misc.h\"\n \n #include \"dcc-get.h\"\n \n@@ -30,7 +31,7 @@ static void sig_dcc_request(GET_DCC_REC *dcc, const char *nickaddr)\n {\n         struct stat statbuf;\n 	const char *masks;\n-        char *str, *file;\n+        char *str, *file, *esc_arg;\n         int max_size;\n \n         if (!IS_DCC_GET(dcc)) return;\n@@ -68,11 +69,14 @@ static void sig_dcc_request(GET_DCC_REC *dcc, const char *nickaddr)\n \n 	/* ok. but do we want/need to resume? */\n 	file = dcc_get_download_path(dcc->arg);\n+	/* we have to escape the quotes as the whole file name gets quoted */\n+	esc_arg = escape_string(dcc->arg, \"\\\"\");\n 	str = g_strdup_printf(settings_get_bool(\"dcc_autoresume\") &&\n 			      stat(file, &statbuf) == 0 ?\n 			      \"RESUME %s \\\"%s\\\"\" : \"GET %s \\\"%s\\\"\",\n-			      dcc->nick, dcc->arg);\n+			      dcc->nick, esc_arg);\n 	signal_emit(\"command dcc\", 2, str, dcc->server);\n+	g_free(esc_arg);\n         g_free(file);\n 	g_free(str);\n }\n','false','false','none'),('irssi','1.0.1','9aaa6449a0a554c83d40b56ce0a87bd0cba8bdbf','ailin-nemui','make foreach send commands','chat-commands.c','384','117','34','9','MODIFY','@@ -438,41 +438,66 @@ static void cmd_foreach(const char *data, SERVER_REC *server,\n /* SYNTAX: FOREACH SERVER <command> */\n static void cmd_foreach_server(const char *data, SERVER_REC *server)\n {\n-        GSList *list;\n+	GSList *list;\n+	const char *cmdchars;\n+	char *str;\n+\n+	cmdchars = settings_get_str(\"cmdchars\");\n+	str = strchr(cmdchars, *data) != NULL ? g_strdup(data) :\n+		g_strdup_printf(\"%c%s\", *cmdchars, data);\n \n 	list = g_slist_copy(servers);\n 	while (list != NULL) {\n-		signal_emit(\"send command\", 3, data, list->data, NULL);\n-                list = g_slist_remove(list, list->data);\n+		signal_emit(\"send command\", 3, str, list->data, NULL);\n+		list = g_slist_remove(list, list->data);\n 	}\n+\n+	g_free(str);\n }\n \n /* SYNTAX: FOREACH CHANNEL <command> */\n static void cmd_foreach_channel(const char *data)\n {\n-        GSList *list;\n+	GSList *list;\n+	const char *cmdchars;\n+	char *str;\n+\n+	cmdchars = settings_get_str(\"cmdchars\");\n+	str = strchr(cmdchars, *data) != NULL ? g_strdup(data) :\n+		g_strdup_printf(\"%c%s\", *cmdchars, data);\n \n 	list = g_slist_copy(channels);\n 	while (list != NULL) {\n 		CHANNEL_REC *rec = list->data;\n \n-		signal_emit(\"send command\", 3, data, rec->server, rec);\n-                list = g_slist_remove(list, list->data);\n+		signal_emit(\"send command\", 3, str, rec->server, rec);\n+		list = g_slist_remove(list, list->data);\n 	}\n+\n+	g_free(str);\n }\n \n /* SYNTAX: FOREACH QUERY <command> */\n static void cmd_foreach_query(const char *data)\n {\n-        GSList *list;\n+	GSList *list;\n+	const char *cmdchars;\n+	char *str;\n+\n+	cmdchars = settings_get_str(\"cmdchars\");\n+	str = strchr(cmdchars, *data) != NULL ? g_strdup(data) :\n+		g_strdup_printf(\"%c%s\", *cmdchars, data);\n+\n \n 	list = g_slist_copy(queries);\n 	while (list != NULL) {\n 		QUERY_REC *rec = list->data;\n \n-		signal_emit(\"send command\", 3, data, rec->server, rec);\n-                list = g_slist_remove(list, list->data);\n+		signal_emit(\"send command\", 3, str, rec->server, rec);\n+		list = g_slist_remove(list, list->data);\n 	}\n+\n+	g_free(str);\n }\n \n void chat_commands_init(void)\n','false','false','none'),('irssi','1.0.1','9aaa6449a0a554c83d40b56ce0a87bd0cba8bdbf','ailin-nemui','make foreach send commands','window-commands.c','711','178','13','5','MODIFY','@@ -839,23 +839,31 @@ static void cmd_layout(const char *data, SERVER_REC *server, WI_ITEM_REC *item)\n /* SYNTAX: FOREACH WINDOW <command> */\n static void cmd_foreach_window(const char *data)\n {\n-        WINDOW_REC *old;\n-        GSList *list;\n+	WINDOW_REC *old;\n+	GSList *list;\n+	const char *cmdchars;\n+	char *str;\n \n-        old = active_win;\n+	cmdchars = settings_get_str(\"cmdchars\");\n+	str = strchr(cmdchars, *data) != NULL ? g_strdup(data) :\n+		g_strdup_printf(\"%c%s\", *cmdchars, data);\n+\n+	old = active_win;\n \n 	list = g_slist_copy(windows);\n 	while (list != NULL) {\n 		WINDOW_REC *rec = list->data;\n \n 		active_win = rec;\n-		signal_emit(\"send command\", 3, data, rec->active_server,\n+		signal_emit(\"send command\", 3, str, rec->active_server,\n 			    rec->active);\n-                list = g_slist_remove(list, list->data);\n+		list = g_slist_remove(list, list->data);\n 	}\n \n 	if (g_slist_find(windows, old) != NULL)\n 		active_win = old;\n+\n+	g_free(str);\n }\n \n void window_commands_init(void)\n','false','false','none'),('irssi','1.0.1','7c09b72848f99886964266ff531b41c69fe138f5','dequis','fe-netjoin: remove irc servers on \"server disconnected\" signal','fe-netjoin.c','375','91','16','0','MODIFY','@@ -470,6 +470,20 @@ static void read_settings(void)\n 	}\n }\n \n+static void sig_server_disconnected(IRC_SERVER_REC *server)\n+{\n+	NETJOIN_SERVER_REC *netjoin_server;\n+\n+	g_return_if_fail(server != NULL);\n+\n+	if (!IS_IRC_SERVER(server))\n+		return;\n+\n+	if ((netjoin_server = netjoin_find_server(server))) {\n+		netjoin_server_remove(netjoin_server);\n+	}\n+}\n+\n void fe_netjoin_init(void)\n {\n 	settings_add_bool(\"misc\", \"hide_netsplit_quits\", TRUE);\n@@ -480,6 +494,7 @@ void fe_netjoin_init(void)\n \n 	read_settings();\n 	signal_add(\"setup changed\", (SIGNAL_FUNC) read_settings);\n+	signal_add(\"server disconnected\", (SIGNAL_FUNC) sig_server_disconnected);\n }\n \n void fe_netjoin_deinit(void)\n@@ -492,6 +507,7 @@ void fe_netjoin_deinit(void)\n 	}\n \n 	signal_remove(\"setup changed\", (SIGNAL_FUNC) read_settings);\n+	signal_remove(\"server disconnected\", (SIGNAL_FUNC) sig_server_disconnected);\n \n 	signal_remove(\"message quit\", (SIGNAL_FUNC) msg_quit);\n 	signal_remove(\"message join\", (SIGNAL_FUNC) msg_join);\n','false','false','none'),('irssi','1.0.1','5c4e6304ce12a3c94cc58eec01d4ef45db4dabc3','Stephen Oberholtzer','don\'t emit the script destroyed signal before script is actually destroyed\n\nthe script unloading code originally worked like this:\n\n1. destroy package\n2. emit \'script destroyed\' signal\n3. unhook script\'s signal handlers\n\nif a script added a \'script destroyed\' signal handler, unloading\nthat script would cause the \'script destroyed\' signal to be sent to the\n(already destroyed) package.  this would cause a script error, which would\ntrigger a script unload, which would start the whole process over again,\nuntil we run out of heap or stack space and segfault.\n\nthis commit simply reorders the operations so that the \'script destroyed\'\nsignal is sent *after* the script is fully destroyed.','perl-core.c','321','60','2','2','MODIFY','@@ -67,11 +67,11 @@ static void perl_script_destroy(PERL_SCRIPT_REC *script)\n {\n 	perl_scripts = g_slist_remove(perl_scripts, script);\n \n-	signal_emit(\"script destroyed\", 1, script);\n-\n 	perl_signal_remove_script(script);\n 	perl_source_remove_script(script);\n \n+	signal_emit(\"script destroyed\", 1, script);\n+\n 	g_free(script->name);\n 	g_free(script->package);\n         g_free_not_null(script->path);\n','false','false','none'),('irssi','1.0.1','dc99f8d7a5f90eebd4c52cef8d186bf20e2a9912','LemonBoy','properly check the command arguments in tail place.\n\na command requiring an argument and given in tail position would not\nraise an error but silently set the value to the empty string \'\'.','commands.c','711','187','3','2','MODIFY','@@ -567,13 +567,14 @@ static int get_cmd_options(char **data, int ignore_unknown,\n \n 	option = NULL; pos = -1;\n 	for (;;) {\n-		if (**data == \'-\') {\n+		if (**data == \'\\0\' || **data == \'-\') {\n 			if (option != NULL && *optlist[pos] == \'+\') {\n 				/* required argument missing! */\n                                 *data = optlist[pos] + 1;\n 				return CMDERR_OPTION_ARG_MISSING;\n 			}\n-\n+		}\n+		if (**data == \'-\') {\n 			(*data)++;\n 			if (**data == \'-\' && (*data)[1] == \' \') {\n 				/* -- option means end of options even\n','false','false','none'),('irssi','1.0.1','62fd3ac1804d5d45bc2302ceac9ee9c7ef372e1a','ailin-nemui','revert \"quote the filename when dcc requests are auto accepted.\"','misc.c','705','239','9','11','MODIFY','@@ -724,20 +724,18 @@ int expand_escape(const char **data)\n 	}\n }\n \n-/* Escape all the characters in `what\' with a backslash */\n-char *escape_string(const char *str, const char *what)\n+/* Escape all \'\"\', \"\'\" and \'\\\' chars with \'\\\' */\n+char *escape_string(const char *str)\n {\n-	const char *p;\n-	char *ret;\n+	char *ret, *p;\n \n-	ret = g_malloc(strlen(str) * 2 + 1);\n-	for (p = str; *p != \'\\0\'; p++, ret++) {\n-		if (strchr(what, *p) != NULL) {\n-			*ret++ = \'\\\\\';\n-		}\n-		*ret = *p;\n+	p = ret = g_malloc(strlen(str)*2+1);\n+	while (*str != \'\\0\') {\n+		if (*str == \'\"\' || *str == \'\\\'\' || *str == \'\\\\\')\n+			*p++ = \'\\\\\';\n+		*p++ = *str++;\n 	}\n-	*ret = \'\\0\';\n+	*p = \'\\0\';\n \n 	return ret;\n }\n','false','false','none'),('irssi','1.0.1','62fd3ac1804d5d45bc2302ceac9ee9c7ef372e1a','ailin-nemui','revert \"quote the filename when dcc requests are auto accepted.\"','misc.h','47','0','2','2','MODIFY','@@ -88,8 +88,8 @@ char *stristr_full(const char *data, const char *key);\n char *ascii_strup(char *str);\n char *ascii_strdown(char *str);\n \n-/* Escape all the characters in `what\' with a backslash */\n-char *escape_string(const char *str, const char *what);\n+/* Escape all \'\"\', \"\'\" and \'\\\' chars with \'\\\' */\n+char *escape_string(const char *str);\n \n /* convert all low-ascii (<32) to ^<A..> combinations */\n char *show_lowascii(const char *str);\n','false','false','none'),('irssi','1.0.1','62fd3ac1804d5d45bc2302ceac9ee9c7ef372e1a','ailin-nemui','revert \"quote the filename when dcc requests are auto accepted.\"','chat-completion.c','954','227','1','1','MODIFY','@@ -1113,7 +1113,7 @@ static void event_text(const char *data, SERVER_REC *server, WI_ITEM_REC *item)\n 	/* the nick is quoted in case it contains \'-\' character. also\n 	   spaces should work too now :) The nick is also escaped in case\n 	   it contains \'\\\' characters */\n-	target = escape_string(window_item_get_target(item), \"\\\"\'\\\\\");\n+	target = escape_string(window_item_get_target(item));\n 	str = g_strdup_printf(IS_CHANNEL(item) ? \"-channel \\\"%s\\\" %s\" :\n 			      IS_QUERY(item) ? \"-nick \\\"%s\\\" %s\" : \"%s %s\",\n 			      target, line);\n','false','false','none'),('irssi','1.0.1','62fd3ac1804d5d45bc2302ceac9ee9c7ef372e1a','ailin-nemui','revert \"quote the filename when dcc requests are auto accepted.\"','dcc-autoget.c','51','18','3','7','MODIFY','@@ -23,7 +23,6 @@\n #include \"masks.h\"\n #include \"settings.h\"\n #include \"servers.h\"\n-#include \"misc.h\"\n \n #include \"dcc-get.h\"\n \n@@ -31,7 +30,7 @@ static void sig_dcc_request(GET_DCC_REC *dcc, const char *nickaddr)\n {\n         struct stat statbuf;\n 	const char *masks;\n-        char *str, *file, *esc_arg;\n+        char *str, *file;\n         int max_size;\n \n         if (!IS_DCC_GET(dcc)) return;\n@@ -69,14 +68,11 @@ static void sig_dcc_request(GET_DCC_REC *dcc, const char *nickaddr)\n \n 	/* ok. but do we want/need to resume? */\n 	file = dcc_get_download_path(dcc->arg);\n-	/* we have to escape the quotes as the whole file name gets quoted */\n-	esc_arg = escape_string(dcc->arg, \"\\\"\");\n 	str = g_strdup_printf(settings_get_bool(\"dcc_autoresume\") &&\n 			      stat(file, &statbuf) == 0 ?\n-			      \"RESUME %s \\\"%s\\\"\" : \"GET %s \\\"%s\\\"\",\n-			      dcc->nick, esc_arg);\n+			      \"RESUME %s %s\" : \"GET %s %s\",\n+			      dcc->nick, dcc->arg);\n 	signal_emit(\"command dcc\", 2, str, dcc->server);\n-	g_free(esc_arg);\n         g_free(file);\n 	g_free(str);\n }\n','false','true','none'),('irssi','1.0.1','7bd1b80687cc97010094de0d398b429480e0a608','ailin-nemui','fix dcc get\n\nfixes #656','dcc-autoget.c','54','18','6','3','MODIFY','@@ -23,6 +23,7 @@\n #include \"masks.h\"\n #include \"settings.h\"\n #include \"servers.h\"\n+#include \"misc.h\"\n \n #include \"dcc-get.h\"\n \n@@ -30,7 +31,7 @@ static void sig_dcc_request(GET_DCC_REC *dcc, const char *nickaddr)\n {\n         struct stat statbuf;\n 	const char *masks;\n-        char *str, *file;\n+        char *str, *file, *esc_arg;\n         int max_size;\n \n         if (!IS_DCC_GET(dcc)) return;\n@@ -68,11 +69,13 @@ static void sig_dcc_request(GET_DCC_REC *dcc, const char *nickaddr)\n \n 	/* ok. but do we want/need to resume? */\n 	file = dcc_get_download_path(dcc->arg);\n+	esc_arg = escape_string(dcc->arg);\n 	str = g_strdup_printf(settings_get_bool(\"dcc_autoresume\") &&\n 			      stat(file, &statbuf) == 0 ?\n-			      \"RESUME %s %s\" : \"GET %s %s\",\n-			      dcc->nick, dcc->arg);\n+			      \"RESUME %s \\\"%s\\\"\" : \"GET %s \\\"%s\\\"\",\n+			      dcc->nick, esc_arg);\n 	signal_emit(\"command dcc\", 2, str, dcc->server);\n+	g_free(esc_arg);\n         g_free(file);\n 	g_free(str);\n }\n','true','false','none'),('irssi','1.0.1','93c158d8156293bd95e4bf5bdb75cf8d44bc4eab','dequis','expand_escape: expand double backslash as a backslash','misc.c','707','240','2','0','MODIFY','@@ -690,6 +690,8 @@ int expand_escape(const char **data)\n 		return \'\\n\';\n 	case \'e\':\n 		return 27; /* ESC */\n+	case \'\\\\\':\n+		return \'\\\\\';\n \n 	case \'x\':\n                 /* hex digit */\n','false','false','none'),('irssi','1.0.1','22bc7fcc3906ff3dd6e3837e049d37dc352edf94','ailin-nemui','amend forgotten fix\n\n(cherry picked from commit 0ada284a257cfb08da984a78dab24c3ddaf09ec7)','NEWS','2869','0','2','0','MODIFY','@@ -8,6 +8,8 @@ v1.0.1 2017-02-03  The Irssi team <staff@irssi.org>\n 	- Fix regression that broke second level completion (#613, #609).\n 	- Correct missing NULL termination in perl_parse. By Hanno BÃ¶ck (#619).\n 	- Sync broken mail.pl script (#624, #607).\n+	- Prevent a memory leak during the processing of the SASL\n+	  response (GL!8, GL#5)\n \n v1.0.0 2017-01-03  The Irssi team <staff@irssi.org>\n 	* Removed --disable-ipv6 (#408).\n','true','false','none'),('irssi','1.0.2','3930f91edcf4b1e96c9d857233d58f7cb45c8a86','Ailin Nemui','tag as 1.0.2\n\n(cherry picked from commit 2a53853f369b47e42e32e183c8109e3d63808899)','NEWS','2878','0','10','0','MODIFY','@@ -1,5 +1,15 @@\n v1.1-head 2017-xx-xx  The Irssi team <staff@irssi.org>\n \n+v1.0.2 2017-03-10  The Irssi team <staff@irssi.org>\n+	- Prevent some null-pointer crashes (GL!9).\n+	- Fix compilation with OpenSSL 1.1.0 (#628, #597).\n+	- Correct dereferencing of already freed server objects during\n+	  output of netjoins. Found by APic (GL!10, GL#7).\n+	- Fix in command arg parser to detect missing arguments in tail place\n+	  (#652, #651).\n+	- Fix regression that broke incoming DCC file transfers (#667, #656).\n+	- Fix issue with escaping \\ in evaluated strings (#669, #520).\n+\n v1.0.1 2017-02-03  The Irssi team <staff@irssi.org>\n 	- Fix Perl compilation in object dir. By Martijn Dekker (#602, #623).\n 	- Disable EC cryptography on Solaris to fix build (#604, #598).\n','false','false','none'),('irssi','1.0.2','966efced3c178031f5f75a1009acc0ec8f897b4a','ailin-nemui','up abi ver','common.h','42','0','1','1','MODIFY','@@ -6,7 +6,7 @@\n #define IRSSI_GLOBAL_CONFIG \"irssi.conf\" /* config file name in /etc/ */\n #define IRSSI_HOME_CONFIG \"config\" /* config file name in ~/.irssi/ */\n \n-#define IRSSI_ABI_VERSION 8\n+#define IRSSI_ABI_VERSION 9\n \n #define DEFAULT_SERVER_ADD_PORT 6667\n \n','false','false','none'),('irssi','1.0.2','a720c3b5efdcd9b580110441bb22be91a4a16732','Rodrigo Rebello','get back to using pkg-config to check for openssl\n\ncommit 6300dfec7 removed the option to disable ssl support from the\nconfigure script since it became a requirement, but it also removed the\nuse of pkg-config for finding the openssl library and its dependencies.\n\nthis had the unfortunate consequence of breaking the correct detection\nof library flags in many static linking scenarios. in some cases, for\nexample, openssl might have been built with zlib, which requires `-lz`\nto be passed to the linker when doing a static link of the irssi\nexecutable. thus, pkg-config becomes an invaluable tool in such\nsituations, since no guessing work is needed as the openssl .pc file\nprovides all the necessary flags.\n\nso, this commit re-inserts the pkg_check_modules macro in the configure\nscript when looking for openssl. the test using ac_check_lib remains,\nbut only as a last resort in case the one using pkg-config fails.\n\nalso, because the macro am_path_glib_2_0 contains an unconditional call\nto pkg_prog_pkg_config, the openssl checks are moved so that they come\nafter the glib ones in order to avoid doubly checking for the pkg-config\nbinary (pkg_check_modules skips that check if it has been performed\nbefore, but pkg_prog_pkg_config does not).','configure.ac','670','4','15','6','MODIFY','@@ -243,11 +243,6 @@ if test \"x$want_socks\" = \"xyes\"; then\n 	])\n fi\n \n-dnl **\n-dnl ** OpenSSL checks\n-dnl **\n-AC_CHECK_LIB([ssl], [SSL_library_init])\n-\n dnl **\n dnl ** fe-text checks\n dnl **\n@@ -289,7 +284,21 @@ if test -z \"$GLIB_LIBS\"; then\n   AC_ERROR([GLIB is required to build irssi.])\n fi\n \n-LIBS=\"$LIBS $GLIB_LIBS -lssl -lcrypto\"\n+LIBS=\"$LIBS $GLIB_LIBS\"\n+\n+dnl **\n+dnl ** OpenSSL checks\n+dnl **\n+PKG_CHECK_MODULES([OPENSSL], [openssl], [\n+	CFLAGS=\"$CFLAGS $OPENSSL_CFLAGS\"\n+	LIBS=\"$LIBS $OPENSSL_LIBS\"\n+], [\n+	AC_CHECK_LIB([ssl], [SSL_library_init], [\n+		LIBS=\"$LIBS -lssl -lcrypto\"\n+	], [\n+		AC_MSG_ERROR([The OpenSSL library was not found])\n+	])\n+])\n \n dnl **\n dnl ** curses checks\n','false','false','none'),('irssi','1.0.2','449b8a4589f1fcadae44387999260ed5f8c6c2bf','Rodrigo Rebello','remove outdated information from install\n\ndisabling ssl support is no longer an option.','INSTALL','80','0','0','4','MODIFY','@@ -30,10 +30,6 @@ configure options\n \n   Build the irssi proxy (see startup-HOWTO).\n \n-  --disable-ssl\n-\n-  Disable SSL support.\n-\n   --with-perl=[yes|no|module]\n \n   Enable Perl support\n','false','false','none'),('irssi','1.0.2','70f9db3cbdc0a3c6b622e64edbd504592f921892','Stephen Oberholtzer','fix delay at startup when running against glib 2.49.3+\n\nin glib v2.49.3, an optimization was made to eliminate certain\nunnecessary wakeups.  (the specific change was made in\ne4ee3079c5afc3c1c3d2415f20c3e8605728f074). before this change, the\nfirst call to g_main_iteration would always complete immediately.\nin irssi, this effectively reversed the order of the main loop, causing\nthe reload_config check and the dirty_check to run *before* the first\nblocking call to g_main_iteration.\n\nwith the new logic, the first g_main_iteration call now blocks,\npreventing the screen from being refreshed until the user starts typing\nor a timer goes off.  (it also delays processing of sighup, but i\nexpect that is not a common situation.)\n\nthis commit reorders the main loop to wait at the end of the loop,\nrather than the beginning, addressing the problem.\n\n(this closes debian bug #856201.)','irssi.c','224','24','4','4','MODIFY','@@ -317,10 +317,6 @@ int main(int argc, char **argv)\n 	/* Does the same as g_main_run(main_loop), except we\n 	   can call our dirty-checker after each iteration */\n 	while (!quitting) {\n-		term_refresh_freeze();\n-		g_main_iteration(TRUE);\n-                term_refresh_thaw();\n-\n 		if (reload_config) {\n                         /* SIGHUP received, do /RELOAD */\n 			reload_config = FALSE;\n@@ -328,6 +324,10 @@ int main(int argc, char **argv)\n 		}\n \n 		dirty_check();\n+\n+		term_refresh_freeze();\n+		g_main_iteration(TRUE);\n+                term_refresh_thaw();\n 	}\n \n 	g_main_destroy(main_loop);\n','true','true','none'),('irssi','1.0.2','2b9be6e2ed446293008d0e850fd726aad30fbcd2','Stephen Oberholtzer','intentation/whitespace fixes\n\nchange several instances of space-indentation to tabs, matching the\nsurrounding code.','irssi.c','224','24','4','4','MODIFY','@@ -318,21 +318,21 @@ int main(int argc, char **argv)\n 	   can call our dirty-checker after each iteration */\n 	while (!quitting) {\n 		if (reload_config) {\n-                        /* SIGHUP received, do /RELOAD */\n+			/* SIGHUP received, do /RELOAD */\n 			reload_config = FALSE;\n-                        signal_emit(\"command reload\", 1, \"\");\n+			signal_emit(\"command reload\", 1, \"\");\n 		}\n \n 		dirty_check();\n \n 		term_refresh_freeze();\n 		g_main_iteration(TRUE);\n-                term_refresh_thaw();\n+		term_refresh_thaw();\n 	}\n \n 	g_main_destroy(main_loop);\n 	textui_deinit();\n \n-        session_upgrade(); /* if we /UPGRADEd, start the new process */\n+	session_upgrade(); /* if we /UPGRADEd, start the new process */\n 	return 0;\n }\n','true','false','none'),('irssi','1.0.2','a4cc4e0ad7278835d976aa5e75ca2b42443f5bb1','Joseph Bisch','fix off by one error with char_expandos','expandos.c','547','114','1','1','MODIFY','@@ -51,7 +51,7 @@ const char *current_expando = NULL;\n \n static int timer_tag;\n \n-static EXPANDO_REC *char_expandos[255];\n+static EXPANDO_REC *char_expandos[256];\n static GHashTable *expandos;\n static char *last_sent_msg, *last_sent_msg_body;\n static char *last_privmsg_from, *last_public_from;\n','true','false','none'),('irssi','1.0.2','41776d71f76924664bce1562ebe37743ba0cc9b7','Joseph Bisch','fix strange history behavior when history is empty\n\nif text is being entered and then the user presses the up arrow\nfollowed by the down arrow, the expected behavior is to return to the\ntext being entered. prior to this commit that was not the case.\n\nfixes #462','command-history.c','227','55','2','0','MODIFY','@@ -116,6 +116,8 @@ const char *command_history_prev(WINDOW_REC *window, const char *text)\n 		history->pos = history->pos->prev;\n 		if (history->pos == NULL)\n                         history->over_counter++;\n+	} else if (history->lines == 0) {\n+		history->over_counter++;\n 	} else {\n 		history->pos = g_list_last(history->list);\n 	}\n','true','false','none'),('irssi','1.0.2','7c86575b02d4f80539bcd2da3bef8195b963fa92','Joseph Bisch','don\'t allow command history to wrap around\n\nthis changes the behavior of the command history to avoid wrapping back\nto the bottom once the top of the history is reached.','command-history.c','220','53','4','10','MODIFY','@@ -113,11 +113,9 @@ const char *command_history_prev(WINDOW_REC *window, const char *text)\n 	pos = history->pos;\n \n 	if (pos != NULL) {\n-		history->pos = history->pos->prev;\n-		if (history->pos == NULL)\n-                        history->over_counter++;\n-	} else if (history->lines == 0) {\n-		history->over_counter++;\n+		/* don\'t go past the first entry (no wrap around) */\n+		if (history->pos->prev != NULL)\n+			history->pos = history->pos->prev;\n 	} else {\n 		history->pos = g_list_last(history->list);\n 	}\n@@ -128,7 +126,7 @@ const char *command_history_prev(WINDOW_REC *window, const char *text)\n 		command_history_add(history, text);\n 	}\n \n-	return history->pos == NULL ? \"\" : history->pos->data;\n+	return history->pos == NULL ? text : history->pos->data;\n }\n \n const char *command_history_next(WINDOW_REC *window, const char *text)\n@@ -141,10 +139,6 @@ const char *command_history_next(WINDOW_REC *window, const char *text)\n \n 	if (pos != NULL)\n 		history->pos = history->pos->next;\n-	else if (history->over_counter > 0) {\n-		history->over_counter--;\n-		history->pos = history->list;\n-	}\n \n 	if (*text != \'\\0\' &&\n 	    (pos == NULL || g_strcmp0(pos->data, text) != 0)) {\n','false','false','none')