0.4','2bc10386610eba0fe5741228f143e89e1acd2a7d','Tristan','clarified ambiguous autogen.sh error\n\nautogen.sh now instructs the user to install elinks or lynx if needed','autogen.sh','51','4','1','0','MODIFY','@@ -39,6 +39,7 @@ elif type links >/dev/null 2>&1 ; then\n   links -dump docs/faq.html|perl -pe \'s/^ *//; if ($_ eq \"\\n\" && $state eq \"Q\") { $_ = \"\"; } elsif (/^([QA]):/) { $state = $1 } elsif ($_ ne \"\\n\") { $_ = \"   $_\"; };\' > docs/faq.txt\n else\n   echo \"**Error**: No lynx or elinks present\"\n+  echo \"Install lynx or elinks, then run autogen.sh again\"\n   exit 1\n fi\n \n','true','true','none'),('irssi','1.0.4','d971c0292082823182f333a412207a9a8f6ad008','LemonBoy','allow the user to clear the sasl-related fields\n\nthere was no easy way for the user to disable the sasl authentication or\nto clear the username/password once the network was created.\ncloses #718','network.in','53','0','1','0','MODIFY','@@ -36,6 +36,7 @@\n     -sasl_mechanism  Specifies the mechanism to use for the SASL authentication.\n                      At the moment irssi only supports the \'plain\' and the\n                      \'external\' mechanisms.\n+                     Use \'\' to disable the authentication.\n     -sasl_username   Specifies the username to use during the SASL authentication.\n     -sasl_password   Specifies the password to use during the SASL authentication.\n \n','false','false','none'),('irssi','1.0.4','d971c0292082823182f333a412207a9a8f6ad008','LemonBoy','allow the user to clear the sasl-related fields\n\nthere was no easy way for the user to disable the sasl authentication or\nto clear the username/password once the network was created.\ncloses #718','fe-ircnet.c','186','68','3','3','MODIFY','@@ -163,11 +163,11 @@ static void cmd_network_add_modify(const char *data, gboolean add)\n \n 	/* the validity of the parameters is checked in sig_server_setup_fill_chatnet */\n 	value = g_hash_table_lookup(optlist, \"sasl_mechanism\");\n-	if (value != NULL && *value != \'\\0\') rec->sasl_mechanism = g_strdup(value);\n+	if (value != NULL) rec->sasl_mechanism = *value != \'\\0\' ? g_strdup(value) : NULL;\n 	value = g_hash_table_lookup(optlist, \"sasl_username\");\n-	if (value != NULL && *value != \'\\0\') rec->sasl_username = g_strdup(value);\n+	if (value != NULL) rec->sasl_username = *value != \'\\0\' ? g_strdup(value) : NULL;\n 	value = g_hash_table_lookup(optlist, \"sasl_password\");\n-	if (value != NULL && *value != \'\\0\') rec->sasl_password = g_strdup(value);\n+	if (value != NULL) rec->sasl_password = *value != \'\\0\' ? g_strdup(value) : NULL;\n \n 	ircnet_create(rec);\n 	printformat(NULL, NULL, MSGLEVEL_CLIENTNOTICE, IRCTXT_NETWORK_ADDED, name);\n','false','false','none'),('irssi','1.0.4','d971c0292082823182f333a412207a9a8f6ad008','LemonBoy','allow the user to clear the sasl-related fields\n\nthere was no easy way for the user to disable the sasl authentication or\nto clear the username/password once the network was created.\ncloses #718','irc-servers-setup.c','146','52','3','3','MODIFY','@@ -89,6 +89,8 @@ static void sig_server_setup_fill_chatnet(IRC_SERVER_CONNECT_REC *conn,\n \n 	/* Validate the SASL parameters filled by sig_chatnet_read() or cmd_network_add */\n 	conn->sasl_mechanism = SASL_MECHANISM_NONE;\n+	conn->sasl_username = NULL;\n+	conn->sasl_password = NULL;\n \n 	if (ircnet->sasl_mechanism != NULL) {\n 		if (!g_ascii_strcasecmp(ircnet->sasl_mechanism, \"plain\")) {\n@@ -102,9 +104,7 @@ static void sig_server_setup_fill_chatnet(IRC_SERVER_CONNECT_REC *conn,\n 				g_warning(\"The fields sasl_username and sasl_password are either missing or empty\");\n 		}\n 		else if (!g_ascii_strcasecmp(ircnet->sasl_mechanism, \"external\")) {\n-				conn->sasl_mechanism = SASL_MECHANISM_EXTERNAL;\n-				conn->sasl_username = NULL;\n-				conn->sasl_password = NULL;\n+			conn->sasl_mechanism = SASL_MECHANISM_EXTERNAL;\n 		}\n 		else\n 			g_warning(\"Unsupported SASL mechanism \\\"%s\\\" selected\", ircnet->sasl_mechanism);\n','false','false','none'),('irssi','1.0.4','e08e2bd87aceafc2a34d72ca3fd362f2f2ea90eb','Edward Tomasz Napierala','don\'t compute log_dir_create_mode in three different places.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','log.c','441','97','2','3','MODIFY','@@ -33,6 +33,8 @@\n #define DEFAULT_LOG_FILE_CREATE_MODE 600\n \n GSList *logs;\n+int log_file_create_mode;\n+int log_dir_create_mode;\n \n static const char *log_item_types[] = {\n 	\"target\",\n@@ -42,8 +44,6 @@ static const char *log_item_types[] = {\n };\n \n static char *log_timestamp;\n-static int log_file_create_mode;\n-static int log_dir_create_mode;\n static int rotate_tag;\n \n static int log_item_str2type(const char *type)\n@@ -562,7 +562,6 @@ static void read_settings(void)\n 	log_timestamp = g_strdup(settings_get_str(\"log_timestamp\"));\n \n 	log_file_create_mode = octal2dec(settings_get_int(\"log_create_mode\"));\n-\n 	log_dir_create_mode = log_file_create_mode;\n 	if (log_file_create_mode & 0400) log_dir_create_mode |= 0100;\n 	if (log_file_create_mode & 0040) log_dir_create_mode |= 0010;\n','false','false','none'),('irssi','1.0.4','e08e2bd87aceafc2a34d72ca3fd362f2f2ea90eb','Edward Tomasz Napierala','don\'t compute log_dir_create_mode in three different places.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','log.h','44','0','2','0','MODIFY','@@ -35,6 +35,8 @@ struct _LOG_REC {\n };\n \n extern GSList *logs;\n+extern int log_file_create_mode;\n+extern int log_dir_create_mode;\n \n /* Create log record - you still need to call log_update() to actually add it\n    into log list */\n','false','false','none'),('irssi','1.0.4','e08e2bd87aceafc2a34d72ca3fd362f2f2ea90eb','Edward Tomasz Napierala','don\'t compute log_dir_create_mode in three different places.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','rawlog.c','169','37','1','8','MODIFY','@@ -20,6 +20,7 @@\n \n #include \"module.h\"\n #include \"rawlog.h\"\n+#include \"log.h\"\n #include \"