ode & 0040) log_dir_create_mode |= 0010;\n-        if (log_file_create_mode & 0004) log_dir_create_mode |= 0001;\n-\n 	if (autolog_ignore_targets != NULL)\n 		g_strfreev(autolog_ignore_targets);\n \n','false','false','none'),('irssi','1.0.4','42b5e89f6e9fba17111f67441e25db38a737a3bd','Edward Tomasz Napierala','add missing diagnostics for rawlog open().\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','rawlog.c','172','38','3','0','MODIFY','@@ -128,6 +128,9 @@ void rawlog_open(RAWLOG_REC *rawlog, const char *fname)\n 	path = convert_home(fname);\n 	rawlog->handle = open(path, O_WRONLY | O_APPEND | O_CREAT,\n 			      log_file_create_mode);\n+	if (rawlog->handle == -1) {\n+		g_warning(\"rawlog open() failed: %s\", strerror(errno));\n+	}\n 	g_free(path);\n \n 	rawlog_dump(rawlog, rawlog->handle);\n','false','false','none'),('irssi','1.0.4','08dc6734b90389c357c73544023ec614e43b0860','Edward Tomasz Napierala','when rawlog open fails, there\'s no point in trying to write.  don\'t.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','rawlog.c','173','38','4','2','MODIFY','@@ -128,13 +128,15 @@ void rawlog_open(RAWLOG_REC *rawlog, const char *fname)\n 	path = convert_home(fname);\n 	rawlog->handle = open(path, O_WRONLY | O_APPEND | O_CREAT,\n 			      log_file_create_mode);\n+	g_free(path);\n+\n 	if (rawlog->handle == -1) {\n 		g_warning(\"rawlog open() failed: %s\", strerror(errno));\n+		return;\n 	}\n-	g_free(path);\n \n 	rawlog_dump(rawlog, rawlog->handle);\n-	rawlog->logging = rawlog->handle != -1;\n+	rawlog->logging = TRUE;\n }\n \n void rawlog_close(RAWLOG_REC *rawlog)\n','false','false','none'),('irssi','1.0.4','2914498b291ea0ca432a320004aa3d2499e50eb5','Edward Tomasz Napierala','consistency: use false instead of 0.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','rawlog.c','173','38','1','1','MODIFY','@@ -144,7 +144,7 @@ void rawlog_close(RAWLOG_REC *rawlog)\n 	if (rawlog->logging) {\n 		write_buffer_flush();\n 		close(rawlog->handle);\n-		rawlog->logging = 0;\n+		rawlog->logging = FALSE;\n 	}\n }\n \n','false','false','none'),('irssi','1.0.4','2907a82b446d9d0a25157aa4ab560b49853f80d2','Edward Tomasz Napierala','fix vertical alignment in ./configure --help output.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','configure.ac','671','4','1','1','MODIFY','@@ -158,7 +158,7 @@ AC_ARG_ENABLE(true-color,\n 	want_truecolor=no)\n \n AC_ARG_ENABLE(gregex,\n-[  --disable-gregex     Build without GRegex (fall back to regex.h)],\n+[  --disable-gregex        Build without GRegex (fall back to regex.h)],\n 	if test x$enableval = xno ; then\n 		want_gregex=no\n 	else\n','true','false','none'),('irssi','1.0.4','f468008b25c7218a03cc0b327082d4d69d5481ef','Edward Tomasz Napierala','make autotools detect capsicum.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','configure.ac','692','4','22','0','MODIFY','@@ -166,6 +166,15 @@ AC_ARG_ENABLE(gregex,\n 	fi,\n 	want_gregex=yes)\n \n+AC_ARG_WITH(capsicum,\n+[  --with-capsicum            Build with Capsicum support],\n+	if test x$withval = xno; then\n+		want_capsicum=no\n+	else\n+		want_capsicum=yes\n+	fi,\n+	want_capsicum=yes)\n+\n dnl **\n dnl ** just some generic stuff...\n dnl **\n@@ -499,6 +508,18 @@ if test \"$want_perl\" != \"no\"; then\n 	fi\n fi\n \n+dnl **\n+dnl ** check for capsicum\n+dnl **\n+\n+if test \"x$want_capsicum\" = \"xyes\"; then\n+	AC_CHECK_LIB(c, cap_enter, [\n+		AC_DEFINE(HAVE_CAPSICUM,, Build with Capsicum support)\n+	], [\n+		want_capsicum=\"no, cap_enter not found\"\n+	])\n+fi\n+\n dnl ** check what we want to build\n AM_CONDITIONAL(BUILD_TEXTUI, test \"$want_textui\" = \"yes\")\n AM_CONDITIONAL(BUILD_IRSSIBOT, test \"$want_irssibot\" = \"yes\")\n@@ -713,6 +734,7 @@ echo\n echo \"Building with 64bit DCC support .. : $offt_64bit\"\n echo \"Building with true color support.. : $want_truecolor\"\n echo \"Building with GRegex ............. : $want_gregex\"\n+echo \"Building with Capsicum ........... : $want_capsicum\"\n \n echo\n echo \"If there are any problems, read the INSTALL file.\"\n','false','false','none'),('irssi','1.0.4','56c94570b420dea28b253af5f2c91184423acee1','Edward Tomasz Napierala','implement /cap_enter.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','configure.ac','693','4','2','1','MODIFY','@@ -167,7 +167,7 @@ AC_ARG_ENABLE(gregex,\n 	want_gregex=yes)\n \n AC_ARG_WITH(capsicum,\n-[  --with-capsicum            Build with Capsicum support],\n+[  --with-capsicum         Build with Capsicum support],\n 	if test x$withval = xno; then\n 		want_capsicum=no\n 	else\n@@ -526,6 +526,7 @@ AM_CONDITIONAL(BUILD_IRSSIBOT, test \"$want_irssibot\" = \"yes\")\n AM_CONDITIONAL(BUILD_IRSSIFUZZER, test \"$want_irssifuzzer\" = \"yes\")\n AM_CONDITIONAL(BUILD_IRSSIPROXY, test \"$want_irssiproxy\" = \"yes\")\n AM_CONDITIONAL(HAVE_PERL, test \"$want_perl\" != \"no\")\n+AM_CONDITIONAL(HAVE_CAPSICUM, test \"x$want_capsicum\" = \"xyes\")\n AM_CONDITIONAL(USE_GREGEX, test \"x$want_gregex\" = \"xyes\")\n \n # move LIBS to PROG_LIBS so they\'re not tried to be used when linking eg. perl libraries\n','false','false','none'),('irssi','1.0.4','56c94570b420dea28b253af5f2c91184423acee1','Edward Tomasz Napierala','implement /cap_enter.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','Makefile.am','110','0','5','0','MODIFY','@@ -56,6 +56,11 @@ libcore_a_SOURCES = \\\n 	tls.c \\\n 	write-buffer.c\n \n+if HAVE_CAPSICUM\n+libcore_a_SOURCES += \\\n+	capsicum.c\n+endif\n+\n structure_headers = \\\n 	channel-rec.h \\\n 	channel-setup-rec.h \\\n','false','false','none'),('irssi','1.0.4','56c94570b420dea28b253af5f2c91184423acee1','Edward Tomasz Napierala','implement /cap_enter.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','capsicum.c','20','4','46','0','ADD','@@ -0,0 +1,46 @@\n+/*\n+ capsicum.c : Capsicum sandboxing support\n+\n+    Copyright (C) 2017 Edward Tomasz Napierala <trasz@FreeBSD.org>\n+\n+    This program is free software; you can redistribute it and/or modify\n+    it under the terms of the GNU General Public License as published by\n+    the Free Software Foundation; either version 2 of the License, or\n+    (at your option) any later version.\n+\n+    This program is distributed in the hope that it will be useful,\n+    but WITHOUT ANY WARRANTY; without even the implied warranty of\n+    MERCHANTABILITY or FITNESS FOR A PARTICULAR PURPOSE.  See the\n+    GNU General Public License for more details.\n+\n+    You should have received a copy of the GNU General Public License along\n+    with this program; if not, write to the Free Software Foundation, Inc.,\n+    51 Franklin Street, Fifth Floor, Boston, MA 02110-1301 USA.\n+*/\n+\n+#include \"module.h\"\n+#include \"signals.h\"\n+#include \"commands.h\"\n+\n+#include <sys/capsicum.h>\n+#include <string.h>\n+\n+static void cmd_cap_enter(void)\n+{\n+	int error;\n+\n+	error = cap_enter();\n+	if (error != 0)\n+		g_error(\"cap_enter(2) failed: %s\", strerror(errno));\n+}\n+\n+void capsicum_init(void)\n+{\n+\n+	command_bind(\"cap_enter\", NULL, (SIGNAL_FUNC) cmd_cap_enter);\n+}\n+\n+void capsicum_deinit(void)\n+{\n+	command_unbind(\"cap_enter\", (SIGNAL_FUNC) cmd_cap_enter);\n+}\n','false','true','none'),('irssi','1.0.4','56c94570b420dea28b253af5f2c91184423acee1','Edward Tomasz Napierala','implement /cap_enter.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','capsicum.h','2','0','7','0','ADD','@@ -0,0 +1,7 @@\n+#ifndef __CAPSICUM_H\n+#define __CAPSICUM_H\n+\n+void capsicum_init(void);\n+void capsicum_deinit(void);\n+\n+#endif\n','false','false','none'),('irssi','1.0.4','56c94570b420dea28b253af5f2c91184423acee1','Edward Tomasz Napierala','implement /cap_enter.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','core.c','224','24','3','0','MODIFY','@@ -29,6 +29,7 @@\n #include \"signals.h\"\n #include \"settings.h\"\n #include \"session.h\"\n+#include \"capsicum.h\"\n \n #include \"chat-protocols.h\"\n #include \"servers.h\"\n@@ -235,6 +236,7 @@ void core_init(void)\n 	commands_init();\n 	nickmatch_cache_init();\n         session_init();\n+	capsicum_init();\n \n 	chat_protocols_init();\n 	chatnets_init();\n@@ -292,6 +294,7 @@ void core_deinit(void)\n 	chatnets_deinit();\n 	chat_protocols_deinit();\n \n+	capsicum_deinit();\n         session_deinit();\n         nickmatch_cache_deinit();\n 	commands_deinit();\n','false','false','none'),('irssi','1.0.4','d5d45c29b8e00c304a6b6656983cafee84fe6bc8','Edward Tomasz Napierala','add capability mode error/success messages.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','capsicum.c','38','7','22','2','MODIFY','@@ -30,17 +30,37 @@ static void cmd_cap_enter(void)\n 	int error;\n \n 	error = cap_enter();\n-	if (error != 0)\n-		g_error(\"cap_enter(2) failed: %s\", strerror(errno));\n+	if (error != 0) {\n+		signal_emit(\"capability mode failed\", 1, strerror(errno));\n+	} else {\n+		signal_emit(\"capability mode enabled\", 0);\n+	}\n+}\n+\n+static void cmd_cap_getmode(void)\n+{\n+	u_int mode;\n+	int error;\n+\n+	error = cap_getmode(&mode);\n+	if (error != 0) {\n+		signal_emit(\"capability mode failed\", 1, strerror(errno));\n+	} else if (mode == 0) {\n+		signal_emit(\"capability mode disabled\", 0);\n+	} else {\n+		signal_emit(\"capability mode enabled\", 0);\n+	}\n }\n \n void capsicum_init(void)\n {\n \n 	command_bind(\"cap_enter\", NULL, (SIGNAL_FUNC) cmd_cap_enter);\n+	command_bind(\"cap_getmode\", NULL, (SIGNAL_FUNC) cmd_cap_getmode);\n }\n \n void capsicum_deinit(void)\n {\n 	command_unbind(\"cap_enter\", (SIGNAL_FUNC) cmd_cap_enter);\n+	command_unbind(\"cap_getmode\", (SIGNAL_FUNC) cmd_cap_getmode);\n }\n','true','false','none'),('irssi','1.0.4','d5d45c29b8e00c304a6b6656983cafee84fe6bc8','Edward Tomasz Napierala','add capability mode error/success messages.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','Makefile.am','65','0','5','0','MODIFY','@@ -38,6 +38,11 @@ libfe_common_core_a_SOURCES = \\\n 	windows-layout.c \\\n 	fe-windows.c\n \n+if HAVE_CAPSICUM\n+libfe_common_core_a_SOURCES += \\\n+	fe-capsicum.c\n+endif\n+\n pkginc_fe_common_coredir=$(pkgincludedir)/src/fe-common/core\n pkginc_fe_common_core_HEADERS = \\\n 	command-history.h \\\n','true','false','none'),('irssi','1.0.4','d5d45c29b8e00c304a6b6656983cafee84fe6bc8','Edward Tomasz Napierala','add capability mode error/success messages.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','fe-capsicum.c','30','5','60','0','ADD','@@ -0,0 +1,60 @@\n+/*\n+ fe-capsicum.c : irssi\n+\n+    Copyright (C) 2017 Edward Tomasz Napierala <trasz@FreeBSD.org>\n+\n+    This program is free software; you can redistribute it and/or modify\n+    it under the terms of the GNU General Public License as published by\n+    the Free Software Foundation; either version 2 of the License, or\n+    (at your option) any later version.\n+\n+    This program is distributed in the hope that it will be useful,\n+    but WITHOUT ANY WARRANTY; without even the implied warranty of\n+    MERCHANTABILITY or FITNESS FOR A PARTICULAR PURPOSE.  See the\n+    GNU General Public License for more details.\n+\n+    You should have received a copy of the GNU General Public License along\n+    with this program; if not, write to the Free Software Foundation, Inc.,\n+    51 Franklin Street, Fifth Floor, Boston, MA 02110-1301 USA.\n+*/\n+\n+#include \"module.h\"\n+#include \"module-formats.h\"\n+#include \"signals.h\"\n+#include \"printtext.h\"\n+\n+#include \"levels.h\"\n+#include \"servers.h\"\n+\n+static void capability_mode_enabled(void)\n+{\n+\n+	printformat(NULL, NULL, MSGLEVEL_CLIENTNOTICE, TXT_CAPSICUM_ENABLED);\n+}\n+\n+static void capability_mode_disabled(void)\n+{\n+\n+	printformat(NULL, NULL, MSGLEVEL_CLIENTNOTICE, TXT_CAPSICUM_DISABLED);\n+}\n+\n+static void capability_mode_failed(gchar *msg)\n+{\n+\n+	printformat(NULL, NULL, MSGLEVEL_CLIENTERROR, TXT_CAPSICUM_FAILED, msg);\n+}\n+\n+void fe_capsicum_init(void)\n+{\n+\n+	signal_add(\"capability mode enabled\", (SIGNAL_FUNC) capability_mode_enabled);\n+	signal_add(\"capability mode disabled\", (SIGNAL_FUNC) capability_mode_disabled);\n+	signal_add(\"capability mode failed\", (SIGNAL_FUNC) capability_mode_failed);\n+}\n+\n+void fe_capsicum_deinit(void)\n+{\n+	signal_remove(\"capability mode enabled\", (SIGNAL_FUNC) capability_mode_enabled);\n+	signal_remove(\"capability mode disabled\", (SIGNAL_FUNC) capability_mode_disabled);\n+	signal_remove(\"capability mode failed\", (SIGNAL_FUNC) capability_mode_failed);\n+}\n','true','false','none'),('irssi','1.0.4','d5d45c29b8e00c304a6b6656983cafee84fe6bc8','Edward Tomasz Napierala','add capability mode error/success messages.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','fe-capsicum.h','2','0','7','0','ADD','@@ -0,0 +1,7 @@\n+#ifndef __FE_CAPSICUM_H\n+#define __FE_CAPSICUM_H\n+\n+void fe_capsicum_init(void);\n+void fe_capsicum_deinit(void);\n+\n+#endif\n','true','false','none'),('irssi','1.0.4','d5d45c29b8e00c304a6b6656983cafee84fe6bc8','Edward Tomasz Napierala','add capability mode error/success messages.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','fe-common-core.c','377','52','3','0','MODIFY','@@ -32,6 +32,7 @@\n #include \"special-vars.h\"\n #include \"fe-core-commands.h\"\n #include \"fe-queries.h\"\n+#include \"fe-capsicum.h\"\n #include \"hilight-text.h\"\n #include \"command-history.h\"\n #include \"completion.h\"\n@@ -179,6 +180,7 @@ void fe_common_core_init(void)\n 	fe_server_init();\n 	fe_settings_init();\n 	fe_tls_init();\n+	fe_capsicum_init();\n 	windows_init();\n 	window_activity_init();\n 	window_commands_init();\n@@ -221,6 +223,7 @@ void fe_common_core_deinit(void)\n 	fe_server_deinit();\n 	fe_settings_deinit();\n 	fe_tls_deinit();\n+	fe_capsicum_deinit();\n 	windows_deinit();\n 	window_activity_deinit();\n 	window_commands_deinit();\n','true','false','none'),('irssi','1.0.4','d5d45c29b8e00c304a6b6656983cafee84fe6bc8','Edward Tomasz Napierala','add capability mode error/success messages.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','module-formats.c','244','0','3','0','MODIFY','@@ -290,6 +290,9 @@ FORMAT_REC fecommon_core_formats[] = {\n 	{ \"completion_header\", \"%#Key        Value                                    Auto\", 0 },\n 	{ \"completion_line\", \"%#$[10]0 $[!40]1 $2\", 3, { 0, 0, 0 } },\n 	{ \"completion_footer\", \"\", 0 },\n+	{ \"capsicum_enabled\", \"Capability mode enabled\", 0 },\n+	{ \"capsicum_disabled\", \"Capability mode not enabled\", 0 },\n+	{ \"capsicum_failed\", \"Capability mode failed: $0\", 1, { 0 } },\n \n 	/* ---- */\n 	{ NULL, \"TLS\", 0 },\n','true','false','none'),('irssi','1.0.4','d5d45c29b8e00c304a6b6656983cafee84fe6bc8','Edward Tomasz Napierala','add capability mode error/success messages.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','module-formats.h','243','0','3','0','MODIFY','@@ -255,6 +255,9 @@ enum {\n 	TXT_COMPLETION_HEADER,\n         TXT_COMPLETION_LINE,\n 	TXT_COMPLETION_FOOTER,\n+	TXT_CAPSICUM_ENABLED,\n+	TXT_CAPSICUM_DISABLED,\n+	TXT_CAPSICUM_FAILED,\n \n 	TLS_FILL_15,\n \n','true','false','none'),('irssi','1.0.4','3200c381dbcb02e99c7667bd955c90ee859d15a0','Edward Tomasz Napierala','rename to \"/capability enter\" and \"/capability status\".\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','capsicum.c','44','8','13','6','MODIFY','@@ -25,7 +25,12 @@\n #include <sys/capsicum.h>\n #include <string.h>\n \n-static void cmd_cap_enter(void)\n+static void cmd_capsicum(const char *data, SERVER_REC *server, void *item)\n+{\n+	command_runsub(\"capsicum\", data, server, item);\n+}\n+\n+static void cmd_capsicum_enter(void)\n {\n 	int error;\n \n@@ -37,7 +42,7 @@ static void cmd_cap_enter(void)\n 	}\n }\n \n-static void cmd_cap_getmode(void)\n+static void cmd_capsicum_status(void)\n {\n 	u_int mode;\n 	int error;\n@@ -55,12 +60,14 @@ static void cmd_cap_getmode(void)\n void capsicum_init(void)\n {\n \n-	command_bind(\"cap_enter\", NULL, (SIGNAL_FUNC) cmd_cap_enter);\n-	command_bind(\"cap_getmode\", NULL, (SIGNAL_FUNC) cmd_cap_getmode);\n+	command_bind(\"capsicum\", NULL, (SIGNAL_FUNC) cmd_capsicum);\n+	command_bind(\"capsicum enter\", NULL, (SIGNAL_FUNC) cmd_capsicum_enter);\n+	command_bind(\"capsicum status\", NULL, (SIGNAL_FUNC) cmd_capsicum_status);\n }\n \n void capsicum_deinit(void)\n {\n-	command_unbind(\"cap_enter\", (SIGNAL_FUNC) cmd_cap_enter);\n-	command_unbind(\"cap_getmode\", (SIGNAL_FUNC) cmd_cap_getmode);\n+	command_unbind(\"capsicum\", (SIGNAL_FUNC) cmd_capsicum);\n+	command_unbind(\"capsicum enter\", (SIGNAL_FUNC) cmd_capsicum_enter);\n+	command_unbind(\"capsicum status\", (SIGNAL_FUNC) cmd_capsicum_status);\n }\n','false','false','none'),('irssi','1.0.4','cec68557aa436b4f16505d2095af02f83d154eda','Edward Tomasz Napierala','/connect works - although only with ip addresses.','configure.ac','698','4','6','1','MODIFY','@@ -514,7 +514,12 @@ dnl **\n \n if test \"x$want_capsicum\" = \"xyes\"; then\n 	AC_CHECK_LIB(c, cap_enter, [\n-		AC_DEFINE(HAVE_CAPSICUM,, Build with Capsicum support)\n+		AC_CHECK_LIB(nv, nvlist_create, [\n+			AC_DEFINE(HAVE_CAPSICUM,, Build with Capsicum support)\n+			LIBS=\"$LIBS -lnv\"\n+		], [\n+			want_capsicum=\"no, nvlist_create not found\"\n+		])\n 	], [\n 		want_capsicum=\"no, cap_enter not found\"\n 	])\n','false','false','none'),('irssi','1.0.4','cec68557aa436b4f16505d2095af02f83d154eda','Edward Tomasz Napierala','/connect works - although only with ip addresses.','capsicum.c','145','26','126','0','MODIFY','@@ -19,21 +19,147 @@\n */\n \n #include \"module.h\"\n+#include \"network.h\"\n #include \"signals.h\"\n #include \"commands.h\"\n \n+#include <sys/types.h>\n #include <sys/capsicum.h>\n+#include <sys/nv.h>\n+#include <sys/procdesc.h>\n+#include <sys/socket.h>\n #include <string.h>\n \n+static int symbiontfds[2];\n+\n+gboolean capsicum_enabled(void)\n+{\n+	u_int mode;\n+	int error;\n+\n+	error = cap_getmode(&mode);\n+	if (error != 0)\n+		return FALSE;\n+\n+	if (mode == 0)\n+		return FALSE;\n+\n+	return TRUE;\n+}\n+\n+int capsicum_net_connect_ip(IPADDR *ip, int port, IPADDR *my_ip)\n+{\n+	nvlist_t *nvl;\n+	int error, saved_errno, sock;\n+\n+	/* Send request to the symbiont. */\n+	nvl = nvlist_create(0);\n+	nvlist_add_binary(nvl, \"ip\", ip, sizeof(*ip));\n+	nvlist_add_number(nvl, \"port\", port);\n+	if (my_ip != NULL) {\n+		/* nvlist_add_binary(3) can\'t handle NULL values. */\n+		nvlist_add_binary(nvl, \"my_ip\", my_ip, sizeof(*my_ip));\n+	}\n+	error = nvlist_send(symbiontfds[1], nvl);\n+	nvlist_destroy(nvl);\n+	if (error != 0) {\n+		g_warning(\"nvlist_send: %s\", strerror(errno));\n+		return -1;\n+	}\n+\n+	/* Receive response. */\n+	nvl = nvlist_recv(symbiontfds[1], 0);\n+	if (nvl == NULL) {\n+		g_warning(\"nvlist_recv: %s\", strerror(errno));\n+		return -1;\n+	}\n+	if (nvlist_exists_descriptor(nvl, \"sock\")) {\n+		sock = nvlist_take_descriptor(nvl, \"sock\");\n+	} else {\n+		sock = -1;\n+	}\n+	saved_errno = nvlist_get_number(nvl, \"errno\");\n+	nvlist_destroy(nvl);\n+	errno = saved_errno;\n+\n+	return sock;\n+}\n+\n static void cmd_capsicum(const char *data, SERVER_REC *server, void *item)\n {\n 	command_runsub(\"capsicum\", data, server, item);\n }\n \n+static int start_symbiont(void)\n+{\n+	pid_t pid;\n+	nvlist_t *nvl;\n+	IPADDR *ip, *my_ip;\n+	int childfd, error, port, saved_errno, sock;\n+\n+	error = socketpair(PF_UNIX, SOCK_STREAM | SOCK_CLOEXEC, 0, symbiontfds);\n+	if (error != 0) {\n+		g_warning(\"socketpair: %s\", strerror(errno));\n+		return 1;\n+	}\n+\n+	pid = pdfork(&childfd, PD_CLOEXEC);\n+	if (pid < 0) {\n+		g_warning(\"pdfork: %s\", strerror(errno));\n+		return 1;\n+	}\n+\n+	if (pid > 0) {\n+		close(symbiontfds[0]);\n+		return 0;\n+	}\n+\n+	/* We\'re the child, running outside the Capsicum sandbox. */\n+	setproctitle(\"capsicum symbiont\");\n+	close(symbiontfds[1]);\n+	close(0);\n+	close(1);\n+	close(2);\n+	for (;;) {\n+		/* Receive parameters from the main irssi process. */\n+		nvl = nvlist_recv(symbiontfds[0], 0);\n+		if (nvl == NULL)\n+			exit(1);\n+		ip = nvlist_get_binary(nvl, \"ip\", NULL);\n+		port = (int)nvlist_get_number(nvl, \"port\");\n+		if (nvlist_exists(nvl, \"my_ip\"))\n+			my_ip = nvlist_get_binary(nvl, \"my_ip\", NULL);\n+		else\n+			my_ip = NULL;\n+\n+		/* Connect. */\n+		sock = net_connect_ip_handle(ip, port, my_ip);\n+		saved_errno = errno;\n+\n+		/* Send back the socket fd. */\n+		nvlist_destroy(nvl);\n+		nvl = nvlist_create(0);\n+\n+		if (sock != -1)\n+			nvlist_move_descriptor(nvl, \"sock\", sock);\n+		nvlist_add_number(nvl, \"errno\", saved_errno);\n+		error = nvlist_send(symbiontfds[0], nvl);\n+		if (error != 0)\n+			exit(1);\n+		nvlist_destroy(nvl);\n+	}\n+}\n+\n static void cmd_capsicum_enter(void)\n {\n 	int error;\n \n+	error = start_symbiont();\n+	if (error != 0) {\n+		signal_emit(\"capability mode failed\", 1, strerror(errno));\n+		return;\n+	}\n+\n 	error = cap_enter();\n 	if (error != 0) {\n 		signal_emit(\"capability mode failed\", 1, strerror(errno));\n','false','false','none'),('irssi','1.0.4','cec68557aa436b4f16505d2095af02f83d154eda','Edward Tomasz Napierala','/connect works - although only with ip addresses.','capsicum.h','4','0','3','0','MODIFY','@@ -1,6 +1,9 @@\n #ifndef __CAPSICUM_H\n #define __CAPSICUM_H\n \n+gboolean capsicum_enabled(void);\n+int capsicum_net_connect_ip(IPADDR *ip, int port, IPADDR *my_ip);\n+\n void capsicum_init(void);\n void capsicum_deinit(void);\n \n','false','false','none'),('irssi','1.0.4','cec68557aa436b4f16505d2095af02f83d154eda','Edward Tomasz Napierala','/connect works - although only with ip addresses.','network.c','388','102','28','5','MODIFY','@@ -20,6 +20,9 @@\n \n #include \"module.h\"\n #include \"network.h\"\n+#ifdef HAVE_CAPSICUM\n+#include \"capsicum.h\"\n+#endif\n \n #include <sys/un.h>\n \n@@ -144,8 +147,7 @@ GIOChannel *net_connect(const char *addr, int port, IPADDR *my_ip)\n 	return net_connect_ip(ip, port, my_ip);\n }\n \n-/* Connect to socket with ip address */\n-GIOChannel *net_connect_ip(IPADDR *ip, int port, IPADDR *my_ip)\n+int net_connect_ip_handle(IPADDR *ip, int port, IPADDR *my_ip)\n {\n 	union sockaddr_union so;\n 	int handle, ret, opt = 1;\n@@ -161,7 +163,7 @@ GIOChannel *net_connect_ip(IPADDR *ip, int port, IPADDR *my_ip)\n 	handle = socket(ip->family, SOCK_STREAM, 0);\n \n 	if (handle == -1)\n-		return NULL;\n+		return -1;\n \n 	/* set socket options */\n 	fcntl(handle, F_SETFL, O_NONBLOCK);\n@@ -176,7 +178,7 @@ GIOChannel *net_connect_ip(IPADDR *ip, int port, IPADDR *my_ip)\n \n 			close(handle);\n 			errno = old_errno;\n-			return NULL;\n+			return -1;\n 		}\n 	}\n \n@@ -190,9 +192,30 @@ GIOChannel *net_connect_ip(IPADDR *ip, int port, IPADDR *my_ip)\n 		int old_errno = errno;\n 		close(handle);\n 		errno = old_errno;\n-		return NULL;\n+		return -1;\n 	}\n \n+	return handle;\n+}\n+\n+\n+/* Connect to socket with ip address */\n+GIOChannel *net_connect_ip(IPADDR *ip, int port, IPADDR *my_ip)\n+{\n+	int handle = -1;\n+\n+#ifdef HAVE_CAPSICUM\n+	if (capsicum_enabled())\n+		handle = capsicum_net_connect_ip(ip, port, my_ip);\n+	else\n+		handle = net_connect_ip_handle(ip, port, my_ip);\n+#else\n+	handle = net_connect_ip_handle(ip, port, my_ip);\n+#endif\n+\n+	if (handle == -1)\n+		return (NULL);\n+\n 	return g_io_channel_new(handle);\n }\n \n','false','true','have_capsicum'),('irssi','1.0.4','cec68557aa436b4f16505d2095af02f83d154eda','Edward Tomasz Napierala','/connect works - although only with ip addresses.','network.h','36','0','2','0','MODIFY','@@ -36,6 +36,8 @@ GIOChannel *g_io_channel_new(int handle);\n /* returns 1 if IPADDRs are the same */\n int net_ip_compare(IPADDR *ip1, IPADDR *ip2);\n \n+int net_connect_ip_handle(IPADDR *ip, int port, IPADDR *my_ip);\n+\n /* Connect to socket */\n GIOChannel *net_connect(const char *addr, int port, IPADDR *my_ip) G_GNUC_DEPRECATED;\n /* Connect to socket with ip address and SSL*/\n','false','true','none'),('irssi','1.0.4','925240155e5c0c20569bcb5fafe4d3047055768a','Edward Tomasz Napierala','con