de \"signals.h\"\n@@ -34,6 +36,9 @@\n #define	OPCODE_CONNECT		1\n #define	OPCODE_GETHOSTBYNAME	2\n \n+static char *irclogs_path;\n+static size_t irclogs_path_len;\n+static int irclogs_fd;\n static int symbiontfds[2];\n \n gboolean capsicum_enabled(void)\n@@ -127,6 +132,23 @@ int capsicum_net_gethostbyname(const char *addr, IPADDR *ip4, IPADDR *ip6)\n 	return ret;\n }\n \n+int capsicum_open(const char *path, int flags, int mode)\n+{\n+	int fd;\n+\n+	/* +1 is for slash separating irclogs_path and the rest. */\n+	if (strlen(path) > irclogs_path_len + 1 && strncmp(path, irclogs_path, irclogs_path_len) == 0) {\n+		fd = openat(irclogs_fd, path + irclogs_path_len + 1, flags, mode);\n+	} else {\n+		fd = open(path, flags, mode);\n+	}\n+\n+	if (fd < 0 && (errno == ENOTCAPABLE || errno == ECAPMODE))\n+		g_warning(\"File system access restricted to %s due to capability mode\", irclogs_path);\n+\n+	return (fd);\n+}\n+\n nvlist_t *symbiont_connect(const nvlist_t *request)\n {\n 	nvlist_t *response;\n@@ -261,6 +283,16 @@ static void cmd_capsicum_enter(void)\n 		return;\n 	}\n \n+	irclogs_path = convert_home(settings_get_str(\"capsicum_irclogs_path\"));\n+	g_mkdir_with_parents(irclogs_path, log_dir_create_mode);\n+	irclogs_path_len = strlen(irclogs_path);\n+	irclogs_fd = open(irclogs_path, O_DIRECTORY | O_CLOEXEC);\n+	if (irclogs_fd < 0) {\n+		g_warning(\"Unable to open %s: %s\", irclogs_path, strerror(errno));\n+		signal_emit(\"capability mode failed\", 1, strerror(errno));\n+		return;\n+	}\n+\n 	error = start_symbiont();\n 	if (error != 0) {\n 		signal_emit(\"capability mode failed\", 1, strerror(errno));\n@@ -299,6 +331,7 @@ void sig_init_finished(void)\n void capsicum_init(void)\n {\n 	settings_add_bool(\"misc\", \"capsicum\", FALSE);\n+	settings_add_str(\"misc\", \"capsicum_irclogs_path\", \"~/irclogs\");\n \n 	signal_add(\"irssi init finished\", (SIGNAL_FUNC) sig_init_finished);\n \n','false','true','none'),('irssi','1.0.4','241dd66ac11d54b37c671cd56f1320fe5b83803d','Edward Tomasz Napierala','working /log and /rawlog.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','capsicum.h','6','0','1','0','MODIFY','@@ -4,6 +4,7 @@\n gboolean capsicum_enabled(void);\n int capsicum_net_connect_ip(IPADDR *ip, int port, IPADDR *my_ip);\n int capsicum_net_gethostbyname(const char *addr, IPADDR *ip4, IPADDR *ip6);\n+int capsicum_open(const char *path, int flags, int mode);\n \n void capsicum_init(void);\n void capsicum_deinit(void);\n','false','false','none'),('irssi','1.0.4','241dd66ac11d54b37c671cd56f1320fe5b83803d','Edward Tomasz Napierala','working /log and /rawlog.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','log.c','448','100','14','1','MODIFY','@@ -26,6 +26,9 @@\n #include \"servers.h\"\n #include \"log.h\"\n #include \"write-buffer.h\"\n+#ifdef HAVE_CAPSICUM\n+#include \"capsicum.h\"\n+#endif\n \n #include \"lib-config/iconfig.h\"\n #include \"settings.h\"\n@@ -73,6 +76,16 @@ static void log_write_timestamp(int handle, const char *format,\n 	if (text != NULL) write_buffer(handle, text, strlen(text));\n }\n \n+static int log_open_wrapper(const char *path, int flags, int mode)\n+{\n+#ifdef HAVE_CAPSICUM\n+	if (capsicum_enabled())\n+		return capsicum_open(path, flags, mode);\n+#endif\n+\n+	return open(path, flags, mode);\n+}\n+\n static char *log_filename(LOG_REC *log)\n {\n 	char *str, fname[1024];\n@@ -119,7 +132,7 @@ int log_start_logging(LOG_REC *log)\n 	}\n \n 	log->handle = log->real_fname == NULL ? -1 :\n-		open(log->real_fname, O_WRONLY | O_APPEND | O_CREAT,\n+		log_open_wrapper(log->real_fname, O_WRONLY | O_APPEND | O_CREAT,\n 		     log_file_create_mode);\n 	if (log->handle == -1) {\n 		signal_emit(\"log create failed\", 1, log);\n','false','false','have_capsicum'),('irssi','1.0.4','241dd66ac11d54b37c671cd56f1320fe5b83803d','Edward Tomasz Napierala','working /log and /rawlog.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','log.h','45','0','2','0','MODIFY','@@ -38,6 +38,8 @@ extern GSList *logs;\n extern int log_file_create_mode;\n extern int log_dir_create_mode;\n \n+extern int log_dir_create_mode;\n+\n /* Create log record - you still need to call log_update() to actually add it\n    into log list */\n LOG_REC *log_create_rec(const char *fname, int level);\n','false','false','none'),('irssi','1.0.4','241dd66ac11d54b37c671cd56f1320fe5b83803d','Edward Tomasz Napierala','working /log and /rawlog.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','network.c','390','104','0','1','MODIFY','@@ -198,7 +198,6 @@ int net_connect_ip_handle(const IPADDR *ip, int port, const IPADDR *my_ip)\n 	return handle;\n }\n \n-\n /* Connect to socket with ip address */\n GIOChannel *net_connect_ip(IPADDR *ip, int port, IPADDR *my_ip)\n {\n','false','false','none'),('irssi','1.0.4','241dd66ac11d54b37c671cd56f1320fe5b83803d','Edward Tomasz Napierala','working /log and /rawlog.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','rawlog.c','180','41','15','2','MODIFY','@@ -27,12 +27,25 @@\n #include \"misc.h\"\n #include \"write-buffer.h\"\n #include \"settings.h\"\n+#ifdef HAVE_CAPSICUM\n+#include \"capsicum.h\"\n+#endif\n \n #include \"servers.h\"\n \n static int rawlog_lines;\n static int signal_rawlog;\n \n+static int rawlog_open_wrapper(const char *path, int flags, int mode)\n+{\n+#ifdef HAVE_CAPSICUM\n+	if (capsicum_enabled())\n+		return capsicum_open(path, flags, mode);\n+#endif\n+\n+	return open(path, flags, mode);\n+}\n+\n RAWLOG_REC *rawlog_create(void)\n {\n 	RAWLOG_REC *rec;\n@@ -126,7 +139,7 @@ void rawlog_open(RAWLOG_REC *rawlog, const char *fname)\n 		return;\n \n 	path = convert_home(fname);\n-	rawlog->handle = open(path, O_WRONLY | O_APPEND | O_CREAT,\n+	rawlog->handle = rawlog_open_wrapper(path, O_WRONLY | O_APPEND | O_CREAT,\n 			      log_file_create_mode);\n 	g_free(path);\n \n@@ -158,7 +171,7 @@ void rawlog_save(RAWLOG_REC *rawlog, const char *fname)\n         g_free(dir);\n \n 	path = convert_home(fname);\n-	f = open(path, O_WRONLY | O_APPEND | O_CREAT, log_file_create_mode);\n+	f = rawlog_open_wrapper(path, O_WRONLY | O_APPEND | O_CREAT, log_file_create_mode);\n 	g_free(path);\n \n 	if (f < 0) {\n','false','false','have_capsicum'),('irssi','1.0.4','e777ae986d64863a444132885b160ab2e9c7458e','Edward Tomasz Napierala','working autolog.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','capsicum.c','294','55','47','4','MODIFY','@@ -136,19 +136,62 @@ int capsicum_open(const char *path, int flags, int mode)\n {\n 	int fd;\n \n-	/* +1 is for slash separating irclogs_path and the rest. */\n-	if (strlen(path) > irclogs_path_len + 1 && strncmp(path, irclogs_path, irclogs_path_len) == 0) {\n-		fd = openat(irclogs_fd, path + irclogs_path_len + 1, flags, mode);\n+	/* +1 is for the slash separating irclogs_path and the rest. */\n+	if (strlen(path) > irclogs_path_len + 1 &&\n+	    strncmp(path, irclogs_path, irclogs_path_len) == 0) {\n+		fd = openat(irclogs_fd, path + irclogs_path_len + 1,\n+		    flags, mode);\n 	} else {\n 		fd = open(path, flags, mode);\n 	}\n \n 	if (fd < 0 && (errno == ENOTCAPABLE || errno == ECAPMODE))\n-		g_warning(\"File system access restricted to %s due to capability mode\", irclogs_path);\n+		g_warning(\"File system access restricted to %s \"\n+		    \"due to capability mode\", irclogs_path);\n \n 	return (fd);\n }\n \n+void capsicum_mkdir_with_parents(const char *path, int mode)\n+{\n+	char *component, *copy, *tofree;\n+	int error, fd, newfd;\n+\n+	/* +1 is for the slash separating irclogs_path and the rest. */\n+	if (strlen(path) <= irclogs_path_len + 1 ||\n+	    strncmp(path, irclogs_path, irclogs_path_len) != 0) {\n+		g_warning(\"Cannot create %s: file system access restricted \"\n+		    \"to %s due to capability mode\", path, irclogs_path);\n+		return;\n+	}\n+\n+	copy = tofree = g_strdup(path + irclogs_path_len + 1);\n+	fd = irclogs_fd;\n+	for (;;) {\n+		component = strsep(&copy, \"/\");\n+		if (component == NULL)\n+			break;\n+		error = mkdirat(fd, component, mode);\n+		if (error != 0 && errno != EEXIST) {\n+			g_warning(\"cannot create %s: %s\",\n+			    component, strerror(errno));\n+			break;\n+		}\n+		newfd = openat(fd, component, O_DIRECTORY);\n+		if (newfd < 0) {\n+			g_warning(\"cannot open %s: %s\",\n+			    component, strerror(errno));\n+			break;\n+		}\n+		if (fd != irclogs_fd)\n+			close(fd);\n+		fd = newfd;\n+	}\n+	g_free(tofree);\n+	if (fd != irclogs_fd)\n+		close(fd);\n+}\n+\n nvlist_t *symbiont_connect(const nvlist_t *request)\n {\n 	nvlist_t *response;\n','false','false','none'),('irssi','1.0.4','e777ae986d64863a444132885b160ab2e9c7458e','Edward Tomasz Napierala','working autolog.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','capsicum.h','7','0','1','0','MODIFY','@@ -5,6 +5,7 @@ gboolean capsicum_enabled(void);\n int capsicum_net_connect_ip(IPADDR *ip, int port, IPADDR *my_ip);\n int capsicum_net_gethostbyname(const char *addr, IPADDR *ip4, IPADDR *ip6);\n int capsicum_open(const char *path, int flags, int mode);\n+void capsicum_mkdir_with_parents(const char *path, int mode);\n \n void capsicum_init(void);\n void capsicum_deinit(void);\n','false','false','none'),('irssi','1.0.4','e777ae986d64863a444132885b160ab2e9c7458e','Edward Tomasz Napierala','working autolog.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','log.c','452','102','7','0','MODIFY','@@ -127,7 +127,14 @@ int log_start_logging(LOG_REC *log)\n 		/* path may contain variables (%time, $vars),\n 		   make sure the directory is created */\n 		dir = g_path_get_dirname(log->real_fname);\n+#ifdef HAVE_CAPSICUM\n+		if (capsicum_enabled())\n+			capsicum_mkdir_with_parents(dir, log_dir_create_mode);\n+		else\n+			g_mkdir_with_parents(dir, log_dir_create_mode);\n+#else\n 		g_mkdir_with_parents(dir, log_dir_create_mode);\n+#endif\n 		g_free(dir);\n 	}\n \n','false','false','have_capsicum'),('irssi','1.0.4','e777ae986d64863a444132885b160ab2e9c7458e','Edward Tomasz Napierala','working autolog.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','rawlog.c','184','43','8','1','MODIFY','@@ -167,7 +167,14 @@ void rawlog_save(RAWLOG_REC *rawlog, const char *fname)\n 	int f;\n \n         dir = g_path_get_dirname(fname);\n-        g_mkdir_with_parents(dir, log_dir_create_mode);\n+#ifdef HAVE_CAPSICUM\n+	if (capsicum_enabled())\n+		capsicum_mkdir_with_parents(dir, log_dir_create_mode);\n+	else\n+		g_mkdir_with_parents(dir, log_dir_create_mode);\n+#else\n+	g_mkdir_with_parents(dir, log_dir_create_mode);\n+#endif\n         g_free(dir);\n \n 	path = convert_home(fname);\n','false','false','have_capsicum'),('irssi','1.0.4','e777ae986d64863a444132885b160ab2e9c7458e','Edward Tomasz Napierala','working autolog.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','fe-log.c','588','150','10','0','MODIFY','@@ -30,6 +30,9 @@\n #include \"special-vars.h\"\n #include \"settings.h\"\n #include \"lib-config/iconfig.h\"\n+#ifdef HAVE_CAPSICUM\n+#include \"capsicum.h\"\n+#endif\n \n #include \"fe-windows.h\"\n #include \"window-items.h\"\n@@ -451,7 +454,14 @@ static void autolog_open(SERVER_REC *server, const char *server_tag,\n 		log_item_add(log, LOG_ITEM_TARGET, target, server_tag);\n \n 		dir = g_path_get_dirname(log->real_fname);\n+#ifdef HAVE_CAPSICUM\n+		if (capsicum_enabled())\n+			capsicum_mkdir_with_parents(dir, log_dir_create_mode);\n+		else\n+			g_mkdir_with_parents(dir, log_dir_create_mode);\n+#else\n 		g_mkdir_with_parents(dir, log_dir_create_mode);\n+#endif\n 		g_free(dir);\n \n 		log->temp = TRUE;\n','false','false','have_capsicum'),('irssi','1.0.4','c00d4753aee732b5a815f12dda3c9fced41ef1f5','Edward Tomasz Napierala','restrict port range available in capability mode.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','capsicum.c','310','60','27','3','MODIFY','@@ -40,6 +40,8 @@ static char *irclogs_path;\n static size_t irclogs_path_len;\n static int irclogs_fd;\n static int symbiontfds[2];\n+static int port_min;\n+static int port_max;\n \n gboolean capsicum_enabled(void)\n {\n@@ -90,6 +92,13 @@ int capsicum_net_connect_ip(IPADDR *ip, int port, IPADDR *my_ip)\n 	}\n 	saved_errno = nvlist_get_number(nvl, \"errno\");\n 	nvlist_destroy(nvl);\n+\n+	if (sock == -1 && (port < port_min || port > port_max)) {\n+		g_warning(\"Access restricted to ports between %d and %d \"\n+		    \"due to capability mode\",\n+		    port_min, port_max);\n+	}\n+\n 	errno = saved_errno;\n \n 	return sock;\n@@ -205,9 +214,19 @@ nvlist_t *symbiont_connect(const nvlist_t *request)\n 	else\n 		my_ip = NULL;\n \n-	/* Connect. */\n-	sock = net_connect_ip_handle(ip, port, my_ip);\n-	saved_errno = errno;\n+	/*\n+	 * Check if the port is in allowed range.  This is to minimize\n+	 * the chance of the attacker rooting another system in case of\n+	 * compromise.\n+	 */\n+	if (port < port_min || port > port_max) {\n+		sock = -1;\n+		saved_errno = EPERM;\n+	} else {\n+		/* Connect. */\n+		sock = net_connect_ip_handle(ip, port, my_ip);\n+		saved_errno = errno;\n+	}\n \n 	/* Send back the socket fd. */\n 	response = nvlist_create(0);\n@@ -326,6 +345,9 @@ static void cmd_capsicum_enter(void)\n 		return;\n 	}\n \n+	port_min = settings_get_int(\"capsicum_port_min\");\n+	port_max = settings_get_int(\"capsicum_port_max\");\n+\n 	irclogs_path = convert_home(settings_get_str(\"capsicum_irclogs_path\"));\n 	g_mkdir_with_parents(irclogs_path, log_dir_create_mode);\n 	irclogs_path_len = strlen(irclogs_path);\n@@ -375,6 +397,8 @@ void capsicum_init(void)\n {\n 	settings_add_bool(\"misc\", \"capsicum\", FALSE);\n 	settings_add_str(\"misc\", \"capsicum_irclogs_path\", \"~/irclogs\");\n+	settings_add_int(\"misc\", \"capsicum_port_min\", 6667);\n+	settings_add_int(\"misc\", \"capsicum_port_max\", 6697);\n \n 	signal_add(\"irssi init finished\", (SIGNAL_FUNC) sig_init_finished);\n \n','false','false','none'),('irssi','1.0.4','12e2c46d6a778f131c1ca6a7134af1cd5e722dbe','Edward Tomasz Napierala','add wrappers to reduce #ifdefs.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','capsicum.c','325','64','17','0','MODIFY','@@ -161,6 +161,14 @@ int capsicum_open(const char *path, int flags, int mode)\n 	return (fd);\n }\n \n+int capsicum_open_wrapper(const char *path, int flags, int mode)\n+{\n+	if (capsicum_enabled()) {\n+		return capsicum_open(path, flags, mode);\n+	}\n+	return open(path, flags, mode);\n+}\n+\n void capsicum_mkdir_with_parents(const char *path, int mode)\n {\n 	char *component, *copy, *tofree;\n@@ -201,6 +209,15 @@ void capsicum_mkdir_with_parents(const char *path, int mode)\n 		close(fd);\n }\n \n+void capsicum_mkdir_with_parents_wrapper(const char *path, int mode)\n+{\n+	if (capsicum_enabled()) {\n+		capsicum_mkdir_with_parents(path, mode);\n+		return;\n+	}\n+	g_mkdir_with_parents(path, mode);\n+}\n+\n nvlist_t *symbiont_connect(const nvlist_t *request)\n {\n 	nvlist_t *response;\n','false','false','none'),('irssi','1.0.4','12e2c46d6a778f131c1ca6a7134af1cd5e722dbe','Edward Tomasz Napierala','add wrappers to reduce #ifdefs.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','capsicum.h','9','0','11','1','MODIFY','@@ -7,7 +7,17 @@ int capsicum_net_gethostbyname(const char *addr, IPADDR *ip4, IPADDR *ip6);\n int capsicum_open(const char *path, int flags, int mode);\n void capsicum_mkdir_with_parents(const char *path, int mode);\n \n+#ifdef HAVE_CAPSICUM\n+int capsicum_open_wrapper(const char *path, int flags, int mode);\n+void capsicum_mkdir_with_parents_wrapper(const char *path, int mode);\n+#else\n+#define	capsicum_open_wrapper(P, F, M)			\\\n+	open(P, F, M)\n+#define	capsicum_mkdir_with_parents_wrapper(P, M)	\\\n+	g_mkdir_with_parents(P, M)\n+#endif\n+\n void capsicum_init(void);\n void capsicum_deinit(void);\n \n-#endif\n+#endif /* !__CAPSICUM_H */\n','false','false','have_capsicum'),('irssi','1.0.4','12e2c46d6a778f131c1ca6a7134af1cd5e722dbe','Edward Tomasz Napierala','add wrappers to reduce #ifdefs.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','log.c','442','97','2','21','MODIFY','@@ -26,9 +26,7 @@\n #include \"servers.h\"\n #include \"log.h\"\n #include \"write-buffer.h\"\n-#ifdef HAVE_CAPSICUM\n #include \"capsicum.h\"\n-#endif\n \n #include \"lib-config/iconfig.h\"\n #include \"settings.h\"\n@@ -76,16 +74,6 @@ static void log_write_timestamp(int handle, const char *format,\n 	if (text != NULL) write_buffer(handle, text, strlen(text));\n }\n \n-static int log_open_wrapper(const char *path, int flags, int mode)\n-{\n-#ifdef HAVE_CAPSICUM\n-	if (capsicum_enabled())\n-		return capsicum_open(path, flags, mode);\n-#endif\n-\n-	return open(path, flags, mode);\n-}\n-\n static char *log_filename(LOG_REC *log)\n {\n 	char *str, fname[1024];\n@@ -127,19 +115,12 @@ int log_start_logging(LOG_REC *log)\n 		/* path may contain variables (%time, $vars),\n 		   make sure the directory is created */\n 		dir = g_path_get_dirname(log->real_fname);\n-#ifdef HAVE_CAPSICUM\n-		if (capsicum_enabled())\n-			capsicum_mkdir_with_parents(dir, log_dir_create_mode);\n-		else\n-			g_mkdir_with_parents(dir, log_dir_create_mode);\n-#else\n-		g_mkdir_with_parents(dir, log_dir_create_mode);\n-#endif\n+		capsicum_mkdir_with_parents_wrapper(dir, log_dir_create_mode);\n 		g_free(dir);\n 	}\n \n 	log->handle = log->real_fname == NULL ? -1 :\n-		log_open_wrapper(log->real_fname, O_WRONLY | O_APPEND | O_CREAT,\n+		capsicum_open_wrapper(log->real_fname, O_WRONLY | O_APPEND | O_CREAT,\n 		     log_file_create_mode);\n 	if (log->handle == -1) {\n 		signal_emit(\"log create failed\", 1, log);\n','false','false','have_capsicum'),('irssi','1.0.4','12e2c46d6a778f131c1ca6a7134af1cd5e722dbe','Edward Tomasz Napierala','add wrappers to reduce #ifdefs.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','rawlog.c','176','38','6','23','MODIFY','@@ -27,25 +27,13 @@\n #include \"misc.h\"\n #include \"write-buffer.h\"\n #include \"settings.h\"\n-#ifdef HAVE_CAPSICUM\n #include \"capsicum.h\"\n-#endif\n \n #include \"servers.h\"\n \n static int rawlog_lines;\n static int signal_rawlog;\n \n-static int rawlog_open_wrapper(const char *path, int flags, int mode)\n-{\n-#ifdef HAVE_CAPSICUM\n-	if (capsicum_enabled())\n-		return capsicum_open(path, flags, mode);\n-#endif\n-\n-	return open(path, flags, mode);\n-}\n-\n RAWLOG_REC *rawlog_create(void)\n {\n 	RAWLOG_REC *rec;\n@@ -139,8 +127,9 @@ void rawlog_open(RAWLOG_REC *rawlog, const char *fname)\n 		return;\n \n 	path = convert_home(fname);\n-	rawlog->handle = rawlog_open_wrapper(path, O_WRONLY | O_APPEND | O_CREAT,\n-			      log_file_create_mode);\n+	rawlog->handle = capsicum_open_wrapper(path,\n+					       O_WRONLY | O_APPEND | O_CREAT,\n+					       log_file_create_mode);\n 	g_free(path);\n \n 	if (rawlog->handle == -1) {\n@@ -167,18 +156,12 @@ void rawlog_save(RAWLOG_REC *rawlog, const char *fname)\n 	int f;\n \n         dir = g_path_get_dirname(fname);\n-#ifdef HAVE_CAPSICUM\n-	if (capsicum_enabled())\n-		capsicum_mkdir_with_parents(dir, log_dir_create_mode);\n-	else\n-		g_mkdir_with_parents(dir, log_dir_create_mode);\n-#else\n-	g_mkdir_with_parents(dir, log_dir_create_mode);\n-#endif\n+	capsicum_mkdir_with_parents_wrapper(dir, log_dir_create_mode);\n         g_free(dir);\n \n 	path = convert_home(fname);\n-	f = rawlog_open_wrapper(path, O_WRONLY | O_APPEND | O_CREAT, log_file_create_mode);\n+	f = capsicum_open_wrapper(path, O_WRONLY | O_APPEND | O_CREAT,\n+				  log_file_create_mode);\n 	g_free(path);\n \n 	if (f < 0) {\n','false','false','have_capsicum'),('irssi','1.0.4','12e2c46d6a778f131c1ca6a7134af1cd5e722dbe','Edward Tomasz Napierala','add wrappers to reduce #ifdefs.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','fe-log.c','584','148','1','10','MODIFY','@@ -30,9 +30,7 @@\n #include \"special-vars.h\"\n #include \"settings.h\"\n #include \"lib-config/iconfig.h\"\n-#ifdef HAVE_CAPSICUM\n #include \"capsicum.h\"\n-#endif\n \n #include \"fe-windows.h\"\n #include \"window-items.h\"\n@@ -454,14 +452,7 @@ static void autolog_open(SERVER_REC *server, const char *server_tag,\n 		log_item_add(log, LOG_ITEM_TARGET, target, server_tag);\n \n 		dir = g_path_get_dirname(log->real_fname);\n-#ifdef HAVE_CAPSICUM\n-		if (capsicum_enabled())\n-			capsicum_mkdir_with_parents(dir, log_dir_create_mode);\n-		else\n-			g_mkdir_with_parents(dir, log_dir_create_mode);\n-#else\n-		g_mkdir_with_parents(dir, log_dir_create_mode);\n-#endif\n+		capsicum_mkdir_with_parents_wrapper(dir, log_dir_create_mode);\n 		g_free(dir);\n \n 		log->temp = TRUE;\n','false','false','have_capsicum'),('irssi','1.0.4','8c04c2be14fc053bd7b5ff03b8e4ba8cb5023700','Edward Tomasz Napierala','fix build without capsicum.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','core.c','224','26','4','0','MODIFY','@@ -236,7 +236,9 @@ void core_init(void)\n 	commands_init();\n 	nickmatch_cache_init();\n         session_init();\n+#ifdef HAVE_CAPSICUM\n 	capsicum_init();\n+#endif\n \n 	chat_protocols_init();\n 	chatnets_init();\n@@ -294,7 +296,9 @@ void core_deinit(void)\n 	chatnets_deinit();\n 	chat_protocols_deinit();\n \n+#ifdef HAVE_CAPSICUM\n 	capsicum_deinit();\n+#endif\n         session_deinit();\n         nickmatch_cache_deinit();\n 	commands_deinit();\n','true','false','have_capsicum'),('irssi','1.0.4','8c04c2be14fc053bd7b5ff03b8e4ba8cb5023700','Edward Tomasz Napierala','fix build without capsicum.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','fe-common-core.c','377','54','4','0','MODIFY','@@ -180,7 +180,9 @@ void fe_common_core_init(void)\n 	fe_server_init();\n 	fe_settings_init();\n 	fe_tls_init();\n+#ifdef HAVE_CAPSICUM\n 	fe_capsicum_init();\n+#endif\n 	windows_init();\n 	window_activity_init();\n 	window_commands_init();\n@@ -223,7 +225,9 @@ void fe_common_core_deinit(void)\n 	fe_server_deinit();\n 	fe_settings_deinit();\n 	fe_tls_deinit();\n+#ifdef HAVE_CAPSICUM\n 	fe_capsicum_deinit();\n+#endif\n 	windows_deinit();\n 	window_activity_deinit();\n 	window_commands_deinit();\n','true','false','have_capsicum'),('irssi','1.0.4','7f2697d30744d1f33cf67430aa48013b8f4ae9cd','Edward Tomasz Napierala','cosmetics.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','log.h','44','0','0','2','MODIFY','@@ -38,8 +38,6 @@ extern GSList *logs;\n extern int log_file_create_mode;\n extern int log_dir_create_mode;\n \n-extern int log_dir_create_mode;\n-\n /* Create log record - you still need to call log_update() to actually add it\n    into log list */\n LOG_REC *log_create_rec(const char *fname, int level);\n','false','false','none'),('irssi','1.0.4','7f2697d30744d1f33cf67430aa48013b8f4ae9cd','Edward Tomasz Napierala','cosmetics.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','rawlog.c','176','38','1','1','MODIFY','@@ -156,7 +156,7 @@ void rawlog_save(RAWLOG_REC *rawlog, const char *fname)\n 	int f;\n \n         dir = g_path_get_dirname(fname);\n-	capsicum_mkdir_with_parents_wrapper(dir, log_dir_create_mode);\n+        capsicum_mkdir_with_parents_wrapper(dir, log_dir_create_mode);\n         g_free(dir);\n \n 	path = convert_home(fname);\n','false','false','none'),('irssi','1.0.4','a29eeaa9f2e85630b08fc46b3a2b8749b9719efd','Edward Tomasz Napierala','fix trailing slash handling for capsicum_irclogs_path.\n\nthis is mostly an anti-footshooting measure, but still.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','capsicum.c','331','68','10','1','MODIFY','@@ -147,6 +147,7 @@ int capsicum_open(const char *path, int flags, int mode)\n \n 	/* +1 is for the slash separating irclogs_path and the rest. */\n 	if (strlen(path) > irclogs_path_len + 1 &&\n+	    path[irclogs_path_len] == \'/\' &&\n 	    strncmp(path, irclogs_path, irclogs_path_len) == 0) {\n 		fd = openat(irclogs_fd, path + irclogs_path_len + 1,\n 		    flags, mode);\n@@ -176,6 +177,7 @@ void capsicum_mkdir_with_parents(const char *path, int mode)\n \n 	/* +1 is for the slash separating irclogs_path and the rest. */\n 	if (strlen(path) <= irclogs_path_len + 1 ||\n+	    path[irclogs_path_len] != \'/\' ||\n 	    strncmp(path, irclogs_path, irclogs_path_len) != 0) {\n 		g_warning(\"Cannot create %s: file system access restricted \"\n 		    \"to %s due to capability mode\", path, irclogs_path);\n@@ -366,8 +368,15 @@ static void cmd_capsicum_enter(void)\n 	port_max = settings_get_int(\"capsicum_port_max\");\n \n 	irclogs_path = convert_home(settings_get_str(\"capsicum_irclogs_path\"));\n-	g_mkdir_with_parents(irclogs_path, log_dir_create_mode);\n 	irclogs_path_len = strlen(irclogs_path);\n+\n+	/* Strip trailing slashes, if any. */\n+	while (irclogs_path_len > 0 && irclogs_path[irclogs_path_len - 1] == \'/\') {\n+		irclogs_path[irclogs_path_len - 1] = \'\\0\';\n+		irclogs_path_len--;\n+	}\n+\n+	g_mkdir_with_parents(irclogs_path, log_dir_create_mode);\n 	irclogs_fd = open(irclogs_path, O_DIRECTORY | O_CLOEXEC);\n 	if (irclogs_fd < 0) {\n 		g_warning(\"Unable to open %s: %s\", irclogs_path, strerror(errno));\n','true','false','none'),('irssi','1.0.4','8bb392a3f47d74cccb9e2d7e3633e3e534306968','Edward Tomasz Napierala','hook up capsicum.h and fe-capsicum.h to autotools.\n\nthis hopefully fixes travis build.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','Makefile.am','111','0','1','0','MODIFY','@@ -74,6 +74,7 @@ structure_headers = \\\n pkginc_coredir=$(pkgincludedir)/src/core\n pkginc_core_HEADERS = \\\n 	args.h \\\n+	capsicum.h \\\n 	channels.h \\\n 	channels-setup.h \\\n 	commands.h \\\n','false','false','none'),('irssi','1.0.4','8bb392a3f47d74cccb9e2d7e3633e3e534306968','Edward Tomasz Napierala','hook up capsicum.h and fe-capsicum.h to autotools.\n\nthis hopefully fixes travis build.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','Makefile.am','66','0','1','0','MODIFY','@@ -48,6 +48,7 @@ pkginc_fe_common_core_HEADERS = \\\n 	command-history.h \\\n 	chat-completion.h \\\n 	completion.h \\\n+	fe-capsicum.h \\\n 	fe-channels.h \\\n 	fe-common-core.h \\\n 	fe-core-commands.h \\\n','false','false','none'),('irssi','1.0.4','5551b4fe9504f5d5b4e81a680741cab72bec82e9','Edward Tomasz