edward tomasz napierala <trasz@freebsd.org>','fe-capsicum.c','30','5','3','4','MODIFY','@@ -23,12 +23,11 @@\n */\n \n #include \"module.h\"\n+#include \"fe-capsicum.h\"\n+#include \"levels.h\"\n #include \"module-formats.h\"\n-#include \"signals.h\"\n #include \"printtext.h\"\n-\n-#include \"levels.h\"\n-#include \"servers.h\"\n+#include \"signals.h\"\n \n static void capability_mode_enabled(void)\n {\n','false','false','none'),('irssi','1.0.4','96f4fe10c6081cf441122039c39eb6422eef13e8','Edward Tomasz Napierala','change the way we load default ca certificates so it works with capsicum.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','capsicum.c','339','69','8','0','MODIFY','@@ -28,6 +28,7 @@\n #include \"log.h\"\n #include \"misc.h\"\n #include \"network.h\"\n+#include \"network-openssl.h\"\n #include \"settings.h\"\n #include \"signals.h\"\n \n@@ -361,6 +362,7 @@ static void cmd_capsicum(const char *data, SERVER_REC *server, void *item)\n static void cmd_capsicum_enter(void)\n {\n 	u_int mode;\n+	gboolean inited;\n 	int error;\n \n 	error = cap_getmode(&mode);\n@@ -369,6 +371,12 @@ static void cmd_capsicum_enter(void)\n 		return;\n 	}\n \n+	inited = irssi_ssl_init();\n+	if (!inited) {\n+		signal_emit(\"capability mode failed\", 1, strerror(errno));\n+		return;\n+	}\n+\n 	port_min = settings_get_int(\"capsicum_port_min\");\n 	port_max = settings_get_int(\"capsicum_port_max\");\n \n','false','false','none'),('irssi','1.0.4','96f4fe10c6081cf441122039c39eb6422eef13e8','Edward Tomasz Napierala','change the way we load default ca certificates so it works with capsicum.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','network-openssl.c','672','144','20','3','MODIFY','@@ -20,6 +20,7 @@\n \n #include \"module.h\"\n #include \"network.h\"\n+#include \"network-openssl.h\"\n #include \"net-sendbuffer.h\"\n #include \"misc.h\"\n #include \"servers.h\"\n@@ -58,6 +59,7 @@ typedef struct\n } GIOSSLChannel;\n \n static int ssl_inited = FALSE;\n+static X509_STORE *store = NULL;\n \n static void irssi_ssl_free(GIOChannel *handle)\n {\n@@ -362,8 +364,10 @@ static GIOFuncs irssi_ssl_channel_funcs = {\n     irssi_ssl_get_flags\n };\n \n-static gboolean irssi_ssl_init(void)\n+gboolean irssi_ssl_init(void)\n {\n+	int success;\n+\n #if (OPENSSL_VERSION_NUMBER >= 0x10100000L) && !defined(LIBRESSL_VERSION_NUMBER)\n 	if (!OPENSSL_init_ssl(OPENSSL_INIT_SSL_DEFAULT, NULL)) {\n 		g_error(\"Could not initialize OpenSSL\");\n@@ -374,6 +378,20 @@ static gboolean irssi_ssl_init(void)\n 	SSL_load_error_strings();\n 	OpenSSL_add_all_algorithms();\n #endif\n+	store = X509_STORE_new();\n+	if (store == NULL) {\n+		g_error(\"Could not initialize OpenSSL: X509_STORE_new() failed\");\n+		return FALSE;\n+	}\n+\n+	success = X509_STORE_set_default_paths(store);\n+	if (success == 0) {\n+		g_error(\"Could not load default certificates\");\n+		X509_STORE_free(store);\n+		store = NULL;\n+		return FALSE;\n+	}\n+\n 	ssl_inited = TRUE;\n \n 	return TRUE;\n@@ -492,8 +510,7 @@ static GIOChannel *irssi_ssl_get_iochannel(GIOChannel *handle, int port, SERVER_\n 		g_free(scapath);\n 		verify = TRUE;\n 	} else {\n-		if (!SSL_CTX_set_default_verify_paths(ctx))\n-			g_warning(\"Could not load default certificates\");\n+		SSL_CTX_set_cert_store(ctx, store);\n 	}\n \n 	if(!(ssl = SSL_new(ctx)))\n','false','true','none'),('irssi','1.0.4','96f4fe10c6081cf441122039c39eb6422eef13e8','Edward Tomasz Napierala','change the way we load default ca certificates so it works with capsicum.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','network-openssl.h','1','0','6','0','ADD','@@ -0,0 +1,6 @@\n+#ifndef __NETWORK_OPENSSL_H\n+#define __NETWORK_OPENSSL_H\n+\n+gboolean irssi_ssl_init(void);\n+\n+#endif /* !__NETWORK_OPENSSL_H */\n','false','false','none'),('irssi','1.0.4','5db6caee0d17eb125d8c7f3090c325ffbee35920','Edward Tomasz Napierala','attempt to fix build by adding the forgotten header.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','Makefile.am','112','0','1','0','MODIFY','@@ -95,6 +95,7 @@ pkginc_core_HEADERS = \\\n 	net-nonblock.h \\\n 	net-sendbuffer.h \\\n 	network.h \\\n+	network-openssl.h \\\n 	nick-rec.h \\\n 	nicklist.h \\\n 	nickmatch-cache.h \\\n','true','false','none'),('irssi','1.0.4','788e47032f9f6b7eea188a1a6e7fc1a19531c1b0','Paul Townsend','update /connect and /server syntax tags (-ssl -> -tls).','chat-commands.c','384','117','7','7','MODIFY','@@ -149,9 +149,9 @@ static SERVER_CONNECT_REC *get_server_connect(const char *data, int *plus_addr,\n         return conn;\n }\n \n-/* SYNTAX: CONNECT [-4 | -6] [-ssl] [-ssl_cert <cert>] [-ssl_pkey <pkey>] [-ssl_pass <password>]\n-                   [-ssl_verify] [-ssl_cafile <cafile>] [-ssl_capath <capath>]\n-                   [-ssl_ciphers <list>]\n+/* SYNTAX: CONNECT [-4 | -6] [-tls] [-tls_cert <cert>] [-tls_pkey <pkey>] [-tls_pass <password>]\n+                   [-tls_verify] [-tls_cafile <cafile>] [-tls_capath <capath>]\n+                   [-tls_ciphers <list>] [-tls_pinned_cert <fingerprint>] [-tls_pinned_pubkey <fingerprint>]\n                    [-!] [-noautosendcmd]\n 		   [-noproxy] [-network <network>] [-host <hostname>]\n 		   [-rawlog <file>]\n@@ -250,10 +250,10 @@ static void cmd_server(const char *data, SERVER_REC *server, WI_ITEM_REC *item)\n 	command_runsub(\"server\", data, server, item);\n }\n \n-/* SYNTAX: SERVER CONNECT [-4 | -6] [-ssl] [-ssl_cert <cert>] [-ssl_pkey <pkey>] \n-		  [-ssl_pass <password>] [-ssl_verify] [-ssl_cafile <cafile>] \n-		  [-ssl_capath <capath>]\n-		  [-ssl_ciphers <list>]\n+/* SYNTAX: SERVER CONNECT [-4 | -6] [-tls] [-tls_cert <cert>] [-tls_pkey <pkey>] \n+		  [-tls_pass <password>] [-tls_verify] [-tls_cafile <cafile>] \n+		  [-tls_capath <capath>]\n+		  [-tls_ciphers <list>] [-tls_pinned_cert <fingerprint>] [-tls_pinned_pubkey <fingerprint>]\n 		  [-!] [-noautosendcmd]\n 		  [-noproxy] [-network <network>] [-host <hostname>]\n 		  [-rawlog <file>]\n','false','false','none'),('irssi','1.0.4','947682e5dde85aa5e0277c01d7fb4afc1032ea04','ailin-nemui','revert \"merge pull request #452 from lemonboy/terminfo-cup\"\n\nfixes #733. the fix outlined in #452 had adverse effects for the\nfollowing reason. the code removed the restoration path that would go on\nthe code path from kill sigtstp. the problem is this: when irssi is not\nrunning in a controlling parent (like a shell), the tstp will in fact be\nignored. in that case, there is no process sending a cont either and\nthus the screen state never gets restored. luckily, the patch in #457 is\nsufficient to prevent the problem in #450 (which lead to the development\nof #452). to that end, we do end up with potentially calling\nterminfo_cont twice but that is better than not calling it at all.\n\nthis reverts commit b1ffd5f6472584aa3966746da9728c5afefcc4ce, reversing\nchanges made to 9cb0419435d1ad331c1f55361a003d9682fae9a8.','term-terminfo.c','532','168','7','0','MODIFY','@@ -629,6 +629,13 @@ void term_stop(void)\n {\n 	terminfo_stop(current_term);\n 	kill(getpid(), SIGTSTP);\n+	/* this call needs to stay here in case the TSTP was ignored,\n+	   because then we never see a CONT to call the restoration\n+	   code. On the other hand we also cannot remove the CONT\n+	   handler because then nothing would restore the screen when\n+	   Irssi is killed with TSTP/STOP from external. */\n+	terminfo_cont(current_term);\n+	irssi_redraw();\n }\n \n static int input_utf8(const unsigned char *buffer, int size, unichar *result)\n','false','false','none'),('irssi','1.0.4','0c49a84ffb8d74506a13653183bfa1ef8ffd2554','Edward Tomasz Napierala','add back some ifdefs.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','capsicum.h','9','0','1','9','MODIFY','@@ -5,17 +5,9 @@ gboolean capsicum_enabled(void);\n int capsicum_net_connect_ip(IPADDR *ip, int port, IPADDR *my_ip);\n int capsicum_net_gethostbyname(const char *addr, IPADDR *ip4, IPADDR *ip6);\n int capsicum_open(const char *path, int flags, int mode);\n-void capsicum_mkdir_with_parents(const char *path, int mode);\n-\n-#ifdef HAVE_CAPSICUM\n int capsicum_open_wrapper(const char *path, int flags, int mode);\n+void capsicum_mkdir_with_parents(const char *path, int mode);\n void capsicum_mkdir_with_parents_wrapper(const char *path, int mode);\n-#else\n-#define	capsicum_open_wrapper(P, F, M)			\\\n-	open(P, F, M)\n-#define	capsicum_mkdir_with_parents_wrapper(P, M)	\\\n-	g_mkdir_with_parents(P, M)\n-#endif\n \n void capsicum_init(void);\n void capsicum_deinit(void);\n','false','false','have_capsicum'),('irssi','1.0.4','0c49a84ffb8d74506a13653183bfa1ef8ffd2554','Edward Tomasz Napierala','add back some ifdefs.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','core.c','224','26','2','0','MODIFY','@@ -29,7 +29,9 @@\n #include \"signals.h\"\n #include \"settings.h\"\n #include \"session.h\"\n+#ifdef HAVE_CAPSICUM\n #include \"capsicum.h\"\n+#endif\n \n #include \"chat-protocols.h\"\n #include \"servers.h\"\n','false','false','have_capsicum'),('irssi','1.0.4','0c49a84ffb8d74506a13653183bfa1ef8ffd2554','Edward Tomasz Napierala','add back some ifdefs.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','log.c','446','100','12','0','MODIFY','@@ -26,7 +26,9 @@\n #include \"servers.h\"\n #include \"log.h\"\n #include \"write-buffer.h\"\n+#ifdef HAVE_CAPSICUM\n #include \"capsicum.h\"\n+#endif\n \n #include \"lib-config/iconfig.h\"\n #include \"settings.h\"\n@@ -115,13 +117,23 @@ int log_start_logging(LOG_REC *log)\n 		/* path may contain variables (%time, $vars),\n 		   make sure the directory is created */\n 		dir = g_path_get_dirname(log->real_fname);\n+#ifdef HAVE_CAPSICUM\n 		capsicum_mkdir_with_parents_wrapper(dir, log_dir_create_mode);\n+#else\n+		g_mkdir_with_parents(dir, log_dir_create_mode);\n+#endif\n 		g_free(dir);\n 	}\n \n+#ifdef HAVE_CAPSICUM\n 	log->handle = log->real_fname == NULL ? -1 :\n 		capsicum_open_wrapper(log->real_fname, O_WRONLY | O_APPEND | O_CREAT,\n 		     log_file_create_mode);\n+#else\n+	log->handle = log->real_fname == NULL ? -1 :\n+		open(log->real_fname, O_WRONLY | O_APPEND | O_CREAT,\n+		     log_file_create_mode);\n+#endif\n 	if (log->handle == -1) {\n 		signal_emit(\"log create failed\", 1, log);\n 		log->failed = TRUE;\n','false','false','have_capsicum'),('irssi','1.0.4','0c49a84ffb8d74506a13653183bfa1ef8ffd2554','Edward Tomasz Napierala','add back some ifdefs.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','rawlog.c','180','41','16','0','MODIFY','@@ -27,7 +27,9 @@\n #include \"misc.h\"\n #include \"write-buffer.h\"\n #include \"settings.h\"\n+#ifdef HAVE_CAPSICUM\n #include \"capsicum.h\"\n+#endif\n \n #include \"servers.h\"\n \n@@ -127,9 +129,15 @@ void rawlog_open(RAWLOG_REC *rawlog, const char *fname)\n 		return;\n \n 	path = convert_home(fname);\n+#ifdef HAVE_CAPSICUM\n 	rawlog->handle = capsicum_open_wrapper(path,\n 					       O_WRONLY | O_APPEND | O_CREAT,\n 					       log_file_create_mode);\n+#else\n+	rawlog->handle = open(path, O_WRONLY | O_APPEND | O_CREAT,\n+			      log_file_create_mode);\n+#endif\n+\n 	g_free(path);\n \n 	if (rawlog->handle == -1) {\n@@ -156,12 +164,20 @@ void rawlog_save(RAWLOG_REC *rawlog, const char *fname)\n 	int f;\n \n         dir = g_path_get_dirname(fname);\n+#ifdef HAVE_CAPSICUM\n         capsicum_mkdir_with_parents_wrapper(dir, log_dir_create_mode);\n+#else\n+        g_mkdir_with_parents(dir, log_dir_create_mode);\n+#endif\n         g_free(dir);\n \n 	path = convert_home(fname);\n+#ifdef HAVE_CAPSICUM\n 	f = capsicum_open_wrapper(path, O_WRONLY | O_APPEND | O_CREAT,\n 				  log_file_create_mode);\n+#else\n+	f = open(path, O_WRONLY | O_APPEND | O_CREAT, log_file_create_mode);\n+#endif\n 	g_free(path);\n \n 	if (f < 0) {\n','false','false','have_capsicum'),('irssi','1.0.4','0c49a84ffb8d74506a13653183bfa1ef8ffd2554','Edward Tomasz Napierala','add back some ifdefs.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','fe-common-core.c','377','54','2','0','MODIFY','@@ -32,7 +32,9 @@\n #include \"special-vars.h\"\n #include \"fe-core-commands.h\"\n #include \"fe-queries.h\"\n+#ifdef HAVE_CAPSICUM\n #include \"fe-capsicum.h\"\n+#endif\n #include \"hilight-text.h\"\n #include \"command-history.h\"\n #include \"completion.h\"\n','false','false','have_capsicum'),('irssi','1.0.4','0c49a84ffb8d74506a13653183bfa1ef8ffd2554','Edward Tomasz Napierala','add back some ifdefs.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','fe-log.c','585','149','6','0','MODIFY','@@ -30,7 +30,9 @@\n #include \"special-vars.h\"\n #include \"settings.h\"\n #include \"lib-config/iconfig.h\"\n+#ifdef HAVE_CAPSICUM\n #include \"capsicum.h\"\n+#endif\n \n #include \"fe-windows.h\"\n #include \"window-items.h\"\n@@ -452,7 +454,11 @@ static void autolog_open(SERVER_REC *server, const char *server_tag,\n 		log_item_add(log, LOG_ITEM_TARGET, target, server_tag);\n \n 		dir = g_path_get_dirname(log->real_fname);\n+#ifdef HAVE_CAPSICUM\n 		capsicum_mkdir_with_parents_wrapper(dir, log_dir_create_mode);\n+#else\n+		g_mkdir_with_parents(dir, log_dir_create_mode);\n+#endif\n 		g_free(dir);\n \n 		log->temp = TRUE;\n','false','false','have_capsicum'),('irssi','1.0.4','4bcbb41113bf23b1d121ad6fbd7b86473f7fa89d','Edward Tomasz Napierala','fix indentation; no functional changes.\n\n(take two.)\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','module-formats.h','240','0','25','25','MODIFY','@@ -12,21 +12,21 @@ enum {\n 	TXT_DAYCHANGE,\n 	TXT_TALKING_WITH,\n 	TXT_REFNUM_TOO_LOW,\n-        TXT_ERROR_SERVER_STICKY,\n-        TXT_SET_SERVER_STICKY,\n+	TXT_ERROR_SERVER_STICKY,\n+	TXT_SET_SERVER_STICKY,\n 	TXT_UNSET_SERVER_STICKY,\n-        TXT_WINDOW_NAME_NOT_UNIQUE,\n-        TXT_WINDOW_LEVEL,\n-        TXT_WINDOW_SET_IMMORTAL,\n-        TXT_WINDOW_UNSET_IMMORTAL,\n-        TXT_WINDOW_IMMORTAL_ERROR,\n+	TXT_WINDOW_NAME_NOT_UNIQUE,\n+	TXT_WINDOW_LEVEL,\n+	TXT_WINDOW_SET_IMMORTAL,\n+	TXT_WINDOW_UNSET_IMMORTAL,\n+	TXT_WINDOW_IMMORTAL_ERROR,\n 	TXT_WINDOWLIST_HEADER,\n 	TXT_WINDOWLIST_LINE,\n 	TXT_WINDOWLIST_FOOTER,\n 	TXT_WINDOWS_LAYOUT_SAVED,\n 	TXT_WINDOWS_LAYOUT_RESET,\n-        TXT_WINDOW_INFO_HEADER,\n-        TXT_WINDOW_INFO_FOOTER,\n+	TXT_WINDOW_INFO_HEADER,\n+	TXT_WINDOW_INFO_FOOTER,\n 	TXT_WINDOW_INFO_REFNUM,\n 	TXT_WINDOW_INFO_REFNUM_STICKY,\n 	TXT_WINDOW_INFO_NAME,\n@@ -34,22 +34,22 @@ enum {\n 	TXT_WINDOW_INFO_IMMORTAL,\n 	TXT_WINDOW_INFO_SIZE,\n 	TXT_WINDOW_INFO_LEVEL,\n-        TXT_WINDOW_INFO_SERVER,\n+	TXT_WINDOW_INFO_SERVER,\n 	TXT_WINDOW_INFO_SERVER_STICKY,\n-        TXT_WINDOW_INFO_THEME,\n+	TXT_WINDOW_INFO_THEME,\n 	TXT_WINDOW_INFO_BOUND_ITEMS_HEADER,\n 	TXT_WINDOW_INFO_BOUND_ITEM,\n 	TXT_WINDOW_INFO_BOUND_ITEMS_FOOTER,\n 	TXT_WINDOW_INFO_ITEMS_HEADER,\n 	TXT_WINDOW_INFO_ITEM,\n-        TXT_WINDOW_INFO_ITEMS_FOOTER,\n+	TXT_WINDOW_INFO_ITEMS_FOOTER,\n \n 	TXT_FILL_2,\n \n 	TXT_LOOKING_UP,\n 	TXT_CONNECTING,\n- 	TXT_RECONNECTING,\n-        TXT_CONNECTION_ESTABLISHED,\n+	TXT_RECONNECTING,\n+	TXT_CONNECTION_ESTABLISHED,\n 	TXT_CANT_CONNECT,\n 	TXT_CONNECTION_LOST,\n 	TXT_LAG_DISCONNECTED,\n@@ -100,7 +100,7 @@ enum {\n 	TXT_CHANSETUP_LINE,\n 	TXT_CHANSETUP_FOOTER,\n \n-        TXT_FILL_4,\n+	TXT_FILL_4,\n \n 	TXT_OWN_MSG,\n 	TXT_OWN_MSG_CHANNEL,\n@@ -162,7 +162,7 @@ enum {\n \n 	TXT_MODULE_HEADER,\n 	TXT_MODULE_LINE,\n-        TXT_MODULE_FOOTER,\n+	TXT_MODULE_FOOTER,\n 	TXT_MODULE_ALREADY_LOADED,\n 	TXT_MODULE_NOT_LOADED,\n 	TXT_MODULE_LOAD_ERROR,\n@@ -183,7 +183,7 @@ enum {\n 	TXT_NOT_JOINED,\n 	TXT_CHAN_NOT_FOUND,\n 	TXT_CHAN_NOT_SYNCED,\n-        TXT_ILLEGAL_PROTO,\n+	TXT_ILLEGAL_PROTO,\n 	TXT_NOT_GOOD_IDEA,\n 	TXT_INVALID_NUMBER,\n 	TXT_INVALID_TIME,\n@@ -232,8 +232,8 @@ enum {\n \n 	TXT_FILL_14,\n \n-        TXT_UNKNOWN_CHAT_PROTOCOL,\n-        TXT_UNKNOWN_CHATNET,\n+	TXT_UNKNOWN_CHAT_PROTOCOL,\n+	TXT_UNKNOWN_CHATNET,\n 	TXT_NOT_TOGGLE,\n 	TXT_PERL_ERROR,\n 	TXT_BIND_HEADER,\n@@ -245,15 +245,15 @@ enum {\n 	TXT_CONFIG_RELOADED,\n 	TXT_CONFIG_MODIFIED,\n 	TXT_GLIB_ERROR,\n-        TXT_OVERWRITE_CONFIG,\n-        TXT_SET_TITLE,\n-        TXT_SET_ITEM,\n-        TXT_SET_UNKNOWN,\n+	TXT_OVERWRITE_CONFIG,\n+	TXT_SET_TITLE,\n+	TXT_SET_ITEM,\n+	TXT_SET_UNKNOWN,\n 	TXT_SET_NOT_BOOLEAN,\n 	TXT_NO_COMPLETIONS,\n-        TXT_COMPLETION_REMOVED,\n+	TXT_COMPLETION_REMOVED,\n 	TXT_COMPLETION_HEADER,\n-        TXT_COMPLETION_LINE,\n+	TXT_COMPLETION_LINE,\n 	TXT_COMPLETION_FOOTER,\n \n 	TLS_FILL_15,\n','true','false','none'),('irssi','1.0.4','edee0ba587069a860eac43f9219f8ecfa49fe1fa','Edward Tomasz Napierala','don\'t error out on failure to load default certificate store.\n\nthis restores the previous behaviour.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','network-openssl.c','671','145','3','3','MODIFY','@@ -386,10 +386,10 @@ gboolean irssi_ssl_init(void)\n \n 	success = X509_STORE_set_default_paths(store);\n 	if (success == 0) {\n-		g_error(\"Could not load default certificates\");\n+		g_warning(\"Could not load default certificates\");\n 		X509_STORE_free(store);\n 		store = NULL;\n-		return FALSE;\n+		/* Don\'t return an error; the user might have their own cafile/capath. */\n 	}\n \n 	ssl_inited = TRUE;\n@@ -509,7 +509,7 @@ static GIOChannel *irssi_ssl_get_iochannel(GIOChannel *handle, int port, SERVER_\n 		g_free(scafile);\n 		g_free(scapath);\n 		verify = TRUE;\n-	} else {\n+	} else if (store != NULL) {\n 		SSL_CTX_set_cert_store(ctx, store);\n 	}\n \n','true','false','none'),('irssi','1.0.4','245a3fd4c1afb86b38eb17477a42c60c52cef793','Edward Tomasz Napierala','add docs/capsicum.txt.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','Makefile.am','15','0','1','0','MODIFY','@@ -2,6 +2,7 @@ man_MANS = \\\n 	irssi.1\n \n doc_DATA = \\\n+	capsicum.txt \\\n 	design.txt \\\n 	formats.txt \\\n 	manual.txt \\\n','false','false','none'),('irssi','1.0.4','245a3fd4c1afb86b38eb17477a42c60c52cef793','Edward Tomasz Napierala','add docs/capsicum.txt.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','capsicum.txt','24','0','30','0','ADD','@@ -0,0 +1,30 @@\n+Capsicum is a lightweight OS capability and sandbox framework provided\n+by FreeBSD.  When built with Capsicum support - which is the default under\n+FreeBSD - Irssi can enter a Capsicum capability mode (a sandbox), greatly\n+limiting possible consequences of a potential security hole in Irssi\n+or the libraries it depends on.\n+\n+To make Irssi enter capability mode on startup, add\n+\n+capsicum = \"yes\";\n+\n+to your ~/.irssi/config and restart the client.  Alternatively you can\n+enter it \"by hand\", using the \"/capsicum enter\" command.  From the security\n+point of view it\'s strongly preferable to use the former method, to avoid\n+establishing connections without the sandbox protection; the \"/capsicum\"\n+command is only intended for experimentation, and in cases where you need\n+to do something that\'s not possible in capability mode - run scripts,\n+for example - before continuing.\n+\n+There is no way to leave the capability mode, apart from exiting Irssi.\n+When running in capability mode, there are certain restrictions - Irssi\n+won\'t be able to access any files outside the directory pointed to by\n+capsicum_irclogs_path (which defaults to ~/irclogs/).  If you change\n+the path when already in capability mode it won\'t be effective until\n+you restart Irssi.  Capability mode also makes it impossible to use\n+the \"/save\" command.\n+\n+Currently there is no way to use custom SSL certificates.  As a workaround\n+you can establish connections and enter the capability mode afterwards\n+using the \"/capsicum enter\" command.\n+\n','false','false','none'),('irssi','1.0.4','7086eae650a616615bc81e688cdfa44d444463e0','LemonBoy','complete filenames ending with a slash','completion.c','653','185','12','1','MODIFY','@@ -321,6 +321,10 @@ GList *filename_complete(const char *path, const char *default_path)\n \n 	g_return_val_if_fail(path != NULL, NULL);\n \n+	if (path[0] == \'\\0\') {\n+	    return NULL;\n+	}\n+\n 	list = NULL;\n \n 	/* get directory part of the path - expand ~/ */\n@@ -350,7 +354,14 @@ GList *filename_complete(const char *path, const char *default_path)\n 		g_free_and_null(dir);\n 	}\n \n-	basename = g_path_get_basename(path);\n+	len = strlen(path);\n+	/* g_path_get_basename() returns the component before the last slash if\n+	 * the path ends with a directory separator, that\'s not what we want */\n+	if (len > 0 && path[len - 1] == G_DIR_SEPARATOR) {\n+	    basename = g_strdup(\"\");\n+	} else {\n+	    basename = g_path_get_basename(path);\n+	}\n 	len = strlen(basename);\n \n 	/* add all files in directory to completion list */\n','false','false','none'),('irssi','1.0.4','b529e1a9df134bbc5618752abcf19af0110b6868','Edward Tomasz Napierala','fix /back in capsicum capability mode.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','fe-core-commands.c','255','53','14','0','MODIFY','@@ -28,6 +28,9 @@\n #include \"settings.h\"\n #include \"irssi-version.h\"\n #include \"servers.h\"\n+#ifdef HAVE_CAPSICUM\n+#include \"capsicum.h\"\n+#endif\n \n #include \"fe-windows.h\"\n #include \"printtext.h\"\n@@ -120,6 +123,9 @@ static void cmd_cat(const char *data)\n 	GIOChannel *handle;\n 	GString *buf;\n 	gsize tpos;\n+#ifdef HAVE_CAPSICUM\n+	int fd;\n+#endif\n \n 	if (!cmd_get_params(data, &free_arg, 2, &fname, &fposstr))\n 		return;\n@@ -128,7 +134,15 @@ static void cmd_cat(const char *data)\n 	fpos = atoi(fposstr);\n         cmd_params_free(free_arg);\n \n+#ifdef HAVE_CAPSICUM\n+	fd = capsicum_open_wrapper(fname, O_RDONLY, 0);\n+	if (fd > 0)\n+		handle = g_io_channel_unix_new(fd);\n+	else\n+		handle = NULL;\n+#else\n 	handle = g_io_channel_new_file(fname, \"r\", NULL);\n+#endif\n 	g_free(fname);\n \n 	if (handle == NULL) {\n','true','false','have_capsicum'),('irssi','1.0.4','96d5a4669d696d57a511119963ae5c651346e167','LemonBoy','increment the x509_store refcount during the connection\n\nopenssl doesn\'t increment the reference count when the store is assigned\nto a ssl_ctx.','network-openssl.c','672','145','4','0','MODIFY','@@ -510,6 +510,10 @@ static GIOChannel *irssi_ssl_get_iochannel(GIOChannel *handle, int port, SERVER_\n 		g_free(scapath);\n 		verify = TRUE;\n 	} else if (store != NULL) {\n+		/* Make sure to increment the refcount every time the store is\n+		 * used, that\'s essential not to get it free\'d by OpenSSL when\n+		 * the SSL_CTX is destroyed. */\n+		X509_STORE_up_ref(store);\n 		SSL_CTX_set_cert_store(ctx, store);\n 	}\n \n','false','false','none'),('irssi','1.0.4','36d8b974fc42ed8eb1ff88811e09d0910ae61187','LemonBoy','restore compatibility with old openssl versions\n\nlet\'s implement x509_store_up_ref on our own.','network-openssl.c','679','147','13','0','MODIFY','@@ -45,6 +45,19 @@\n #define ASN1_STRING_data(x)       ASN1_STRING_get0_data(x)\n #endif\n \n+/* OpenSSL 1.1.0 also introduced some useful additions to the api */\n+#if (OPENSSL_VERSION_NUMBER < 0x10100000L) || defined (LIBRESSL_VERSION_NUMBER)\n+static int X509_STORE_up_ref(X509_STORE *vfy)\n+{\n+    int n;\n+\n+    n = CRYPTO_add(&vfy->references, 1, CRYPTO_LOCK_X509_STORE);\n+    g_assert(n > 1);\n+\n+    return (n > 1) ? 1 : 0;\n+}\n+#endif\n+\n /* ssl i/o channel object */\n typedef struct\n {\n','false','false','none'),('irssi','1.0.4','624ef4a696fc736779c6b7af94c7e376b7033590','Edward Tomasz Napierala','get rid of the zombies in capsicum capability mode.\n\nsigned-off-by: edward tomasz napierala <trasz@freebsd.org>','capsicum.c','340','69','7','0','MODIFY','@@ -403,6 +403,13 @@ static void cmd_capsicum_enter(void)\n 		return;\n 	}\n \n+	/*\n+	 * XXX: We should use pdwait(2) to wait for children.  Unfortunately\n+	 *      it\'s not implemented yet.  Thus the workaround, to get rid\n+	 *      of the zombies at least.\n+	 */\n+	signal(SIGCHLD, SIG_IGN);\n+\n 	error = cap_enter();\n 	if (error != 0) {\n 		signal_emit(\"capability mode failed\", 1, strerror(errno));\n','false','false','none'),('irssi','1.0.4','0468c5d912fc8a89bd552611ef6243bd1c889df7','ailin-nemui','add new function to set the position in bytes\n\nfixes #752','gui-entry.c','799','249','20','0','MODIFY','@@ -936,6 +936,26 @@ void gui_entry_set_pos(GUI_ENTRY_REC *entry, int pos)\n 	gui_entry_draw(entry);\n }\n \n+void gui_entry_set_text_and_pos_bytes(GUI_ENTRY_REC *entry, const char *str, int pos_bytes)\n+{\n+	int pos;\n+	const char *ptr;\n+\n+	g_return_if_fail(entry != NULL);\n+\n+	gui_entry_set_text(entry, str);\n+\n+	if (entry->utf8) {\n+		g_utf8_validate(str, pos_bytes, &ptr);\n+		pos = g_utf8_pointer_to_offset(str, ptr);\n+	} else if (term_type == TERM_TYPE_BIG5)\n+		pos = strlen_big5((const unsigned char *)str) - strlen_big5((const unsigned char *)(str + pos_bytes));\n+	else\n+		pos = pos_bytes;\n+\n+	gui_entry_set_pos(entry, pos);\n+}\n+\n void gui_entry_move_pos(GUI_ENTRY_REC *entry, int pos)\n {\n         g_return_if_fail(entry != NULL);\n','false','false','none'),('irssi','1.0.4','0468c5d912fc8a89bd552611ef6243bd1c889df7','ailin-nemui','add new function to set the position in bytes\n\nfixes #752','gui-entry.h','56','0','1','0','MODIFY','@@ -50,6 +50,7 @@ void gui_entry_set_utf8(GUI_ENTRY_REC *entry, int utf8);\n void gui_entry_set_text(GUI_ENTRY_REC *entry, const char *str);\n char *gui_entry_get_text(GUI_ENTRY_REC *entry);\n char *gui_entry_get_text_and_pos(GUI_ENTRY_REC *entry, int *pos);\n+void gui_entry_set_text_and_pos_bytes(GUI_ENTRY_REC *entry, const char *str, int pos_bytes);\n \n void gui_entry_insert_text(GUI_ENTRY_REC *entry, const char *str);\n void gui_entry_insert_char(GUI_ENTRY_REC *entry, unichar chr);\n','false','false','none'),('irssi','1.0.4','0468c5d912fc8a89bd552611ef6243bd1c889df7','ailin-nemui','add new function to set the position in bytes\n\nfixes #752','gui-readline.c','1029','208','2','4','MODIFY','@@ -878,8 +878,7 @@ static void key_completion(int erase, int backward)\n 	g_free(text);\n \n 	if (line != NULL) {\n-		gui_entry_set_text(active_entry, line);\n-		gui_entry_set_pos(active_entry, pos);\n+		gui_entry_set_text_and_pos_bytes(active_entry, line, pos);\n 		g_free(line);\n 	}\n }\n@@ -909,8 +908,7 @@ static void key_check_replaces(void)\n 	g_free(text);\n \n 	if (line != NULL) {\n-		gui_entry_set_text(active_entry, line);\n-		gui_entry_set_pos(active_entry, pos);\n+		gui_entry_set_text_and_pos_bytes(active_entry, line, pos);\n 		g_free(line);\n 	}\n }\n','false','false','none'),('irssi','1.0.4','8dfeca57ede1e726de07522a87203ce13676882d','ailin-nemui','hidden lines','common.h','42','0','1','1','MODIFY','@@ -6,7 +6,7 @@\n #define IRSSI_GLOBAL_CONFIG \"irssi.conf\" /* config file name in /etc/ */\n #define IRSSI_HOME_CONFIG \"config\" /* config file name in ~/.irssi/ */\n \n-#define IRSSI_ABI_VERSION 10\n+#define IRSSI_ABI_VERSION 11\n \n #define DEFAULT_SERVER_ADD_PORT 6667\n #define DEFAULT_SERVER_ADD_TLS_PORT 6697\n','false','false','none'),('irssi','1.0.4','8dfeca57ede1e726de07522a87203ce13676882d','ailin-nemui','hidden lines','levels.c','132','39','11','10','MODIFY','@@ -21,6 +21,7 @@\n #include \"module.h\"\n #include \"levels.h\"\n \n+/* the order of these levels must match the bits in levels.h */\n static const char *levels[] = {\n 	\"CRAP\",\n 	\"MSGS\",\n@@ -44,9 +45,6 @@ static const char *levels[] = {\n 	\"CLIENTCRAP\",\n 	\"CLIENTERRORS\",\n 	\"HILIGHTS\",\n-\n-	\"NOHILIGHT\",\n-	\"NO_ACT\",\n 	NULL\n };\n \n@@ -63,6 +61,9 @@ int level_get(const char *level)\n 	if (g_ascii_strcasecmp(level, \"NO_ACT\") == 0)\n 		return MSGLEVEL_NO_ACT;\n \n+	if (g_ascii_strcasecmp(level, \"HIDDEN\") == 0)\n+		return MSGLEVEL_HIDDEN;\n+\n 	len = strlen(level);\n 	if (len == 0) return 0;\n \n@@ -138,17 +139,13 @@ char *bits2level(int bits)\n \n \n 	str = g_string_new(NULL);\n-	if (bits & MSGLEVEL_NEVER) {\n+	if (bits & MSGLEVEL_NEVER)\n 		g_string_append(str, \"NEVER \");\n-		bits &= ~MSGLEVEL_NEVER;\n-	}\n \n-	if (bits & MSGLEVEL_NO_ACT) {\n+	if (bits & MSGLEVEL_NO_ACT)\n 		g_string_append(str, \"NO_ACT \");\n-		bits &= ~MSGLEVEL_NO_ACT;\n-	}\n \n-	if (bits == MSGLEVEL_ALL) {\n+	if ((bits & MSGLEVEL_ALL) == MSGLEVEL_ALL) {\n 		g_string_append(str, \"ALL \");\n 	} else {\n 		for (n = 0; levels[n] != NULL; n++) {\n@@ -156,6 +153,10 @@ char *bits2level(int bits)\n 				g_string_append_printf(str, \"%s \", levels[n]);\n 		}\n 	}\n+\n+	if (bits & MSGLEVEL_HIDDEN)\n+		g_string_append(str, \"HIDDEN \");\n+\n         if (str->len > 0)\n 		g_string_truncate(str, str->len-1);\n \n','false','false','none'),('irssi','1.0.4','8dfeca57ede1e726de07522a87203ce13676882d','ailin-nemui','hidden lines','levels.h','34','0','3','1','MODIFY','@@ -36,7 +36,9 @@ enum {\n 	MSGLEVEL_NOHILIGHT    = 0x1000000, /* Don\'t highlight this message */\n 	MSGLEVEL_NO_ACT       = 0x2000000, /* Don\'t trigger channel activity */\n 	MSGLEVEL_NEVER        = 0x4000000, /* never ignore / never log */\n-	MSGLEVEL_LASTLOG      = 0x8000000 /* never ignore / never log */\n+	MSGLEVEL_LASTLOG      = 0x8000000, /* used for /lastlog */\n+\n+	MSGLEVEL_HIDDEN       = 0x10000000 /* Hidden from view */\n };\n \n int level_get(const char *level);\n','false','false','none'),('irssi','1.0.4','8dfeca57ede1e726de07522a87203ce13676882d','ailin-nemui','hidden lines','gui-windows.c','221','51','4','0','MODIFY','@@ -23,6 +23,7 @@\n #include \"misc.h\"\n #include \"settings.h\"\n #include \"special-vars.h\"\n+#include \"levels.h\"\n \n #include \"term.h\"\n #include \"gui-entry.h\"\n@@ -50,6 +51,7 @@ static GUI_WINDOW_REC *gui_window_init(WINDOW_REC *window,\n 					   !settings_get_bool(\"indent_always\"),\n 					   get_default_indent_func());\n 	textbuffer_view_set_break_wide(gui->view, settings_get_bool(\"break_wide\"));\n+	textbuffer_view_set_hidden_level(gui->view, MSGLEVEL_HIDDEN);\n 	if (parent->active == window)\n 		textbuffer_view_set_window(gui->view, parent->screen_win);\n 	return gui;\n@@ -204,6 +206,8 @@ void gui_windows_reset_settings(void)\n 		WINDOW_REC *rec = tmp->data;\n 		GUI_WINDOW_REC *gui = WINDOW_GUI(rec);\n \n+		textbuffer_view_set_hidden_level(gui->view, MSGLEVEL_HIDDEN);\n+\n 		textbuffer_view_set_break_wide(gui->view, settings_get_bool(\"break_wide\"));\n \n 		textbuffer_view_set_default_indent(gui->view,\n','false','false','none'),('irssi','1.0.4','8dfeca57ede1e726de07522a87203ce13676882d','ailin-nemui','hidden lines','mainwindows-layout.c','167','38','10','0','MODIFY','@@ -23,6 +23,7 @@\n #include \"misc.h\"\n #include \"lib-config/iconfig.h\"\n #include \"settings.h\"\n+#include \"levels.h\"\n \n #include \"mainwindows.h\"\n #include \"gui-windows.h\"\n@@ -41,6 +42,12 @@ static void sig_layout_window_save(WINDOW_REC *window, CONFIG_NODE *node)\n 			iconfig_node_set_int(node, \"parent\", active->refnum);\n 	}\n \n+	if (gui->view->hidden_level != MSGLEVEL_HIDDEN) {\n+		char *level = bits2level(gui->view->hidden_level);\n+		iconfig_node_set_str(node, \"hidelevel\", level);\n+		g_free(level);\n+	}\n+\n 	if (gui->use_scroll)\n                 iconfig_node_set_bool(node, \"scroll\", gui->scroll);\n }\n@@ -58,6 +65,9 @@ static void sig_layout_window_restore(WINDOW_REC *window, CONFIG_NODE *node)\n \n 	if (config_node_get_bool(node, \"sticky\", FALSE))\n 		gui_window_set_sticky(window);\n+\n+	textbuffer_view_set_hidden_level(gui->view, level2bits(config_node_get_str(node, \"hidelevel\", \"HIDDEN\"), NULL));\n+\n 	if (config_node_get_str(node, \"scroll\", NULL) != NULL) {\n 		gui->use_scroll = TRUE;\n 		gui->scroll = config_node_get_bool(node, \"scroll\", TRUE);\n','false','false','none'),('irssi','1.0.4','8dfeca57ede1e726de07522a87203ce13676882d','ailin-nemui','hidden lines','module-formats.c','49','0','1','0','MODIFY','@@ -50,6 +50,7 @@ FORMAT_REC gui_text_formats[] =\n 	{ \"window_info_scroll\", \"%#Scroll  : $0\", 1, { 0 } },\n 	{ \"window_scroll\", \"Window scroll mode is now $0\", 1, { 0 } },\n 	{ \"window_scroll_unknown\", \"Unknown scroll mode $0, must be ON, OFF or DEFAULT\", 1, { 0 } },\n+	{ \"window_hidelevel\", \"Window hidden level is now $0\", 1, { 0 } },\n \n 	/* ---- */\n 	{ NULL, \"Statusbars\", 0 },\n','false','false','none'),('irssi','1.0.4','8dfeca57ede