\n 		if (*s == \':\') {\n 			memmove(s, s+1, strlen(s+1)+1);\n@@ -299,7 +301,7 @@ static void strip_params_colon(char *const params)\n 		}\n \n 		s = strchr(s, \' \');\n-		if (!s) {\n+		if (s == NULL) {\n 			return;\n 		}\n \n','false','false','none'),('irssi','1.0.5','1328e1ba08430b0eaa589919272d732bb84a6921','Will Storey','add a test program to test mode parsing\n\nthis uses glib\'s testing framework. it is to test the changes to the\nmode parsing for #603.','.gitignore','54','0','3','0','MODIFY','@@ -49,6 +49,9 @@ src/perl/*/Makefile.old\n src/fe-fuzz/crash-*\n src/fe-fuzz/oom-*\n \n+tests/irc/core/test-irc\n+tests/irc/core/test-irc.trs\n+\n *.a\n *.bs\n *.la\n','false','false','none'),('irssi','1.0.5','1328e1ba08430b0eaa589919272d732bb84a6921','Will Storey','add a test program to test mode parsing\n\nthis uses glib\'s testing framework. it is to test the changes to the\nmode parsing for #603.','.travis.yml','50','0','3','1','MODIFY','@@ -49,5 +49,7 @@ before_script:\n     - echo ^quit >> irssi-test/startup\n     - irssi-build/bin/irssi --home irssi-test\n     - cat irc.log.*\n+    - cd $TRAVIS_BUILD_DIR\n \n-script: true\n+script:\n+    - make check\n','false','false','none'),('irssi','1.0.5','1328e1ba08430b0eaa589919272d732bb84a6921','Will Storey','add a test program to test mode parsing\n\nthis uses glib\'s testing framework. it is to test the changes to the\nmode parsing for #603.','Makefile.am','22','0','1','1','MODIFY','@@ -16,7 +16,7 @@ irssi-version.h:\n 	VERSION=\"$(VERSION)\" $(srcdir)/utils/irssi-version.sh $(srcdir) | \\\n 		cmp -s - $@ || VERSION=\"$(VERSION)\" $(srcdir)/utils/irssi-version.sh $(srcdir) >$@\n \n-SUBDIRS = src docs scripts themes utils\n+SUBDIRS = src tests docs scripts themes utils\n \n confdir = $(sysconfdir)\n conf_DATA = irssi.conf\n','false','false','none'),('irssi','1.0.5','1328e1ba08430b0eaa589919272d732bb84a6921','Will Storey','add a test program to test mode parsing\n\nthis uses glib\'s testing framework. it is to test the changes to the\nmode parsing for #603.','configure.ac','703','4','3','0','MODIFY','@@ -658,6 +658,9 @@ src/perl/ui/Makefile.PL\n src/perl/textui/Makefile.PL\n scripts/Makefile\n scripts/examples/Makefile\n+tests/Makefile\n+tests/irc/Makefile\n+tests/irc/core/Makefile\n docs/Makefile\n docs/help/Makefile\n docs/help/in/Makefile\n','false','false','none'),('irssi','1.0.5','1328e1ba08430b0eaa589919272d732bb84a6921','Will Storey','add a test program to test mode parsing\n\nthis uses glib\'s testing framework. it is to test the changes to the\nmode parsing for #603.','Makefile.am','1','0','1','0','ADD','@@ -0,0 +1 @@\n+SUBDIRS = irc\n','false','false','none'),('irssi','1.0.5','1328e1ba08430b0eaa589919272d732bb84a6921','Will Storey','add a test program to test mode parsing\n\nthis uses glib\'s testing framework. it is to test the changes to the\nmode parsing for #603.','Makefile.am','1','0','1','0','ADD','@@ -0,0 +1 @@\n+SUBDIRS = core\n','false','false','none'),('irssi','1.0.5','1328e1ba08430b0eaa589919272d732bb84a6921','Will Storey','add a test program to test mode parsing\n\nthis uses glib\'s testing framework. it is to test the changes to the\nmode parsing for #603.','Makefile.am','31','0','36','0','ADD','@@ -0,0 +1,36 @@\n+AM_CPPFLAGS = \\\n+	-I$(top_srcdir)/src \\\n+	-I$(top_srcdir)/src/core \\\n+	-DSYSCONFDIR=\\\"\"$(sysconfdir)\"\\\" \\\n+	$(GLIB_CFLAGS)\n+\n+TESTS = test-irc\n+check_PROGRAMS = test-irc\n+\n+test_irc_CPPFLAGS = \\\n+	-I$(top_srcdir)/src/irc/core \\\n+	$(AM_CPPFLAGS)\n+\n+test_irc_DEPENDENCIES = \\\n+	../../../src/core/libcore.a \\\n+	../../../src/lib-config/libirssi_config.a\n+\n+test_irc_LDADD = \\\n+	../../../src/core/libcore.a \\\n+	../../../src/lib-config/libirssi_config.a \\\n+	@GLIB_LIBS@ \\\n+	@OPENSSL_LIBS@\n+\n+test_irc_SOURCES = \\\n+	test-irc.c \\\n+	../../../src/irc/core/irc-cap.c \\\n+	../../../src/irc/core/irc-nicklist.c \\\n+	../../../src/irc/core/irc-queries.c \\\n+	../../../src/irc/core/irc-servers-reconnect.c \\\n+	../../../src/irc/core/irc-servers-setup.c \\\n+	../../../src/irc/core/irc-servers.c \\\n+	../../../src/irc/core/irc.c \\\n+	../../../src/irc/core/mode-lists.c \\\n+	../../../src/irc/core/modes.c \\\n+	../../../src/irc/core/servers-idle.c \\\n+	../../../src/irc/core/servers-redirect.c\n','false','false','none'),('irssi','1.0.5','1328e1ba08430b0eaa589919272d732bb84a6921','Will Storey','add a test program to test mode parsing\n\nthis uses glib\'s testing framework. it is to test the changes to the\nmode parsing for #603.','test-irc.c','189','5','230','0','ADD','@@ -0,0 +1,230 @@\n+/*\n+ test-irc.c : irssi\n+\n+    Copyright (C) 2017 Will Storey\n+\n+    This program is free software; you can redistribute it and/or modify\n+    it under the terms of the GNU General Public License as published by\n+    the Free Software Foundation; either version 2 of the License, or\n+    (at your option) any later version.\n+\n+    This program is distributed in the hope that it will be useful,\n+    but WITHOUT ANY WARRANTY; without even the implied warranty of\n+    MERCHANTABILITY or FITNESS FOR A PARTICULAR PURPOSE.  See the\n+    GNU General Public License for more details.\n+\n+    You should have received a copy of the GNU General Public License along\n+    with this program; if not, write to the Free Software Foundation, Inc.,\n+    51 Franklin Street, Fifth Floor, Boston, MA 02110-1301 USA.\n+*/\n+\n+#include <glib.h>\n+#include <irc.h>\n+#include <string.h>\n+\n+static void test_event_get_param(void);\n+static void test_event_get_params(void);\n+\n+int main(int argc, char **argv)\n+{\n+	g_test_init(&argc, &argv, NULL);\n+\n+	g_test_add_func(\"/test/event_get_param\", test_event_get_param);\n+	g_test_add_func(\"/test/event_get_params\", test_event_get_params);\n+\n+	return g_test_run();\n+}\n+\n+static void test_event_get_param(void)\n+{\n+	struct test_case {\n+		char const *const description;\n+		char const *const input;\n+		char const *const input_after;\n+		char const *const output;\n+	};\n+\n+	struct test_case const tests[] = {\n+		{\n+			.description = \"Zero parameters\",\n+			.input       = \"\",\n+			.input_after = \"\",\n+			.output      = \"\",\n+		},\n+		{\n+			.description = \"One parameter\",\n+			.input       = \"#test\",\n+			.input_after = \"\",\n+			.output      = \"#test\",\n+		},\n+		{\n+			.description = \"One parameter, trailing space\",\n+			.input       = \"#test \",\n+			.input_after = \"\",\n+			.output      = \"#test\",\n+		},\n+		{\n+			.description = \"One parameter, more trailing space\",\n+			.input       = \"#test  \",\n+			.input_after = \" \",\n+			.output      = \"#test\",\n+		},\n+		{\n+			.description = \"Two parameters\",\n+			.input       = \"#test +o\",\n+			.input_after = \"+o\",\n+			.output      = \"#test\",\n+		},\n+		{\n+			.description = \"Two parameters continued\",\n+			.input       = \"+o\",\n+			.input_after = \"\",\n+			.output      = \"+o\",\n+		},\n+		{\n+			.description = \"Two parameters with trailing space\",\n+			.input       = \"#test +o \",\n+			.input_after = \"+o \",\n+			.output      = \"#test\",\n+		},\n+		{\n+			.description = \"Two parameters with trailing space continued\",\n+			.input       = \"+o \",\n+			.input_after = \"\",\n+			.output      = \"+o\",\n+		},\n+		{\n+			.description = \"Two parameters with inline and trailing space\",\n+			.input       = \"#test  +o \",\n+			.input_after = \" +o \",\n+			.output      = \"#test\",\n+		},\n+		/* TODO: It seems not ideal that the caller has to deal with inline space.\n+		 */\n+		{\n+			.description = \"Two parameters with inline and trailing space continued\",\n+			.input       = \" +o \",\n+			.input_after = \"+o \",\n+			.output      = \"\",\n+		},\n+	};\n+\n+	char *buf = g_malloc0(1024);\n+\n+	int i = 0;\n+	for (i = 0; i < sizeof(tests)/sizeof(tests[0]); i++) {\n+		struct test_case const test = tests[i];\n+\n+		memcpy(buf, test.input, strlen(test.input)+1);\n+		char *input = buf;\n+\n+		char *const output = event_get_param(&input);\n+\n+		g_assert_cmpstr(input, ==, test.input_after);\n+		g_assert_cmpstr(output, ==, test.output);\n+	}\n+\n+	g_free(buf);\n+}\n+\n+static void test_event_get_params(void)\n+{\n+	struct test_case {\n+		char const *const description;\n+		char const *const input;\n+		char const *const output0;\n+		char const *const output1;\n+	};\n+\n+	struct test_case const tests[] = {\n+		{\n+			.description = \"Only a channel\",\n+			.input       = \"#test\",\n+			.output0     = \"#test\",\n+			.output1     = \"\",\n+		},\n+		{\n+			.description = \"Only a channel with trailing space\",\n+			.input       = \"#test \",\n+			.output0     = \"#test\",\n+			.output1     = \"\",\n+		},\n+		{\n+			.description = \"No :<trailing>, channel mode with one parameter after channel name\",\n+			.input       = \"#test +i\",\n+			.output0     = \"#test\",\n+			.output1     = \"+i\",\n+		},\n+		{\n+			.description = \"No :<trailing>, channel mode with two parameters after channel name\",\n+			.input       = \"#test +o tester\",\n+			.output0     = \"#test\",\n+			.output1     = \"+o tester\",\n+		},\n+		{\n+			.description = \"No :<trailing>, channel mode with three parameters afer channel name\",\n+			.input       = \"#test +ov tester tester2\",\n+			.output0     = \"#test\",\n+			.output1     = \"+ov tester tester2\",\n+		},\n+		{\n+			.description = \"No :<trailing>, channel mode with three parameters afer channel name, bunch of extra space\",\n+			.input       = \"#test  +ov  tester  tester2 \",\n+			.output0     = \"#test\",\n+			.output1     = \" +ov  tester  tester2 \",\n+		},\n+		{\n+			.description = \"Channel mode with one parameter after channel name, :<trailing> at the start of modes\",\n+			.input       = \"#test :+i\",\n+			.output0     = \"#test\",\n+			.output1     = \"+i\",\n+		},\n+		{\n+			.description = \"Channel mode with two parameters after channel name, :<trailing> at the  start of modes\",\n+			.input       = \"#test :+o tester\",\n+			.output0     = \"#test\",\n+			.output1     = \"+o tester\",\n+		},\n+		{\n+			.description = \"Channel mode with three parameters after channel name, :<trailing> at the start of modes\",\n+			.input       = \"#test :+ov tester tester2\",\n+			.output0     = \"#test\",\n+			.output1     = \"+ov tester tester2\",\n+		},\n+		{\n+			.description = \"Channel mode with two parameters after channel name, :<trailing> on the final parameter\",\n+			.input       = \"#test +o :tester\",\n+			.output0     = \"#test\",\n+			.output1     = \"+o tester\",\n+		},\n+		{\n+			.description = \"Channel mode with three parameters after channel name, :<trailing> on the final parameter\",\n+			.input       = \"#test +ov tester :tester2\",\n+			.output0     = \"#test\",\n+			.output1     = \"+ov tester tester2\",\n+		},\n+		{\n+			.description = \"Channel mode with three parameters after channel name, :<trailing> on the final parameter, also a second : present\",\n+			.input       = \"#test +ov tester :tester2 hi:there\",\n+			.output0     = \"#test\",\n+			.output1     = \"+ov tester tester2 hi:there\",\n+		},\n+	};\n+\n+	int i = 0;\n+	for (i = 0; i < sizeof(tests)/sizeof(tests[0]); i++) {\n+		struct test_case const test = tests[i];\n+\n+		char *output0 = NULL;\n+		char *output1 = NULL;\n+		char *const params = event_get_params(test.input, 2 | PARAM_FLAG_GETREST,\n+				&output0, &output1);\n+\n+		/* params happens to always point at the first output */\n+		g_assert_cmpstr(params, ==, test.output0);\n+		g_assert_cmpstr(output0, ==, test.output0);\n+		g_assert_cmpstr(output1, ==, test.output1);\n+\n+		g_free(params);\n+	}\n+}\n','false','false','none'),('irssi','1.0.5','c20eddeb385974d6f46494e71ab63d4493df119b','ailin-nemui','add glib testing tap utilities','configure.ac','704','4','2','0','MODIFY','@@ -295,6 +295,8 @@ fi\n \n LIBS=\"$LIBS $GLIB_LIBS\"\n \n+GLIB_TESTS\n+\n dnl **\n dnl ** OpenSSL checks\n dnl **\n','false','false','none'),('irssi','1.0.5','c20eddeb385974d6f46494e71ab63d4493df119b','ailin-nemui','add glib testing tap utilities','glibtests.m4','27','0','28','0','ADD','@@ -0,0 +1,28 @@\n+dnl GLIB_TESTS\n+dnl\n+\n+AC_DEFUN([GLIB_TESTS],\n+[\n+  AC_ARG_ENABLE(installed-tests,\n+                AS_HELP_STRING([--enable-installed-tests],\n+                               [Enable installation of some test cases]),\n+                [case ${enableval} in\n+                  yes) ENABLE_INSTALLED_TESTS=\"1\"  ;;\n+                  no)  ENABLE_INSTALLED_TESTS=\"\" ;;\n+                  *) AC_MSG_ERROR([bad value ${enableval} for --enable-installed-tests]) ;;\n+                 esac])\n+  AM_CONDITIONAL([ENABLE_INSTALLED_TESTS], test \"$ENABLE_INSTALLED_TESTS\" = \"1\")\n+  AC_ARG_ENABLE(always-build-tests,\n+                AS_HELP_STRING([--enable-always-build-tests],\n+                               [Enable always building tests during \'make all\']),\n+                [case ${enableval} in\n+                  yes) ENABLE_ALWAYS_BUILD_TESTS=\"1\"  ;;\n+                  no)  ENABLE_ALWAYS_BUILD_TESTS=\"\" ;;\n+                  *) AC_MSG_ERROR([bad value ${enableval} for --enable-always-build-tests]) ;;\n+                 esac])\n+  AM_CONDITIONAL([ENABLE_ALWAYS_BUILD_TESTS], test \"$ENABLE_ALWAYS_BUILD_TESTS\" = \"1\")\n+  if test \"$ENABLE_INSTALLED_TESTS\" = \"1\"; then\n+    AC_SUBST(installed_test_metadir, [${datadir}/installed-tests/]AC_PACKAGE_NAME)\n+    AC_SUBST(installed_testdir, [${libexecdir}/installed-tests/]AC_PACKAGE_NAME)\n+  fi\n+])\n','false','false','none'),('irssi','1.0.5','c20eddeb385974d6f46494e71ab63d4493df119b','ailin-nemui','add glib testing tap utilities','Makefile.am','31','0','3','2','MODIFY','@@ -1,11 +1,12 @@\n+include $(top_srcdir)/utils/glib-tap.mk\n+\n AM_CPPFLAGS = \\\n 	-I$(top_srcdir)/src \\\n 	-I$(top_srcdir)/src/core \\\n 	-DSYSCONFDIR=\\\"\"$(sysconfdir)\"\\\" \\\n 	$(GLIB_CFLAGS)\n \n-TESTS = test-irc\n-check_PROGRAMS = test-irc\n+test_programs = test-irc\n \n test_irc_CPPFLAGS = \\\n 	-I$(top_srcdir)/src/irc/core \\\n','false','false','none'),('irssi','1.0.5','c20eddeb385974d6f46494e71ab63d4493df119b','ailin-nemui','add glib testing tap utilities','test-irc.c','190','5','1','0','MODIFY','@@ -32,6 +32,7 @@ int main(int argc, char **argv)\n 	g_test_add_func(\"/test/event_get_param\", test_event_get_param);\n 	g_test_add_func(\"/test/event_get_params\", test_event_get_params);\n \n+	g_test_set_nonfatal_assertions();\n 	return g_test_run();\n }\n \n','false','false','none'),('irssi','1.0.5','c20eddeb385974d6f46494e71ab63d4493df119b','ailin-nemui','add glib testing tap utilities','Makefile.am','6','0','3','1','MODIFY','@@ -1,4 +1,6 @@\n EXTRA_DIST = \\\n 	file2header.sh \\\n 	irssi-version.sh \\\n-	syntax.pl\n+	syntax.pl \\\n+	tap-driver.sh \\\n+	tap-test\n','false','false','none'),('irssi','1.0.5','c20eddeb385974d6f46494e71ab63d4493df119b','ailin-nemui','add glib testing tap utilities','glib-tap.mk','86','0','134','0','ADD','@@ -0,0 +1,134 @@\n+# GLIB - Library of useful C routines\n+\n+TESTS_ENVIRONMENT= \\\n+	G_TEST_SRCDIR=\"$(abs_srcdir)\" 		\\\n+	G_TEST_BUILDDIR=\"$(abs_builddir)\" 	\\\n+	G_DEBUG=gc-friendly 			\\\n+	MALLOC_CHECK_=2 			\\\n+	MALLOC_PERTURB_=$$(($${RANDOM:-256} % 256))\n+LOG_DRIVER = env AM_TAP_AWK=\'$(AWK)\' $(SHELL) $(top_srcdir)/utils/tap-driver.sh\n+LOG_COMPILER = $(top_srcdir)/utils/tap-test\n+\n+NULL =\n+\n+# initialize variables for unconditional += appending\n+BUILT_SOURCES =\n+BUILT_EXTRA_DIST =\n+CLEANFILES = *.log *.trs\n+DISTCLEANFILES =\n+MAINTAINERCLEANFILES =\n+EXTRA_DIST =\n+TESTS =\n+\n+installed_test_LTLIBRARIES =\n+installed_test_PROGRAMS =\n+installed_test_SCRIPTS =\n+nobase_installed_test_DATA =\n+\n+noinst_LTLIBRARIES =\n+noinst_PROGRAMS =\n+noinst_SCRIPTS =\n+noinst_DATA =\n+\n+check_LTLIBRARIES =\n+check_PROGRAMS =\n+check_SCRIPTS =\n+check_DATA =\n+\n+# We support a fairly large range of possible variables.  It is expected that all types of files in a test suite\n+# will belong in exactly one of the following variables.\n+#\n+# First, we support the usual automake suffixes, but in lowercase, with the customary meaning:\n+#\n+#   test_programs, test_scripts, test_data, test_ltlibraries\n+#\n+# The above are used to list files that are involved in both uninstalled and installed testing.  The\n+# test_programs and test_scripts are taken to be actual testcases and will be run as part of the test suite.\n+# Note that _data is always used with the nobase_ automake variable name to ensure that installed test data is\n+# installed in the same way as it appears in the package layout.\n+#\n+# In order to mark a particular file as being only for one type of testing, use \'installed\' or \'uninstalled\',\n+# like so:\n+#\n+#   installed_test_programs, uninstalled_test_programs\n+#   installed_test_scripts, uninstalled_test_scripts\n+#   installed_test_data, uninstalled_test_data\n+#   installed_test_ltlibraries, uninstalled_test_ltlibraries\n+#\n+# Additionally, we support \'extra\' infixes for programs and scripts.  This is used for support programs/scripts\n+# that should not themselves be run as testcases (but exist to be used from other testcases):\n+#\n+#   test_extra_programs, installed_test_extra_programs, uninstalled_test_extra_programs\n+#   test_extra_scripts, installed_test_extra_scripts, uninstalled_test_extra_scripts\n+#\n+# Additionally, for _scripts and _data, we support the customary dist_ prefix so that the named script or data\n+# file automatically end up in the tarball.\n+#\n+#   dist_test_scripts, dist_test_data, dist_test_extra_scripts\n+#   dist_installed_test_scripts, dist_installed_test_data, dist_installed_test_extra_scripts\n+#   dist_uninstalled_test_scripts, dist_uninstalled_test_data, dist_uninstalled_test_extra_scripts\n+#\n+# Note that no file is automatically disted unless it appears in one of the dist_ variables.  This follows the\n+# standard automake convention of not disting programs scripts or data by default.\n+#\n+# test_programs, test_scripts, uninstalled_test_programs and uninstalled_test_scripts (as well as their disted\n+# variants) will be run as part of the in-tree \'make check\'.  These are all assumed to be runnable under\n+# gtester.  That\'s a bit strange for scripts, but it\'s possible.\n+\n+TESTS += $(test_programs) $(test_scripts) $(uninstalled_test_programs) $(uninstalled_test_scripts) \\\n+         $(dist_test_scripts) $(dist_uninstalled_test_scripts)\n+\n+# Note: build even the installed-only targets during \'make check\' to ensure that they still work.\n+# We need to do a bit of trickery here and manage disting via EXTRA_DIST instead of using dist_ prefixes to\n+# prevent automake from mistreating gmake functions like $(wildcard ...) and $(addprefix ...) as if they were\n+# filenames, including removing duplicate instances of the opening part before the space, eg. \'$(addprefix\'.\n+all_test_programs     = $(test_programs) $(uninstalled_test_programs) $(installed_test_programs) \\\n+                        $(test_extra_programs) $(uninstalled_test_extra_programs) $(installed_test_extra_programs)\n+all_test_scripts      = $(test_scripts) $(uninstalled_test_scripts) $(installed_test_scripts) \\\n+                        $(test_extra_scripts) $(uninstalled_test_extra_scripts) $(installed_test_extra_scripts)\n+all_dist_test_scripts = $(dist_test_scripts) $(dist_uninstalled_test_scripts) $(dist_installed_test_scripts) \\\n+                        $(dist_test_extra_scripts) $(dist_uninstalled_test_extra_scripts) $(dist_installed_test_extra_scripts)\n+all_test_scripts     += $(all_dist_test_scripts)\n+EXTRA_DIST           += $(all_dist_test_scripts)\n+all_test_data         = $(test_data) $(uninstalled_test_data) $(installed_test_data)\n+all_dist_test_data    = $(dist_test_data) $(dist_uninstalled_test_data) $(dist_installed_test_data)\n+all_test_data        += $(all_dist_test_data)\n+EXTRA_DIST           += $(all_dist_test_data)\n+all_test_ltlibs       = $(test_ltlibraries) $(uninstalled_test_ltlibraries) $(installed_test_ltlibraries)\n+\n+if ENABLE_ALWAYS_BUILD_TESTS\n+noinst_LTLIBRARIES += $(all_test_ltlibs)\n+noinst_PROGRAMS += $(all_test_programs)\n+noinst_SCRIPTS += $(all_test_scripts)\n+noinst_DATA += $(all_test_data)\n+else\n+check_LTLIBRARIES += $(all_test_ltlibs)\n+check_PROGRAMS += $(all_test_programs)\n+check_SCRIPTS += $(all_test_scripts)\n+check_DATA += $(all_test_data)\n+endif\n+\n+if ENABLE_INSTALLED_TESTS\n+installed_test_PROGRAMS += $(test_programs) $(installed_test_programs) \\\n+                          $(test_extra_programs) $(installed_test_extra_programs)\n+installed_test_SCRIPTS += $(test_scripts) $(installed_test_scripts) \\\n+                          $(test_extra_scripts) $(test_installed_extra_scripts)\n+installed_test_SCRIPTS += $(dist_test_scripts) $(dist_test_extra_scripts) \\\n+                          $(dist_installed_test_scripts) $(dist_installed_test_extra_scripts)\n+nobase_installed_test_DATA += $(test_data) $(installed_test_data)\n+nobase_installed_test_DATA += $(dist_test_data) $(dist_installed_test_data)\n+installed_test_LTLIBRARIES += $(test_ltlibraries) $(installed_test_ltlibraries)\n+installed_testcases = $(test_programs) $(installed_test_programs) \\\n+                      $(test_scripts) $(installed_test_scripts) \\\n+                      $(dist_test_scripts) $(dist_installed_test_scripts)\n+\n+installed_test_meta_DATA = $(installed_testcases:=.test)\n+\n+%.test: %$(EXEEXT) Makefile\n+	$(AM_V_GEN) (echo \'[Test]\' > $@.tmp; \\\n+	echo \'Type=session\' >> $@.tmp; \\\n+	echo \'Exec=$(installed_testdir)/$<\' >> $@.tmp; \\\n+	mv $@.tmp $@)\n+\n+CLEANFILES += $(installed_test_meta_DATA)\n+endif\n','false','false','none'),('irssi','1.0.5','c20eddeb385974d6f46494e71ab63d4493df119b','ailin-nemui','add glib testing tap utilities','tap-driver.sh','452','2','652','0','ADD','@@ -0,0 +1,652 @@\n+#! /bin/sh\n+# Copyright (C) 2011-2013 Free Software Foundation, Inc.\n+#\n+# This program is free software; you can redistribute it and/or modify\n+# it under the terms of the GNU General Public License as published by\n+# the Free Software Foundation; either version 2, or (at your option)\n+# any later version.\n+#\n+# This program is distributed in the hope that it will be useful,\n+# but WITHOUT ANY WARRANTY; without even the implied warranty of\n+# MERCHANTABILITY or FITNESS FOR A PARTICULAR PURPOSE.  See the\n+# GNU General Public License for more details.\n+#\n+# You should have received a copy of the GNU General Public License\n+# along with this program.  If not, see <http://www.gnu.org/licenses/>.\n+\n+# As a special exception to the GNU General Public License, if you\n+# distribute this file as part of a program that contains a\n+# configuration script generated by Autoconf, you may include it under\n+# the same distribution terms that you use for the rest of that program.\n+\n+# This file is maintained in Automake, please report\n+# bugs to <bug-automake@gnu.org> or send patches to\n+# <automake-patches@gnu.org>.\n+\n+scriptversion=2011-12-27.17; # UTC\n+\n+# Make unconditional expansion of undefined variables an error.  This\n+# helps a lot in preventing typo-related bugs.\n+set -u\n+\n+me=tap-driver.sh\n+\n+fatal ()\n+{\n+  echo \"$me: fatal: $*\" >&2\n+  exit 1\n+}\n+\n+usage_error ()\n+{\n+  echo \"$me: $*\" >&2\n+  print_usage >&2\n+  exit 2\n+}\n+\n+print_usage ()\n+{\n+  cat <<END\n+Usage:\n+  tap-driver.sh --test-name=NAME --log-file=PATH --trs-file=PATH\n+                [--expect-failure={yes|no}] [--color-tests={yes|no}]\n+                [--enable-hard-errors={yes|no}] [--ignore-exit]\n+                [--diagnostic-string=STRING] [--merge|--no-merge]\n+                [--comments|--no-comments] [--] TEST-COMMAND\n+The \\`--test-name\', \\`--log-file\' and \\`--trs-file\' options are mandatory.\n+END\n+}\n+\n+# TODO: better error handling in option parsing (in particular, ensure\n+# TODO: $log_file, $trs_file and $test_name are defined).\n+test_name= # Used for reporting.\n+log_file=  # Where to save the result and output of the test script.\n+trs_file=  # Where to save the metadata of the test run.\n+expect_failure=0\n+color_tests=0\n+merge=0\n+ignore_exit=0\n+comments=0\n+diag_string=\'#\'\n+while test $# -gt 0; do\n+  case $1 in\n+  --help) print_usage; exit $?;;\n+  --version) echo \"$me $scriptversion\"; exit $?;;\n+  --test-name) test_name=$2; shift;;\n+  --log-file) log_file=$2; shift;;\n+  --trs-file) trs_file=$2; shift;;\n+  --color-tests) color_tests=$2; shift;;\n+  --expect-failure) expect_failure=$2; shift;;\n+  --enable-hard-errors) shift;; # No-op.\n+  --merge) merge=1;;\n+  --no-merge) merge=0;;\n+  --ignore-exit) ignore_exit=1;;\n+  --comments) comments=1;;\n+  --no-comments) comments=0;;\n+  --diagnostic-string) diag_string=$2; shift;;\n+  --) shift; break;;\n+  -*) usage_error \"invalid option: \'$1\'\";;\n+  esac\n+  shift\n+done\n+\n+test $# -gt 0 || usage_error \"missing test command\"\n+\n+case $expect_failure in\n+  yes) expect_failure=1;;\n+    *) expect_failure=0;;\n+esac\n+\n+if test $color_tests = yes; then\n+  init_colors=\'\n+    color_map[\"red\"]=\"[0;31m\" # Red.\n+    color_map[\"grn\"]=\"[0;32m\" # Green.\n+    color_map[\"lgn\"]=\"[1;32m\" # Light green.\n+    color_map[\"blu\"]=\"[1;34m\" # Blue.\n+    color_map[\"mgn\"]=\"[0;35m\" # Magenta.\n+    color_map[\"std\"]=\"[m\"     # No color.\n+    color_for_result[\"ERROR\"] = \"mgn\"\n+    color_for_result[\"PASS\"]  = \"grn\"\n+    color_for_result[\"XPASS\"] = \"red\"\n+    color_for_result[\"FAIL\"]  = \"red\"\n+    color_for_result[\"XFAIL\"] = \"lgn\"\n+    color_for_result[\"SKIP\"]  = \"blu\"\'\n+else\n+  init_colors=\'\'\n+fi\n+\n+# :; is there to work around a bug in bash 3.2 (and earlier) which\n+# does not always set \'$?\' properly on redirection failure.\n+# See the Autoconf manual for more details.\n+:;{\n+  (\n+    # Ignore common signals (in this subshell only!), to avoid potential\n+    # problems with Korn shells.  Some Korn shells are known to propagate\n+    # to themselves signals that have killed a child process they were\n+    # waiting for; this is done at least for SIGINT (and usually only for\n+    # it, in truth).  Without the `trap\' below, such a behaviour could\n+    # cause a premature exit in the current subshell, e.g., in case the\n+    # test command it runs gets terminated by a SIGINT.  Thus, the awk\n+    # script we are piping into would never seen the exit status it\n+    # expects on its last input line (which is displayed below by the\n+    # last `echo $?\' statement), and would thus die reporting an internal\n+    # error.\n+    # For more information, see the Autoconf manual and the threads:\n+    # <http://lists.gnu.org/archive/html/bug-autoconf/2011-09/msg00004.html>\n+    # <http://mail.opensolaris.org/pipermail/ksh93-integration-discuss/2009-February/004121.html>\n+    trap : 1 3 2 13 15\n+    if test $merge -gt 0; then\n+      exec 2>&1\n+    else\n+      exec 2>&3\n+    fi\n+    \"$@\"\n+    echo $?\n+  ) | LC_ALL=C ${AM_TAP_AWK-awk} \\\n+        -v me=\"$me\" \\\n+        -v test_script_name=\"$test_name\" \\\n+        -v log_file=\"$log_file\" \\\n+        -v trs_file=\"$trs_file\" \\\n+        -v expect_failure=\"$expect_failure\" \\\n+        -v merge=\"$merge\" \\\n+        -v ignore_exit=\"$ignore_exit\" \\\n+        -v comments=\"$comments\" \\\n+        -v diag_string=\"$diag_string\" \\\n+\'\n+# FIXME: the usages of \"cat >&3\" below could be optimized when using\n+# FIXME: GNU awk, and/on on systems that supports /dev/fd/.\n+\n+# Implementation note: in what follows, `result_obj` will be an\n+# associative array that (partly) simulates a TAP result object\n+# from the `TAP::Parser` perl module.\n+\n+## ----------- ##\n+##  FUNCTIONS  ##\n+## ----------- ##\n+\n+function fatal(msg)\n+{\n+  print me \": \" msg | \"cat >&2\"\n+  exit 1\n+}\n+\n+function abort(where)\n+{\n+  fatal(\"internal error \" where)\n+}\n+\n+# Convert a boolean to a \"yes\"/\"no\" string.\n+function yn(bool)\n+{\n+  return bool ? \"yes\" : \"no\";\n+}\n+\n+function add_test_result(result)\n+{\n+  if (!test_results_index)\n+    test_results_index = 0\n+  test_results_list[test_results_index] = result\n+  test_results_index += 1\n+  test_results_seen[result] = 1;\n+}\n+\n+# Whether the test script should be re-run by \"make recheck\".\n+function must_recheck()\n+{\n+  for (k in test_results_seen)\n+    if (k != \"XFAIL\" && k != \"PASS\" && k != \"SKIP\")\n+      return 1\n+  return 0\n+}\n+\n+# Whether the content of the log file associated to this test should\n+# be copied into the \"global\" test-suite.log.\n+function copy_in_global_log()\n+{\n+  for (k in test_results_seen)\n+    if (k != \"PASS\")\n+      return 1\n+  return 0\n+}\n+\n+# FIXME: this can certainly be improved ...\n+function get_global_test_result()\n+{\n+    if (\"ERROR\" in test_results_seen)\n+      return \"ERROR\"\n+    if (\"FAIL\" in test_results_seen || \"XPASS\" in test_results_seen)\n+      return \"FAIL\"\n+    all_skipped = 1\n+    for (k in test_results_seen)\n+      if (k != \"SKIP\")\n+        all_skipped = 0\n+    if (all_skipped)\n+      return \"SKIP\"\n+    return \"PASS\";\n+}\n+\n+function stringify_result_obj(result_obj)\n+{\n+  if (result_obj[\"is_unplanned\"] || result_obj[\"number\"] != testno)\n+    return \"ERROR\"\n+\n+  if (plan_seen == LATE_PLAN)\n+    return \"ERROR\"\n+\n+  if (result_obj[\"directive\"] == \"TODO\")\n+    return result_obj[\"is_ok\"] ? \"XPASS\" : \"XFAIL\"\n+\n+  if (result_obj[\"directive\"] == \"SKIP\")\n+    return result_obj[\"is_ok\"] ? \"SKIP\" : COOKED_FAIL;\n+\n+  if (length(result_obj[\"directive\"]))\n+      abort(\"in function stringify_result_obj()\")\n+\n+  return result_obj[\"is_ok\"] ? COOKED_PASS : COOKED_FAIL\n+}\n+\n+function decorate_result(result)\n+{\n+  color_name = color_for_result[result]\n+  if (color_name)\n+    return color_map[color_name] \"\" result \"\" color_map[\"std\"]\n+  # If we are not using colorized output, or if we do not know how\n+  # to colorize the given result, we should return it unchanged.\n+  return result\n+}\n+\n+function report(result, details)\n+{\n+  if (result ~ /^(X?(PASS|FAIL)|SKIP|ERROR)/)\n+    {\n+      msg = \": \" test_script_name\n+      add_test_result(result)\n+    }\n+  else if (result == \"#\")\n+    {\n+      msg = \" \" test_script_name \":\"\n+    }\n+  else\n+    {\n+      abort(\"in function report()\")\n+    }\n+  if (length(details))\n+    msg = msg \" \" details\n+  # Output on console might be colorized.\n+  print decorate_result(result) msg\n+  # Log the result in the log file too, to help debugging (this is\n+  # especially true when said result is a TAP error or \"Bail out!\").\n+  print result msg | \"cat >&3\";\n+}\n+\n+function testsuite_error(error_message)\n+{\n+  report(\"ERROR\", \"- \" error_message)\n+}\n+\n+function handle_tap_result()\n+{\n+  details = result_obj[\"number\"];\n+  if (length(result_obj[\"description\"]))\n+    details = details \" \" result_obj[\"description\"]\n+\n+  if (plan_seen == LATE_PLAN)\n+    {\n+      details = details \" # AFTER LATE PLAN\";\n+    }\n+  else if (result_obj[\"is_unplanned\"])\n+    {\n+       details = details \" # UNPLANNED\";\n+    }\n+  else if (result_obj[\"number\"] != testno)\n+    {\n+       details = sprintf(\"%s # OUT-OF-ORDER (expecting %d)\",\n+                         details, testno);\n+    }\n+  else if (result_obj[\"directive\"])\n+    {\n+      details = details \" # \" result_obj[\"directive\"];\n+      if (length(result_obj[\"explanation\"]))\n+        details = details \" \" result_obj[\"explanation\"]\n+    }\n+\n+  report(stringify_result_obj(result_obj), details)\n+}\n+\n+# `skip_reason` should be empty whenever planned > 0.\n+function handle_tap_plan(planned, skip_reason)\n+{\n+  planned += 0 # Avoid getting confused if, say, `planned` is \"00\"\n+  if (length(skip_reason) && planned > 0)\n+    abort(\"in function handle_tap_plan()\")\n+  if (plan_seen)\n+    {\n+      # Error, only one plan per stream is acceptable.\n+      testsuite_error(\"multiple test plans\")\n+      return;\n+    }\n+  planned_tests = planned\n+  # The TAP plan can come before or after *all* the TAP results; we speak\n+  # respectively of an \"early\" or a \"late\" plan.  If we see the plan line\n+  # after at least one TAP result has been seen, assume we have a late\n+  # plan; in this case, any further test result seen after the plan will\n+  # be flagged as an error.\n+  plan_seen = (testno >= 1 ? LATE_PLAN : EARLY_PLAN)\n+  # If testno > 0, we have an error (\"too many tests run\") that will be\n+  # automatically dealt with later, so do not worry about it here.  If\n+  # $plan_seen is true, we have an error due to a repeated plan, and that\n+  # has already been dealt with above.  Otherwise, we have a valid \"plan\n+  # with SKIP\" specification, and should report it as a particular kind\n+  # of SKIP result.\n+  if (planned == 0 && testno == 0)\n+    {\n+      if (length(skip_reason))\n+        skip_reason = \"- \"  skip_reason;\n+      report(\"SKIP\", skip_reason);\n+    }\n+}\n+\n+function extract_tap_comment(line)\n+{\n+  if (index(line, diag_string) == 1)\n+    {\n+      # Strip leading `diag_string` from `line`.\n+      line = substr(line, length(diag_string) + 1)\n+      # And strip any leading and trailing whitespace left.\n+      sub(\"^[ \\t]*\", \"\", line)\n+      sub(\"[ \\t]*$\", \"\", line)\n+      # Return what is left (if any).\n+      return line;\n+    }\n+  return \"\";\n+}\n+\n+# When this function is called, we know that line is a TAP result line,\n+# so that it matches the (perl) RE \"^(not )?ok\\b\".\n+function setup_result_obj(line)\n+{\n+  # Get the result, and remove it from the line.\n+  result_obj[\"is_ok\"] = (substr(line, 1, 2) == \"ok\" ? 1 : 0)\n+  sub(\"^(not )?ok[ \\t]*\", \"\", line)\n+\n+  # If the result has an explicit number, get it and strip it; otherwise,\n+  # automatically assing the next progresive number to it.\n+  if (line ~ /^[0-9]+$/ || line ~ /^[0-9]+[^a-zA-Z0-9_]/)\n+    {\n+      match(line, \"^[0-9]+\")\n+      # The final `+ 0` is to normalize numbers with leading zeros.\n+      result_obj[\"number\"] = substr(line, 1, RLENGTH) + 0\n+      line = substr(line, RLENGTH + 1)\n+    }\n+  else\n+    {\n+      result_obj[\"number\"] = testno\n+    }\n+\n+  if (plan_seen == LATE_PLAN)\n+    # No further test results are acceptable after a \"late\" TAP plan\n+    # has been seen.\n+    result_obj[\"is_unplanned\"] = 1\n+  else if (plan_seen && testno > planned_tests)\n+    result_obj[\"is_unplanned\"] = 1\n+  else\n+    result_obj[\"is_unplanned\"] = 0\n+\n+  # Strip trailing and leading whitespace.\n+  sub(\"^[ \\t]*\", \"\", line)\n+  sub(\"[ \\t]*$\", \"\", line)\n+\n+  # This will have to be corrected if we have a \"TODO\"/\"SKIP\" directive.\n+  result_obj[\"description\"] = line\n+  result_obj[\"directive\"] = \"\"\n+  result_obj[\"explanation\"] = \"\"\n+\n+  if (index(line, \"#\") == 0)\n+    return # No possible directive, nothing more to do.\n+\n+  # Directives are case-insensitive.\n+  rx = \"[ \\t]*#[ \\t]*([tT][oO][dD][oO]|[sS][kK][iI][pP])[ \\t]*\"\n+\n+  # See whether we have the directive, and if yes, where.\n+  pos = match(line, rx \"$\")\n+  if (!pos)\n+    pos = match(line, rx \"[^a-zA-Z0-9_]\")\n+\n+  # If there was no TAP directive, we have nothing more to do.\n+  if (!pos)\n+    return\n+\n+  # Let`s now see if the TAP directive has been escaped.  For example:\n+  #  escaped:     ok \\# SKIP\n+  #  not escaped: ok \\\\# SKIP\n+  #  escaped:     ok \\\\\\\\\\# SKIP\n+  #  not escaped: ok \\ # SKIP\n+  if (substr(line, pos, 1) == \"#\")\n+    {\n+      bslash_count = 0\n+      for (i = pos; i > 1 && substr(line, i - 1, 1) == \"\\\\\"; i--)\n+        bslash_count += 1\n+      if (bslash_count % 2)\n+        return # Directive was escaped.\n+    }\n+\n+  # Strip the directive and its explanation (if any) from the test\n+  # description.\n+  result_obj[\"description\"] = substr(line, 1, pos - 1)\n+  # Now remove the test description from the line, that has been dealt\n+  # with already.\n+  line = substr(line, pos)\n+  # Strip the directive, and save its value (normalized to upper case).\n+  sub(\"^[ \\t]*#[ \\t]*\", \"\", line)\n+  result_obj[\"directive\"] = toupper(substr(line, 1, 4))\n+  line = substr(line, 5)\n+  # Now get the explanation for the directive (if any), with leading\n+  # and trailing whitespace removed.\n+  sub(\"^[ \\t]*\", \"\", line)\n+  sub(\"[ \\t]*$\", \"\", line)\n+  result_obj[\"explanation\"] = line\n+}\n+\n+function get_test_exit_message(status)\n+{\n+  if (status == 0)\n+    return \"\"\n+  if (status !~ /^[1-9][0-9]*$/)\n+    abort(\"getting exit status\")\n+  if (status < 127)\n+    exit_details = \"\"\n+  else if (status == 127)\n+    exit_details = \" (command not found?)\"\n+  else if (status >= 128 && status <= 255)\n+    exit_details = sprintf(\" (terminated by signal %d?)\", status - 128)\n+  else if (status > 256 && status <= 384)\n+    # We used to report an \"abnormal termination\" here, but some Korn\n+    # shells, when a child process die due to signal number n, can leave\n+    # in $? an exit status of 256+n instead of the more standard 128+n.\n+    # Apparently, both behaviours are allowed by POSIX (2008), so be\n+    # prepared to handle them both.  See also Austing Group report ID\n+    # 0000051 <http://www.austingroupbugs.net/view.php?id=51>\n+    exit_details = sprintf(\" (terminated by signal %d?)\", status - 256)\n+  else\n+    # Never seen in practice.\n+    exit_details = \" (abnormal termination)\"\n+  return sprintf(\"exited with status %d%s\", status, exit_details)\n+}\n+\n+function write_test_results()\n+{\n+  print \":global-test-result: \" get_global_test_result() > trs_file\n+  print \":recheck: \"  yn(must_recheck()) > trs_file\n+  print \":copy-in-global-log: \" yn(copy_in_global_log()) > trs_file\n+  for (i = 0; i < test_results_index; i += 1)\n+    print \":test-result: \" test_results_list[i] > trs_file\n+  close(trs_file);\n+}\n+\n+BEGIN {\n+\n+## ------- ##\n+##  SETUP  ##\n+## ------- ##\n+\n+\'\"$init_colors\"\'\n+\n+# Properly initialized once the TAP plan is seen.\n+planned_tests = 0\n+\n+COOKED_PASS = expect_failure ? \"XPASS\": \"PASS\";\n+COOKED_FAIL = expect_failure ? \"XFAIL\": \"FAIL\";\n+\n+# Enumeration-like constants to remember which kind of plan (if any)\n+# has been seen.  It is important that NO_PLAN evaluates \"false\" as\n+# a boolean.\n+NO_PLAN = 0\n+EARLY_PLAN = 1\n+LATE_PLAN = 2\n+\n+testno = 0     # Number of test results seen so far.\n+bailed_out = 0 # Whether a \"Bail out!\" directive has been seen.\n+\n+# Whether the TAP plan has been seen or not, and if yes, which kind\n+# it is (\"early\" is seen before any test result, \"late\" otherwise).\n+plan_seen = NO_PLAN\n+\n+## --------- ##\n+##  PARSING  ##\n+## --------- ##\n+\n+is_first_read = 1\n+\n+while (1)\n+  {\n+    # Involutions required so that we are able to read the exit status\n+    # from the last input line.\n+    st = getline\n+    if (st < 0) # I/O error.\n+      fatal(\"I/O error while reading from input stream\")\n+    else if (st == 0) # End-of-input\n+      {\n+        if (is_first_read)\n+          abort(\"in input loop: only one input line\")\n+        break\n+      }\n+    if (is_first_read)\n+      {\n+        is_first_read = 0\n+        nextline = $0\n+        continue\n+      }\n+    else\n+      {\n+        curline = nextline\n+        nextline = $0\n+        $0 = curline\n+      }\n+    # Copy any input line verbatim into the log file.\n+    print | \"cat >&3\"\n+    # Parsing of TAP input should stop after a \"Bail out!\" directive.\n+    if (bailed_out)\n+      continue\n+\n+    # TAP test result.\n+    if ($0 ~ /^(not )?ok$/ || $0 ~ /^(not )?ok[^a-zA-Z0-9_]/)\n+      {\n+        testno += 1\n+        setup_result_obj($0)\n+        handle_tap_result()\n+      }\n+    # TAP plan (normal or \"SKIP\" without explanation).\n+    else if ($0 ~ /^1\\.\\.[0-9]+[ \\t]*$/)\n+      {\n+        # The next two lines will put the number of planned tests in $0.\n+        sub(\"^1\\\\.\\\\.\", \"\")\n+        sub(\"[^0-9]*$\", \"\")\n+        handle_tap_plan($0, \"\")\n+        continue\n+      }\n+    # TAP \"SKIP\" plan, with an explanation.\n+    else if ($0 ~ /^1\\.\\.0+[ \\t]*#/)\n+      {\n+        # The next lines will put the skip explanation in $0, stripping\n+        # any leading and trailing whitespace.  This is a little more\n+        # tricky in truth, since we want to also strip a potential leading\n+        # \"SKIP\" string from the message.\n+        sub(\"^[^#]*#[ \\t]*(SKIP[: \\t][ \\t]*)?\", \"\")\n+        sub(\"[ \\t]*$\", \"\");\n+        handle_tap_plan(0, $0)\n+      }\n+    # \"Bail out!\" magic.\n+    # Older versions of prove and TAP::Harness (e.g., 3.17) did not\n+    # recognize a \"Bail out!\" directive when preceded by leading\n+    # whitespace, but more modern versions (e.g., 3.23) do.  So we\n+    # emulate the latter, \"more modern\" behaviour.\n+    else if ($0 ~ /^[ \\t]*Bail out!/)\n+      {\n+        bailed_out = 1\n+        # Get the bailout message (if any), with leading and trailing\n+        # whitespace stripped.  The message remains stored in `$0`.\n+        sub(\"^[ \\t]*Bail out![ \\t]*\", \"\");\n+        sub(\"[ \\t]*$\", \"\");\n+        # Format the error message for the\n+        bailout_message = \"Bail out!\"\n+        if (length($0))\n+          bailout_message = bailout_message \" \" $0\n+        testsuite_error(bailout_message)\n+      }\n+    # Maybe we have too look for dianogtic comments too.\n+    else if (comments != 0)\n+      {\n+        comment = extract_tap_comment($0);\n+        if (length(comment))\n+          report(\"#\", comment);\n+      }\n+  }\n+\n+## -------- ##\n+##  FINISH  ##\n+## -------- ##\n+\n+# A \"Bail out!\" directive should cause us to ignore any following TAP\n+# error, as well as a non-zero exit status from the TAP producer.\n+if (!bailed_out)\n+  {\n+    if (!plan_seen)\n+      {\n+        testsuite_error(\"missing test plan\")\n+      }\n+    else if (planned_tests != testno)\n+      {\n+        bad_amount = testno > planned_tests ? \"many\" : \"few\"\n+        testsuite_error(sprintf(\"too %s tests run (expected %d, got %d)\",\n+                                bad_amount, planned_tests, testno))\n+      }\n+    if (!ignore_exit)\n+      {\n+        # Fetch exit status from the last line.\n+        exit_message = get_test_exit_message(nextline)\n+        if (exit_message)\n+          testsuite_error(exit_message)\n+      }\n+  }\n+\n+write_test_results()\n+\n+exit 0\n+\n+} # End of \"BEGIN\" block.\n+\'\n+\n+# TODO: document that we consume the file descriptor 3 :-(\n+} 3>\"$log_file\"\n+\n+test $? -eq 0 || fatal \"I/O or internal error\"\n+\n+# Local Variables:\n+# mode: shell-script\n+# sh-indentation: 2\n+# eval: (add-hook \'write-file-hooks \'time-stamp)\n+# time-stamp-start: \"scriptversion=\"\n+# time-stamp-format: \"%:y-%02m-%02d.%02H\"\n+# time-stamp-time-zone: \"UTC\"\n+# time-stamp-end: \"; # UTC\"\n+# End:\n','false','false','none'),('irssi','1.0.5','c20eddeb385974d6f46494e71ab63d4493df119b','ailin-nemui','add glib testing tap utilities','tap-test','2','0','5','0','ADD','@@ -0,0 +1,5 @@\n+#! /bin/sh\n+\n+# run a GTest in tap mode. The test binary is passed as $1\n+\n+$1 -k --tap\n','false','false','none'),('irssi','1.0.5','43d06369bf8e5c5e470dc2b00656ddcdb9ee0480','ailin-nemui','move fixtures to outer scope','Makefile.am','22','0','4','12','MODIFY','@@ -1,5 +1,7 @@\n include $(top_srcdir)/utils/glib-tap.mk\n \n+PACKAGE_STRING=irc/core\n+\n AM_CPPFLAGS = \\\n 	-I$(top_srcdir)/src \\\n 	-I$(top_srcdir)/src/core \\\n@@ -17,21 +19,11 @@ test_irc_DEPENDENCIES = \\\n 	../../../src/lib-config/libirssi_config.a\n \n test_irc_LDADD = \\\n+	../../../src/irc/core/libirc_core.a \\\n 	../../../src/core/libcore.a \\\n 	../../../src/lib-config/libirssi_config.a \\\n 	@GLIB_LIBS@ \\\n 	@OPENSSL_LIBS@\n \n test_irc_SOURCES = \\\n-	test-irc.c \\\n-	../../../src/irc/core/irc-cap.c \\\n-	../../../src/irc/core/irc-nicklist.c \\\n-	../../../src/irc/core/irc-queries.c \\\n-	../../../src/irc/core/irc-servers-reconnect.c \\\n-	../../../src/irc/core/irc-servers-setup.c \\\n-	../../../src/irc/core/irc-servers.c \\\n-	../../../src/irc/core/irc.c \\\n-	../../../src/irc/core/mode-lists.c \\\n-	../../../src/irc/core/modes.c \\\n-	../../../src/irc/core/servers-idle.c \\\n-	../../../src/irc/core/servers-redirect.c\n+	test-irc.c\n','false','false','none'),('irssi','1.0.5','43d06369bf8e5c5e470dc2b00656ddcdb9ee0480','ailin-nemui','move fixtures to outer scope','test-irc.c','191','5','190','190','MODIFY','@@ -22,210 +22,210 @@\n #include <irc.h>\n #include <string.h>\n \n-static void test_event_get_param(void);\n-static void test_event_get_params(void);\n+typedef struct {\n+	char const *const description;\n+	char const *const input;\n+	char const *const input_after;\n+	char const *const output;\n+} event_get_param_test_case;\n+\n+event_get_param_test_case const event_get_param_fixtures[] = {\n+	{\n+		.description = \"Zero parameters\",\n+		.input       = \"\",\n+		.input_after = \"\",\n+		.output      = \"\",\n+	},\n+	{\n+		.description = \"One parameter\",\n+		.input       = \"#test\",\n+		.input_after = \"\",\n+		.output      = \"#test\",\n+	},\n+	{\n+		.description = \"One parameter, trailing space\",\n+		.input       = \"#test \",\n+		.input_after = \"\",\n+		.output      = \"#test\",\n+	},\n+	{\n+		.description = \"One parameter, more trailing space\",\n+		.input       = \"#test  \",\n+		.input_after = \" \",\n+		.output      = \"#test\",\n+	},\n+	{\n+		.description = \"Two parameters\",\n+		.input       = \"#test +o\",\n+		.input_after = \"+o\",\n+		.output      = \"#test\",\n+	},\n+	{\n+		.description = \"Two parameters continued\",\n+		.input       = \"+o\",\n+		.input_after = \"\",\n+		.output      = \"+o\",\n+	},\n+	{\n+		.description = \"Two parameters with trailing space\",\n+		.input       = \"#test +o \",\n+		.input_after = \"+o \",\n+		.output      = \"#test\",\n+	},\n+	{\n+		.description = \"Two parameters with trailing space continued\",\n+		.input       = \"+o \",\n+		.input_after = \"\",\n+		.output      = \"+o\",\n+	},\n+	{\n+		.description = \"Two parameters with inline and trailing space\",\n+		.input       = \"#test  +o \",\n+		.input_after = \" +o \",\n+		.output      = \"#test\",\n+	},\n+	/* TODO: It seems not ideal that the caller has to deal with inline space.\n+	 */\n+	{\n+		.description = \"Two parameters with inline and trailing space continued\",\n+		.input       = \" +o \",\n+		.input_after = \"+o \",\n+		.output      = \"\",\n+	},\n+};\n+\n+static void test_event_get_param(const event_get_param_test_case *test);\n+\n+typedef struct {\n+	char const *const description;\n+	char const *const input;\n+	char const *const output0;\n+	char const *const output1;\n+} event_get_params_test_case;\n+\n+event_get_params_test_case const event_get_params_fixtures[] = {\n+	{\n+		.description = \"Only a channel\",\n+		.input       = \"#test\",\n+		.output0     = \"#test\",\n+		.output1     = \"\",\n+	},\n+	{\n+		.description = \"Only a channel with trailing space\",\n+		.input       = \"#test \",\n+		.output0     = \"#test\",\n+		.output1     = \"\",\n+	},\n+	{\n+		.description = \"No :<trailing>, channel mode with one parameter after channel name\",\n+		.input       = \"#test +i\",\n+		.output0     = \"#test\",\n+		.output1     = \"+i\",\n+	},\n+	{\n+		.description = \"No :<trailing>, channel mode with two parameters after channel name\",\n+		.input       = \"#test +o tester\",\n+		.output0     = \"#test\",\n+		.output1     = \"+o tester\",\n+	},\n+	{\n+		.description = \"No :<trailing>, channel mode with three parameters afer channel name\",\n+		.input       = \"#test +ov tester tester2\",\n+		.output0     = \"#test\",\n+		.output1     = \"+ov tester tester2\",\n+	},\n+	{\n+		.description = \"No :<trailing>, channel mode with three parameters afer channel name, bunch of extra space\",\n+		.input       = \"#test  +ov  tester  tester2 \",\n+		.output0     = \"#test\",\n+		.output1     = \" +ov  tester  tester2 \",\n+	},\n+	{\n+		.description = \"Channel mode with one parameter after channel name, :<trailing> at the start of modes\",\n+		.input       = \"#test :+i\",\n+		.output0     = \"#test\",\n+		.output1     = \"+i\",\n+	},\n+	{\n+		.description = \"Channel mode with two parameters after channel name, :<trailing> at the  start of modes\",\n+		.input       = \"#test :+o tester\",\n+		.output0     = \"#test\",\n+		.output1     = \"+o tester\",\n+	},\n+	{\n+		.description = \"Channel mode with three parameters after channel name, :<trailing> at the start of modes\",\n+		.input       = \"#test :+ov tester tester2\",\n+		.output0     = \"#test\",\n+		.output1     = \"+ov tester tester2\",\n+	},\n+	{\n+		.description = \"Channel mode with two parameters after channel name, :<trailing> on the final parameter\",\n+		.input       = \"#test +o :tester\",\n+		.output0     = \"#test\",\n+		.output1     = \"+o tester\",\n+	},\n+	{\n+		.description = \"Channel mode with three parameters after channel name, :<trailing> on the final parameter\",\n+		.input       = \"#test +ov tester :tester2\",\n+		.output0     = \"#test\",\n+		.output1     = \"+ov tester tester2\",\n+	},\n+	{\n+		.description = \"Channel mode with three parameters after channel name, :<trailing> on the final parameter, also a second : present\",\n+		.input       = \"#test +ov tester :tester2 hi:there\",\n+		.output0     = \"#test\",\n+		.output1     = \"+ov tester tester2 hi:there\",\n+	},\n+};\n+\n+static void test_event_get_params(const event_get_params_test_case *test);\n \n int main(int argc, char **argv)\n {\n+	int i;\n+\n 	g_test_init(&argc, &argv, NULL);\n \n-	g_test_add_func(\"/test/event_get_param\", test_event_get_param);\n-	g_test_add_func(\"/test/event_get_params\", test_event_get