n 			return TRUE;\n 		}\n 	}\n','false','true','none'),('irssi','1.0.6','121a4971e61176e326ee3d2f71867e87732f4519','ailin-nemui','update fe-common-core.c\n\nfix mixed decls','fe-common-core.c','394','62','6','3','MODIFY','@@ -461,16 +461,19 @@ void fe_common_core_finish_init(void)\n \n gboolean strarray_find_dest(char **array, const TEXT_DEST_REC *dest)\n {\n-	int channel_type = module_get_uniq_id_str(\"WINDOW ITEM TYPE\", \"CHANNEL\");\n-	int query_type = module_get_uniq_id_str(\"WINDOW ITEM TYPE\", \"QUERY\");\n+	WI_ITEM_REC *item;\n+	int server_tag_len, channel_type, query_type;\n 	char **tmp;\n \n+	channel_type = module_get_uniq_id_str(\"WINDOW ITEM TYPE\", \"CHANNEL\");\n+	query_type = module_get_uniq_id_str(\"WINDOW ITEM TYPE\", \"QUERY\");\n+\n 	g_return_val_if_fail(array != NULL, FALSE);\n 	g_return_val_if_fail(dest != NULL, FALSE);\n 	g_return_val_if_fail(dest->window != NULL, FALSE);\n 	g_return_val_if_fail(dest->target != NULL, FALSE);\n \n-	WI_ITEM_REC *item = window_item_find_window(dest->window, dest->server, dest->target);\n+	item = window_item_find_window(dest->window, dest->server, dest->target);\n 	if (item == NULL) {\n 		return FALSE;\n 	}\n','false','false','none'),('irssi','1.0.6','0aafd011c0adc58ddf43783764b96fdd74578243','dequis','add a startup warning if the term var is wrong inside tmux/screen\n\none of the most common and confusing issues we get in #irssi,\nthis should help identifying and mitigating it.','irssi.c','225','30','2','0','MODIFY','@@ -207,6 +207,8 @@ static void textui_finish_init(void)\n 		fe_settings_set_print(\"nick\");\n 	if (user_settings_changed & USER_SETTINGS_HOSTNAME)\n 		fe_settings_set_print(\"hostname\");\n+\n+	term_environment_check();\n }\n \n static void textui_deinit(void)\n','false','false','none'),('irssi','1.0.6','0aafd011c0adc58ddf43783764b96fdd74578243','dequis','add a startup warning if the term var is wrong inside tmux/screen\n\none of the most common and confusing issues we get in #irssi,\nthis should help identifying and mitigating it.','term-terminfo.c','553','177','28','0','MODIFY','@@ -733,3 +733,31 @@ void term_gets(GArray *buffer, int *line_count)\n 		}\n 	}\n }\n+\n+static const char* term_env_warning =\n+	\"The TERM environment variable is set to \'%s\' which can cause display \"\n+	\"glitches when running under %s.\\n\"\n+	\"Consider changing TERM to \'screen\' or \'screen-256color\' instead.\";\n+\n+void term_environment_check(void)\n+{\n+	const char *term, *sty, *tmux, *multiplexer;\n+\n+	term = g_getenv(\"TERM\");\n+	sty = g_getenv(\"STY\");\n+	tmux = g_getenv(\"TMUX\");\n+\n+	multiplexer = (sty && *sty) ? \"screen\" :\n+	              (tmux && *tmux) ? \"tmux\" : NULL;\n+\n+	if (!multiplexer) {\n+		return;\n+	}\n+\n+	if (term && (g_str_has_prefix(term, \"screen\") ||\n+	             g_str_has_prefix(term, \"tmux\"))) {\n+		return;\n+	}\n+\n+	g_warning(term_env_warning, term, multiplexer);\n+}\n','false','false','none'),('irssi','1.0.6','0aafd011c0adc58ddf43783764b96fdd74578243','dequis','add a startup warning if the term var is wrong inside tmux/screen\n\none of the most common and confusing issues we get in #irssi,\nthis should help identifying and mitigating it.','term.h','42','0','2','0','MODIFY','@@ -105,4 +105,6 @@ void term_gets(GArray *buffer, int *line_count);\n void term_common_init(void);\n void term_common_deinit(void);\n \n+void term_environment_check(void);\n+\n #endif\n','false','false','none'),('irssi','1.0.6','f9c8365999c5319be6970dcf27f21190b0481314','dequis','fe-common-core: fix redeclaration of server_tag_len','fe-common-core.c','394','62','1','1','MODIFY','@@ -478,7 +478,7 @@ gboolean strarray_find_dest(char **array, const TEXT_DEST_REC *dest)\n 		return FALSE;\n 	}\n \n-	int server_tag_len = dest->server_tag != NULL ? strlen(dest->server_tag) : 0;\n+	server_tag_len = dest->server_tag != NULL ? strlen(dest->server_tag) : 0;\n 	for (tmp = array; *tmp != NULL; tmp++) {\n 		char *str = *tmp;\n 		if (*str == \'\\0\') {\n','true','false','none'),('irssi','1.0.6','0d5e353ef1be253106ed66417b270d03b2b91ed3','ailin-nemui','reword warning message\n\ninclude multiplexer name in term recommendation','term-terminfo.c','553','177','3','3','MODIFY','@@ -735,9 +735,9 @@ void term_gets(GArray *buffer, int *line_count)\n }\n \n static const char* term_env_warning =\n-	\"The TERM environment variable is set to \'%s\' which can cause display \"\n-	\"glitches when running under %s.\\n\"\n-	\"Consider changing TERM to \'screen\' or \'screen-256color\' instead.\";\n+	\"You seem to be running Irssi inside %2$s, but the TERM environment variable\"\n+	\"is set to \'%1$s\', which can cause display glitches.\\n\"\n+	\"Consider changing TERM to \'%2$s\' or \'%2$s-256color\' instead.\";\n \n void term_environment_check(void)\n {\n','false','true','none'),('irssi','1.0.6','98836f8b7ee058c32d54562903676c4d16f83aa4','LemonBoy','parse the k/v form in cap ls\n\nthis is a prerequisite for the irc v3.2 compliance.','irc-cap.c','148','40','39','4','MODIFY','@@ -45,7 +45,7 @@ int cap_toggle (IRC_SERVER_REC *server, char *cap, int enable)\n \n 	if (enable && !gslist_find_string(server->cap_active, cap)) {\n 		/* Make sure the required cap is supported by the server */\n-		if (!gslist_find_string(server->cap_supported, cap))\n+		if (!g_hash_table_lookup_extended(server->cap_supported, cap, NULL, NULL))\n 			return FALSE;\n \n 		irc_send_cmdv(server, \"CAP REQ %s\", cap);\n@@ -96,9 +96,44 @@ static void event_cap (IRC_SERVER_REC *server, char *args, char *nick, char *add\n 	caps_length = g_strv_length(caps);\n \n 	if (!g_strcmp0(evt, \"LS\")) {\n+		if (server->cap_supported) {\n+			g_hash_table_destroy(server->cap_supported);\n+		}\n+		/* Start with a fresh table */\n+		server->cap_supported = g_hash_table_new_full(g_str_hash,\n+							      g_str_equal,\n+							      g_free, g_free);\n+\n 		/* Create a list of the supported caps */\n-		for (i = 0; i < caps_length; i++)\n-			server->cap_supported = g_slist_prepend(server->cap_supported, g_strdup(caps[i]));\n+		for (i = 0; i < caps_length; i++) {\n+			const char *name = caps[i];\n+			const char *eq = strchr(name, \'=\');\n+			int fresh = TRUE;\n+\n+			if (!eq) {\n+				fresh = g_hash_table_insert(server->cap_supported,\n+							    g_strdup(name),\n+							    NULL);\n+			}\n+			/* Some values are in a KEY=VALUE form, parse them */\n+			else if (eq[1] != \'\\0\') {\n+				char *key = g_strndup(name, (int)(eq - name));\n+				char *val = g_strdup(eq + 1);\n+				fresh = g_hash_table_insert(server->cap_supported,\n+							    key, val);\n+			}\n+			/* If the string ends after the \'=\' consider the value\n+			 * as invalid */\n+			else {\n+				g_warning(\"Invalid CAP key/value pair\");\n+			}\n+\n+			/* The specification doesn\'t say anything about\n+			 * duplicated values, let\'s just warn the user */\n+			if (fresh == FALSE) {\n+				g_warning(\"Duplicate value\");\n+			}\n+		}\n \n 		/* Request the required caps, if any */\n 		if (server->cap_queue == NULL) {\n@@ -111,7 +146,7 @@ static void event_cap (IRC_SERVER_REC *server, char *args, char *nick, char *add\n \n 			/* Check whether the cap is supported by the server */\n 			for (tmp = server->cap_queue; tmp != NULL; tmp = tmp->next) {\n-				if (gslist_find_string(server->cap_supported, tmp->data)) {\n+				if (g_hash_table_lookup_extended(server->cap_supported, tmp->data, NULL, NULL)) {\n 					if (avail_caps > 0)\n 						g_string_append_c(cmd, \' \');\n 					g_string_append(cmd, tmp->data);\n','false','false','none'),('irssi','1.0.6','98836f8b7ee058c32d54562903676c4d16f83aa4','LemonBoy','parse the k/v form in cap ls\n\nthis is a prerequisite for the irc v3.2 compliance.','irc-servers.c','780','195','1','1','MODIFY','@@ -443,7 +443,7 @@ static void sig_disconnected(IRC_SERVER_REC *server)\n 	gslist_free_full(server->cap_active, (GDestroyNotify) g_free);\n 	server->cap_active = NULL;\n \n-	gslist_free_full(server->cap_supported, (GDestroyNotify) g_free);\n+	g_hash_table_destroy(server->cap_supported);\n 	server->cap_supported = NULL;\n \n 	gslist_free_full(server->cap_queue, (GDestroyNotify) g_free);\n','false','false','none'),('irssi','1.0.6','98836f8b7ee058c32d54562903676c4d16f83aa4','LemonBoy','parse the k/v form in cap ls\n\nthis is a prerequisite for the irc v3.2 compliance.','irc-servers.h','81','0','1','1','MODIFY','@@ -75,7 +75,7 @@ struct _IRC_SERVER_REC {\n 	int max_whois_in_cmd; /* max. number of nicks in one /WHOIS command */\n 	int max_msgs_in_cmd; /* max. number of targets in one /MSG */\n \n-	GSList *cap_supported; /* A list of caps supported by the server */\n+	GHashTable *cap_supported; /* A list of caps supported by the server */\n 	GSList *cap_active;    /* A list of caps active for this session */\n 	GSList *cap_queue;     /* A list of caps to request on connection */ \n \n','false','false','none'),('irssi','1.0.6','98836f8b7ee058c32d54562903676c4d16f83aa4','LemonBoy','parse the k/v form in cap ls\n\nthis is a prerequisite for the irc v3.2 compliance.','Irc.xs','208','18','11','4','MODIFY','@@ -12,7 +12,10 @@ static void perl_irc_connect_fill_hash(HV *hv, IRC_SERVER_CONNECT_REC *conn)\n static void perl_irc_server_fill_hash(HV *hv, IRC_SERVER_REC *server)\n {\n 	AV *av;\n+	HV *hv_;\n 	GSList *tmp;\n+	GHashTableIter iter;\n+	gpointer key_, val_;\n \n 	perl_irc_connect_fill_hash(hv, server->connrec);\n 	perl_server_fill_hash(hv, (SERVER_REC *) server);\n@@ -34,10 +37,14 @@ static void perl_irc_server_fill_hash(HV *hv, IRC_SERVER_REC *server)\n 	(void) hv_store(hv, \"cap_complete\", 12, newSViv(server->cap_complete), 0);\n 	(void) hv_store(hv, \"sasl_success\", 12, newSViv(server->sasl_success), 0);\n \n-	av = newAV();\n-	for (tmp = server->cap_supported; tmp != NULL; tmp = tmp->next)\n-		av_push(av, new_pv(tmp->data));\n-	(void) hv_store(hv, \"cap_supported\", 13, newRV_noinc((SV*)av), 0);\n+	hv_ = newHV();\n+	g_hash_table_iter_init(&iter, server->cap_supported);\n+	while (g_hash_table_iter_next(&iter, &key_, &val_)) {\n+		char *key = (char *)key_;\n+		char *val = (char *)val_;\n+		hv_store(hv_, key, strlen(key), new_pv(val), 0);\n+	}\n+	(void) hv_store(hv, \"cap_supported\", 13, newRV_noinc((SV*)hv_), 0);\n \n 	av = newAV();\n 	for (tmp = server->cap_active; tmp != NULL; tmp = tmp->next)\n','false','false','none'),('irssi','1.0.6','d21706e1cc78284d5e7b2d69ebe4873e459d0e9b','LemonBoy','factor out the parsing function\n\nthis is also needed for cap new and cap del.','irc-cap.c','159','42','36','22','MODIFY','@@ -79,6 +79,33 @@ static void cap_emit_signal (IRC_SERVER_REC *server, char *cmd, char *args)\n 	g_free(signal_name);\n }\n \n+static gboolean parse_cap_name(char *name, char **key, char **val)\n+{\n+	g_return_val_if_fail(name != NULL, FALSE);\n+	g_return_val_if_fail(name[0] != \'\\0\', FALSE);\n+\n+	const char *eq = strchr(name, \'=\');\n+	/* KEY only value */\n+	if (!eq) {\n+		*key = g_strdup(name);\n+		*val = NULL;\n+		return TRUE;\n+	}\n+	/* Some values are in a KEY=VALUE form, parse them */\n+	else if (eq[1] != \'\\0\') {\n+		*key = g_strndup(name, (int)(eq - name));\n+		*val = g_strdup(eq + 1);\n+		return TRUE;\n+	}\n+	/* If the string ends after the \'=\' consider the value\n+	 * as invalid */\n+	else {\n+		*key = NULL;\n+		*val = NULL;\n+		return FALSE;\n+	}\n+}\n+\n static void event_cap (IRC_SERVER_REC *server, char *args, char *nick, char *address)\n {\n 	GSList *tmp;\n@@ -106,33 +133,20 @@ static void event_cap (IRC_SERVER_REC *server, char *args, char *nick, char *add\n \n 		/* Create a list of the supported caps */\n 		for (i = 0; i < caps_length; i++) {\n-			const char *name = caps[i];\n-			const char *eq = strchr(name, \'=\');\n-			int fresh = TRUE;\n-\n-			if (!eq) {\n-				fresh = g_hash_table_insert(server->cap_supported,\n-							    g_strdup(name),\n-							    NULL);\n-			}\n-			/* Some values are in a KEY=VALUE form, parse them */\n-			else if (eq[1] != \'\\0\') {\n-				char *key = g_strndup(name, (int)(eq - name));\n-				char *val = g_strdup(eq + 1);\n-				fresh = g_hash_table_insert(server->cap_supported,\n-							    key, val);\n+			char *key, *val;\n+\n+			if (parse_cap_name(caps[i], &key, &val)) {\n+				if (!g_hash_table_insert(server->cap_supported,\n+							 key, val)) {\n+					/* The specification doesn\'t say anything about\n+					 * duplicated values, let\'s just warn the user */\n+					g_warning(\"Duplicate value\");\n+				}\n 			}\n-			/* If the string ends after the \'=\' consider the value\n-			 * as invalid */\n 			else {\n 				g_warning(\"Invalid CAP key/value pair\");\n 			}\n \n-			/* The specification doesn\'t say anything about\n-			 * duplicated values, let\'s just warn the user */\n-			if (fresh == FALSE) {\n-				g_warning(\"Duplicate value\");\n-			}\n 		}\n \n 		/* Request the required caps, if any */\n','false','true','none'),('irssi','1.0.6','57827ca743e95a6b6ad830fab03f46efaf1d9069','LemonBoy','don\'t free the hash table if there\'s none\n\nglib doesn\'t like that and shows a harmless warning.','irc-servers.c','782','196','4','2','MODIFY','@@ -443,8 +443,10 @@ static void sig_disconnected(IRC_SERVER_REC *server)\n 	gslist_free_full(server->cap_active, (GDestroyNotify) g_free);\n 	server->cap_active = NULL;\n \n-	g_hash_table_destroy(server->cap_supported);\n-	server->cap_supported = NULL;\n+	if (server->cap_supported) {\n+	    g_hash_table_destroy(server->cap_supported);\n+	    server->cap_supported = NULL;\n+	}\n \n 	gslist_free_full(server->cap_queue, (GDestroyNotify) g_free);\n 	server->cap_queue = NULL;\n','false','false','none'),('irssi','1.0.6','8c87766132b7dbb75d8a49548ff7c97037ea983b','LemonBoy','parse multiline responses to cap ls\n\nthe parsing logic isn\'t too elegant because of the optional parameter\nused for signaling if a response has a continuation one.','irc-cap.c','176','46','52','27','MODIFY','@@ -110,13 +110,31 @@ static void event_cap (IRC_SERVER_REC *server, char *args, char *nick, char *add\n {\n 	GSList *tmp;\n 	GString *cmd;\n-	char *params, *evt, *list, **caps;\n-	int i, caps_length, disable, avail_caps;\n+	char *params, *evt, *list, *star, **caps;\n+	int i, caps_length, disable, avail_caps, multiline;\n \n-	params = event_get_params(args, 3, NULL, &evt, &list);\n+	params = event_get_params(args, 4, NULL, &evt, &star, &list);\n 	if (params == NULL)\n 		return;\n \n+	/* Multiline responses have an additional parameter and we have to do\n+	 * this stupid dance to parse them */\n+	if (evt[0] == \'L\' && !strcmp(star, \"*\")) {\n+		multiline = TRUE;\n+	}\n+	/* This branch covers the \'*\' parameter isn\'t present, adjust the\n+	 * parameter pointer to compensate for this */\n+	else if (list[0] == \'\\0\') {\n+		multiline = FALSE;\n+		list = star;\n+	}\n+	/* Malformed request, terminate the negotiation */\n+	else {\n+		cap_finish_negotiation(server);\n+		g_warn_if_reached();\n+		return;\n+	}\n+\n 	/* Strip the trailing whitespaces before splitting the string, some servers send responses with\n 	 * superfluous whitespaces that g_strsplit the interprets as tokens */\n 	caps = g_strsplit(g_strchomp(list), \" \", -1);\n@@ -149,37 +167,41 @@ static void event_cap (IRC_SERVER_REC *server, char *args, char *nick, char *add\n \n 		}\n \n-		/* Request the required caps, if any */\n-		if (server->cap_queue == NULL) {\n-			cap_finish_negotiation(server);\n-		}\n-		else {\n-			cmd = g_string_new(\"CAP REQ :\");\n+		/* A multiline response is always terminated by a normal one,\n+		 * wait until we receive that one to require any CAP */\n+		if (multiline == FALSE) {\n+			/* No CAP has been requested */\n+			if (server->cap_queue == NULL) {\n+				cap_finish_negotiation(server);\n+			}\n+			else {\n+				cmd = g_string_new(\"CAP REQ :\");\n \n-			avail_caps = 0;\n+				avail_caps = 0;\n \n-			/* Check whether the cap is supported by the server */\n-			for (tmp = server->cap_queue; tmp != NULL; tmp = tmp->next) {\n-				if (g_hash_table_lookup_extended(server->cap_supported, tmp->data, NULL, NULL)) {\n-					if (avail_caps > 0)\n-						g_string_append_c(cmd, \' \');\n-				