h','53','0','9','9','MODIFY','@@ -30,24 +30,24 @@ enum {    	TXT_FILL_3,   -        TXT_STATUSBAR_LIST_HEADER, +	TXT_STATUSBAR_LIST_HEADER,  	TXT_STATUSBAR_LIST_FOOTER,  	TXT_STATUSBAR_LIST,  	TXT_STATUSBAR_INFO_NAME,  	TXT_STATUSBAR_INFO_TYPE, -        TXT_STATUSBAR_INFO_PLACEMENT, +	TXT_STATUSBAR_INFO_PLACEMENT,  	TXT_STATUSBAR_INFO_POSITION,  	TXT_STATUSBAR_INFO_VISIBLE, -        TXT_STATUSBAR_INFO_ITEM_HEADER, +	TXT_STATUSBAR_INFO_ITEM_HEADER,  	TXT_STATUSBAR_INFO_ITEM_FOOTER, -        TXT_STATUSBAR_INFO_ITEM_NAME, -        TXT_STATUSBAR_NOT_FOUND, -        TXT_STATUSBAR_NOT_ENABLED, -        TXT_STATUSBAR_ITEM_NOT_FOUND, +	TXT_STATUSBAR_INFO_ITEM_NAME, +	TXT_STATUSBAR_NOT_FOUND, +	TXT_STATUSBAR_NOT_ENABLED, +	TXT_STATUSBAR_ITEM_NOT_FOUND,  	TXT_STATUSBAR_UNKNOWN_COMMAND, -        TXT_STATUSBAR_UNKNOWN_TYPE, +	TXT_STATUSBAR_UNKNOWN_TYPE,  	TXT_STATUSBAR_UNKNOWN_PLACEMENT, -        TXT_STATUSBAR_UNKNOWN_VISIBILITY, +	TXT_STATUSBAR_UNKNOWN_VISIBILITY,    	TXT_FILL_4,   '),('irssi','1.1.1','138eca0749dd05a4a16df9e43d87af2143b224f2','ailin-nemui','fix build','statusbar-config.c','612','152','2','3','MODIFY','@@ -101,7 +101,7 @@ statusbar_config_find(STATUSBAR_GROUP_REC *group, const char *name)  			return config;  	}   -        return NULL; +	return NULL;  }    static void statusbar_reset_defaults(void) @@ -542,7 +542,7 @@ static CONFIG_NODE *config_sbar_items_section(CONFIG_REC *config, CONFIG_NODE *p  static CONFIG_NODE *statusbar_copy_config(CONFIG_REC *config, CONFIG_NODE *source,                                            CONFIG_NODE *parent)  { -        GSList *tmp; +	GSList *tmp;    	g_return_val_if_fail(parent != NULL, NULL);   @@ -671,7 +671,6 @@ static void cmd_statusbar_additem_modifyitem(const char *data, void *server, voi  /* SYNTAX: STATUSBAR REMOVEITEM <item> <statusbar> */  static void cmd_statusbar_removeitem(const char *data, void *server, void *witem)  { -	CONFIG_REC *config, *close_config;  	CONFIG_NODE *node;  	char *item, *statusbar;  	void *free_arg; '),('irssi','1.1.1','25a44dacf4114f33f3a887f358c02f4fd9938427','Dorian Harmans','fix build with LibreSSL 2.7.0','network-openssl.c','684','151','4','2','MODIFY','@@ -35,7 +35,8 @@  #include <openssl/err.h>    /* OpenSSL 1.1.0 introduced some backward-incompatible changes to the api */ -#if (OPENSSL_VERSION_NUMBER >= 0x10100000L) && !defined(LIBRESSL_VERSION_NUMBER) +#if (OPENSSL_VERSION_NUMBER >= 0x10100000L) && \\ +    (!defined(LIBRESSL_VERSION_NUMBER) || LIBRESSL_VERSION_NUMBER < 0x2070000fL)  /* The two functions below could be already defined if OPENSSL_API_COMPAT is   * below the 1.1.0 version so let\'s do a clean start */  #undef  X509_get_notBefore @@ -47,7 +48,8 @@    /* OpenSSL 1.1.0 also introduced some useful additions to the api */  #if (OPENSSL_VERSION_NUMBER >= 0x10002000L) -#if (OPENSSL_VERSION_NUMBER < 0x10100000L) || defined (LIBRESSL_VERSION_NUMBER) +#if (OPENSSL_VERSION_NUMBER < 0x10100000L) || \\ +    (defined (LIBRESSL_VERSION_NUMBER) && LIBRESSL_VERSION_NUMBER < 0x2070000fL)  static int X509_STORE_up_ref(X509_STORE *vfy)  {      int n; '),('irssi','1.1.1','b52cf1015eaa4d759c9377df35a56988655dce89','dequis','otr: add KEY_GEN_STARTED state to avoid starting it twice  Fun race condition there, got this when testing with the jabber xmlconsole open in bitlbee','key.c','255','27','5','3','MODIFY','@@ -43,9 +43,10 @@   */  enum key_gen_status {  	KEY_GEN_IDLE		= 0, -	KEY_GEN_RUNNING		= 1, -	KEY_GEN_FINISHED	= 2, -	KEY_GEN_ERROR		= 3, +	KEY_GEN_STARTED		= 1, +	KEY_GEN_RUNNING		= 2, +	KEY_GEN_FINISHED	= 3, +	KEY_GEN_ERROR		= 4,  };    /* @@ -208,6 +209,7 @@ void key_gen_run(struct otr_user_state *ustate, const char *account_name)  	/* Make sure the pointer does not go away during the proess. */  	key_gen_state.account_name = strdup(account_name);  	key_gen_state.ustate = ustate; +	key_gen_state.status = KEY_GEN_STARTED;    	/* Creating key file path. */  	key_gen_state.key_file_path = file_path_build(OTR_KEYFILE); '),('irssi','1.1.1','dd7dc7ca7d0b04ae2d522a209845c453bfd9aab5','dequis','otr: fix missing \'target\' param in \'message private\' signal  Fixes warnings like these on \'/msg yourself\'  -!- Irssi: critical privmsg_get_query: assertion \'nick != NULL\' failed -!- Irssi: critical query_find: assertion \'nick != NULL\' failed','module.c','149','29','3','3','MODIFY','@@ -79,7 +79,7 @@ static void sig_server_sendmsg(SERVER_REC *server, const char *target,  /*   * Pipes all incoming private messages through OTR   */ -void sig_message_private(SERVER_REC *server, const char *msg, const char *nick, const char *address) +void sig_message_private(SERVER_REC *server, const char *msg, const char *nick, const char *address, const char *target)  {  	char *new_msg = NULL;   @@ -91,7 +91,7 @@ void sig_message_private(SERVER_REC *server, const char *msg, const char *nick,    	if (new_msg == NULL) {  		/* This message was not OTR */ -		signal_continue(4, server, msg, nick, address); +		signal_continue(5, server, msg, nick, address, target);  	} else {  		/*  		 * Check for /me IRC marker and if so, handle it so the user does not @@ -103,7 +103,7 @@ void sig_message_private(SERVER_REC *server, const char *msg, const char *nick,  			signal_emit(\"message irc action\", 5, server, new_msg + OTR_IRC_MARKER_ME_LEN, nick, address, nick);  		} else {  			/* OTR received message */ -			signal_continue(4, server, new_msg, nick, address); +			signal_continue(5, server, new_msg, nick, address, target);  		}  	}   '),('irssi','1.1.1','dd7dc7ca7d0b04ae2d522a209845c453bfd9aab5','dequis','otr: fix missing \'target\' param in \'message private\' signal  Fixes warnings like these on \'/msg yourself\'  -!- Irssi: critical privmsg_get_query: assertion \'nick != NULL\' failed -!- Irssi: critical query_find: assertion \'nick != NULL\' failed','module.h','3','0','1','1','MODIFY','@@ -24,6 +24,6 @@  #include \"common.h\"  #include \"servers.h\"   -void sig_message_private(SERVER_REC *server, const char *msg, const char *nick, const char *address); +void sig_message_private(SERVER_REC *server, const char *msg, const char *nick, const char *address, const char *target);    #endif /* IRSSI_OTR_MODULE */ '),('irssi','1.1.1','7d7c6975edc13d6f285c00dfc8a4f1a98ffa6702','dequis','otr: add target param to the unencrypted \'message private\' signal  Same case as the previous commit.','otr-ops.c','248','49','1','1','MODIFY','@@ -183,7 +183,7 @@ static void ops_handle_msg_event(void *opdata, OtrlMessageEvent msg_event, ConnC  			 * submit a patch or email me a better way.  			 */  			signal_remove(\"message private\", (SIGNAL_FUNC) sig_message_private); -			signal_emit(\"message private\", 4, server, message, username, server->connrec->address); +			signal_emit(\"message private\", 5, server, message, username, server->connrec->address, server->nick);  			signal_add_first(\"message private\", (SIGNAL_FUNC) sig_message_private);  			break;  		case OTRL_MSGEVENT_RCVDMSG_UNRECOGNIZED: '),('irssi','1.1.1','a3b9e2370fd02eb6a400744e31d7db27c8fe92b8','ailin-nemui','up abi','common.h','42','0','1','1','MODIFY','@@ -6,7 +6,7 @@  #define IRSSI_GLOBAL_CONFIG \"irssi.conf\" /* config file name in /etc/ */  #define IRSSI_HOME_CONFIG \"config\" /* config file name in ~/.irssi/ */   -#define IRSSI_ABI_VERSION 15 +#define IRSSI_ABI_VERSION 16    #define DEFAULT_SERVER_ADD_PORT 6667  #define DEFAULT_SERVER_ADD_TLS_PORT 6697 ');
INSERT INTO `irssi` VALUES ('irssi','1.1.1','2830d91a89277052ee8e6978a41a7773c9fe5025','ailin-nemui','use precompiled libotr','.travis.yml','63','0','4','7','MODIFY','@@ -31,19 +31,16 @@ addons:    before_install:      - perl -V -    - wget https://otr.cypherpunks.ca/libotr-4.1.1.tar.gz -    - tar zxf libotr-*.tar.gz -    - cd libotr-* -    - ./configure --prefix $HOME/otr-prefix && make && make install -    - cd .. -    - ./autogen.sh --with-proxy --with-bot --with-perl=module --with-otr=yes --with-libotr-prefix=$HOME/otr-prefix/lib --with-libotr-inc-prefix=$HOME/otr-prefix/include +    - wget https://github.com/irssi-import/libotr/releases/download/4.1.1/travis-trusty-libotr-4.1.1.tar.gz +    - tar zxf travis-trusty-libotr*.tar.gz -C $HOME +    - ./autogen.sh --with-proxy --with-bot --with-perl=module --with-otr=yes --with-libotr-prefix=$HOME/otr-build/lib --with-libotr-inc-prefix=$HOME/otr-build/include      - make dist      - cd ..      - tar xaf */irssi-*.tar.*      - cd irssi-*    install: -    - ./configure --with-proxy --with-bot --with-perl=module --with-otr=yes --with-libotr-prefix=$HOME/otr-prefix/lib --with-libotr-inc-prefix=$HOME/otr-prefix/include --prefix=$HOME/irssi-build +    - ./configure --with-proxy --with-bot --with-perl=module --with-otr=yes --with-libotr-prefix=$HOME/otr-build/lib --with-libotr-inc-prefix=$HOME/otr-build/include --prefix=$HOME/irssi-build        $( $UNITTESTS && echo --enable-always-build-tests )      - make CFLAGS=\"-Wall -Werror -Werror=declaration-after-statement\"      - make install '),('irssi','1.1.1','c08685a1a3e4a719fcfe8f30a338a91db8c3b8d1','ailin-nemui','also reconnect lookup_servers','servers-reconnect.c','378','83','15','0','MODIFY','@@ -111,6 +111,21 @@ static int server_reconnect_timeout(void)  		}  	}   +	for (tmp = lookup_servers; tmp != NULL; tmp = next) { +		SERVER_REC *server = tmp->data; + +		next = tmp->next; +		if (server->connect_time + connect_timeout < now && +		    connect_timeout > 0) { +			if (server->connect_tag != -1) { +				g_source_remove(server->connect_tag); +				server->connect_tag = -1; +			} +			server->connection_lost = TRUE; +			server_connect_failed(server, \"Timeout\"); +		} +	} +  	/* If server_connect() removes the next reconnection in queue,  	   we\'re screwed. I don\'t think this should happen anymore, but just  	   to be sure we don\'t crash, do this safely. */ '),('irssi','1.1.1','c08685a1a3e4a719fcfe8f30a338a91db8c3b8d1','ailin-nemui','also reconnect lookup_servers','servers.c','547','133','1','0','MODIFY','@@ -426,6 +426,7 @@ int server_start_connect(SERVER_REC *server)  				    server_connect_callback_readpipe,  				    server);   +		server->connect_time = time(NULL);  		lookup_servers = g_slist_append(lookup_servers, server);    		signal_emit(\"server looking\", 1, server); '),('irssi','1.1.1','4501917cae4523d11fb049a5d20e5582bb0893ea','dequis','otr: rename module.c to otr-module.c','Makefile.am','34','0','1','1','MODIFY','@@ -21,7 +21,7 @@ libotr_core_la_LIBADD = $(OTR_LDFLAGS)    otr_sources = \\  	key.c \\ -	module.c \\ +	otr-module.c \\  	otr-formats.c \\  	otr-ops.c \\  	otr-fe.c \\ '),('irssi','1.1.1','4501917cae4523d11fb049a5d20e5582bb0893ea','dequis','otr: rename module.c to otr-module.c','otr-module.c','None','None','0','0','RENAME',''),('irssi','1.1.1','0b2f5b0a1e30932b07245c508a673eb0e9b6ab37','dequis','otr: fix blatant lies in help text','otr.in','73','0','1','1','MODIFY','@@ -1,7 +1,7 @@    OTR %|[OPTION]   -Command to control the OTR module. Without an option, this help is printed. +Command to control the OTR module. Without an option, /OTR INFO is printed.    This help contains three sections which are %9options, quickstart and files.%n   '),('irssi','1.1.1','407680efdc662787ecbd1297d1f4781acdb5168c','dequis','Add libgcrypt.m4 so autogen doesn\'t require libgcrypt-dev','libgcrypt.m4','130','0','143','0','ADD','@@ -0,0 +1,143 @@ +# libgcrypt.m4 - Autoconf macros to detect libgcrypt +# Copyright (C) 2002, 2003, 2004, 2011, 2014 g10 Code GmbH +# +# This file is free software; as a special exception the author gives +# unlimited permission to copy and/or distribute it, with or without +# modifications, as long as this notice is preserved. +# +# This file is distributed in the hope that it will be useful, but +# WITHOUT ANY WARRANTY, to the extent permitted by law; without even the +# implied warranty of MERCHANTABILITY or FITNESS FOR A PARTICULAR PURPOSE. +# +# Last-changed: 2014-10-02 + + +dnl AM_PATH_LIBGCRYPT([MINIMUM-VERSION, +dnl                   [ACTION-IF-FOUND [, ACTION-IF-NOT-FOUND ]]]) +dnl Test for libgcrypt and define LIBGCRYPT_CFLAGS and LIBGCRYPT_LIBS. +dnl MINIMUN-VERSION is a string with the version number optionalliy prefixed +dnl with the API version to also check the API compatibility. Example: +dnl a MINIMUN-VERSION of 1:1.2.5 won\'t pass the test unless the installed +dnl version of libgcrypt is at least 1.2.5 *and* the API number is 1.  Using +dnl this features allows to prevent build against newer versions of libgcrypt +dnl with a changed API. +dnl +dnl If a prefix option is not used, the config script is first +dnl searched in $SYSROOT/bin and then along $PATH.  If the used +dnl config script does not match the host specification the script +dnl is added to the gpg_config_script_warn variable. +dnl +AC_DEFUN([AM_PATH_LIBGCRYPT], +[ AC_REQUIRE([AC_CANONICAL_HOST]) +  AC_ARG_WITH(libgcrypt-prefix, +            AC_HELP_STRING([--with-libgcrypt-prefix=PFX], +                           [prefix where LIBGCRYPT is installed (optional)]), +     libgcrypt_config_prefix=\"$withval\", libgcrypt_config_prefix=\"\") +  if test x\"${LIBGCRYPT_CONFIG}\" = x ; then +     if test x\"${libgcrypt_config_prefix}\" != x ; then +        LIBGCRYPT_CONFIG=\"${libgcrypt_config_prefix}/bin/libgcrypt-config\" +     else +       case \"${SYSROOT}\" in +         /*) +           if test -x \"${SYSROOT}/bin/libgcrypt-config\" ; then +             LIBGCRYPT_CONFIG=\"${SYSROOT}/bin/libgcrypt-config\" +           fi +           ;; +         \'\') +           ;; +          *) +           AC_MSG_WARN([Ignoring \\$SYSROOT as it is not an absolute path.]) +           ;; +       esac +     fi +  fi + +  AC_PATH_PROG(LIBGCRYPT_CONFIG, libgcrypt-config, no) +  tmp=ifelse([$1], ,1:1.2.0,$1) +  if echo \"$tmp\" | grep \':\' >/dev/null 2>/dev/null ; then +     req_libgcrypt_api=`echo \"$tmp\"     | sed \'s/\\(.*\\):\\(.*\\)/\\1/\'` +     min_libgcrypt_version=`echo \"$tmp\" | sed \'s/\\(.*\\):\\(.*\\)/\\2/\'` +  else +     req_libgcrypt_api=0 +     min_libgcrypt_version=\"$tmp\" +  fi + +  AC_MSG_CHECKING(for LIBGCRYPT - version >= $min_libgcrypt_version) +  ok=no +  if test \"$LIBGCRYPT_CONFIG\" != \"no\" ; then +    req_major=`echo $min_libgcrypt_version | \\ +               sed \'s/\\([[0-9]]*\\)\\.\\([[0-9]]*\\)\\.\\([[0-9]]*\\)/\\1/\'` +    req_minor=`echo $min_libgcrypt_version | \\ +               sed \'s/\\([[0-9]]*\\)\\.\\([[0-9]]*\\)\\.\\([[0-9]]*\\)/\\2/\'` +    req_micro=`echo $min_libgcrypt_version | \\ +               sed \'s/\\([[0-9]]*\\)\\.\\([[0-9]]*\\)\\.\\([[0-9]]*\\)/\\3/\'` +    libgcrypt_config_version=`$LIBGCRYPT_CONFIG --version` +    major=`echo $libgcrypt_config_version | \\ +               sed \'s/\\([[0-9]]*\\)\\.\\([[0-9]]*\\)\\.\\([[0-9]]*\\).*/\\1/\'` +    minor=`echo $libgcrypt_config_version | \\ +               sed \'s/\\([[0-9]]*\\)\\.\\([[0-9]]*\\)\\.\\([[0-9]]*\\).*/\\2/\'` +    micro=`echo $libgcrypt_config_version | \\ +               sed \'s/\\([[0-9]]*\\)\\.\\([[0-9]]*\\)\\.\\([[0-9]]*\\).*/\\3/\'` +    if test \"$major\" -gt \"$req_major\"; then +        ok=yes +    else +        if test \"$major\" -eq \"$req_major\"; then +            if test \"$minor\" -gt \"$req_minor\"; then +               ok=yes +            else +               if test \"$minor\" -eq \"$req_minor\"; then +                   if test \"$micro\" -ge \"$req_micro\"; then +                     ok=yes +                   fi +               fi +            fi +        fi +    fi +  fi +  if test $ok = yes; then +    AC_MSG_RESULT([yes ($libgcrypt_config_version)]) +  else +    AC_MSG_RESULT(no) +  fi +  if test $ok = yes; then +     # If we have a recent libgcrypt, we should also check that the +     # API is compatible +     if test \"$req_libgcrypt_api\" -gt 0 ; then +        tmp=`$LIBGCRYPT_CONFIG --api-version 2>/dev/null || echo 0` +        if test \"$tmp\" -gt 0 ; then +           AC_MSG_CHECKING([LIBGCRYPT API version]) +           if test \"$req_libgcrypt_api\" -eq \"$tmp\" ; then +             AC_MSG_RESULT([okay]) +           else +             ok=no +             AC_MSG_RESULT([does not match. want=$req_libgcrypt_api got=$tmp]) +           fi +        fi +     fi +  fi +  if test $ok = yes; then +    LIBGCRYPT_CFLAGS=`$LIBGCRYPT_CONFIG --cflags` +    LIBGCRYPT_LIBS=`$LIBGCRYPT_CONFIG --libs` +    ifelse([$2], , :, [$2]) +    libgcrypt_config_host=`$LIBGCRYPT_CONFIG --host 2>/dev/null || echo none` +    if test x\"$libgcrypt_config_host\" != xnone ; then +      if test x\"$libgcrypt_config_host\" != x\"$host\" ; then +  AC_MSG_WARN([[ +*** +*** The config script $LIBGCRYPT_CONFIG was +*** built for $libgcrypt_config_host and thus may not match the +*** used host $host. +*** You may want to use the configure option --with-libgcrypt-prefix +*** to specify a matching config script or use \\$SYSROOT. +***]]) +        gpg_config_script_warn=\"$gpg_config_script_warn libgcrypt\" +      fi +    fi +  else +    LIBGCRYPT_CFLAGS=\"\" +    LIBGCRYPT_LIBS=\"\" +    ifelse([$3], , :, [$3]) +  fi +  AC_SUBST(LIBGCRYPT_CFLAGS) +  AC_SUBST(LIBGCRYPT_LIBS) +]) '),('irssi','1.1.1','8607a5fa78ba44db51cc75a020e1a53c4e04a057','dequis','signals.txt: add missing \'server cap new|delete\' signals  Emitted by cap_emit_signal in irc/core/irc-cap.c, added as part of the CAP 3.2 PR merged recentlyish.','signals.txt','276','0','2','0','MODIFY','@@ -132,6 +132,8 @@ irc-servers.c:  irc-cap.c   \"server cap ack \"<cmd>, SERVER_REC   \"server cap nak \"<cmd>, SERVER_REC + \"server cap new \"<cmd>, SERVER_REC + \"server cap delete \"<cmd>, SERVER_REC   \"server cap end\", SERVER_REC    sasl.c '),('irssi','1.1.1','af5ee997e7544eb59ed509ef8fb79ec449756e90','dequis','Add \"server cap req\" signal, emitted before requesting caps','signals.txt','275','0','1','0','MODIFY','@@ -133,6 +133,7 @@ irc-cap.c   \"server cap ack \"<cmd>, SERVER_REC   \"server cap nak \"<cmd>, SERVER_REC   \"server cap end\", SERVER_REC + \"server cap req\", SERVER_REC, char *caps    sasl.c   \"server sasl failure\", SERVER_REC, char *reason '),('irssi','1.1.1','af5ee997e7544eb59ed509ef8fb79ec449756e90','dequis','Add \"server cap req\" signal, emitted before requesting caps','irc-cap.c','203','52','11','3','MODIFY','@@ -48,11 +48,17 @@ int cap_toggle (IRC_SERVER_REC *server, char *cap, int enable)  		if (!g_hash_table_lookup_extended(server->cap_supported, cap, NULL, NULL))  			return FALSE;   +		signal_emit(\"server cap req\", 2, server, cap);  		irc_send_cmdv(server, \"CAP REQ %s\", cap);  		return TRUE;  	}  	else if (!enable && gslist_find_string(server->cap_active, cap)) { -		irc_send_cmdv(server, \"CAP REQ -%s\", cap); +		char *negcap = g_strdup_printf(\"-%s\", cap); + +		signal_emit(\"server cap req\", 2, server, negcap); +		irc_send_cmdv(server, \"CAP REQ %s\", negcap); + +		g_free(negcap);  		return TRUE;  	}   @@ -194,10 +200,12 @@ static void event_cap (IRC_SERVER_REC *server, char *args, char *nick, char *add  				server->cap_queue = NULL;    				/* If the server doesn\'t support any cap we requested close the negotiation here */ -				if (avail_caps > 0) +				if (avail_caps > 0) { +					signal_emit(\"server cap req\", 2, server, cmd->str + sizeof(\"CAP REQ :\") - 1);  					irc_send_cmd_now(server, cmd->str); -				else +				} else {  					cap_finish_negotiation(server); +				}    				g_string_free(cmd, TRUE);  			} '),('irssi','1.1.1','d0151fd5a21cf026afa67239ad79f23bc26f0a10','dequis','Add fe-cap to show messages for CAP-related events in the UI  Replaces cap.pl','Makefile.am','41','0','1','0','MODIFY','@@ -27,6 +27,7 @@ real_sources = \\  	fe-common-irc.c \\  	fe-whois.c \\  	fe-sasl.c \\ +	fe-cap.c \\  	irc-completion.c \\  	module-formats.c    '),('irssi','1.1.1','d0151fd5a21cf026afa67239ad79f23bc26f0a10','dequis','Add fe-cap to show messages for CAP-related events in the UI  Replaces cap.pl','fe-cap.c','50','9','84','0','ADD','@@ -0,0 +1,84 @@ +/* +    fe-cap.c : irssi + +    Copyright (C) 2018 dequis + +    This program is free software; you can redistribute it and/or modify +    it under the terms of the GNU General Public License as published by +    the Free Software Foundation; either version 2 of the License, or +    (at your option) any later version. + +    This program is distributed in the hope that it will be useful, +    but WITHOUT ANY WARRANTY; without even the implied warranty of +    MERCHANTABILITY or FITNESS FOR A PARTICULAR PURPOSE.  See the +    GNU General Public License for more details. + +    You should have received a copy of the GNU General Public License along +    with this program; if not, write to the Free Software Foundation, Inc., +    51 Franklin Street, Fifth Floor, Boston, MA 02110-1301 USA. +*/ + +#include \"module.h\" +#include \"module-formats.h\" +#include \"signals.h\" +#include \"levels.h\" +#include \"misc.h\" + +#include \"irc-servers.h\" + +#include \"printtext.h\" + +static const struct { +	const char *command; +	const int template; +} fe_cap_messages[] = { +	{\"LS\", IRCTXT_CAP_LS}, +	{\"ACK\", IRCTXT_CAP_ACK}, +	{\"NAK\", IRCTXT_CAP_NAK}, +	{\"LIST\", IRCTXT_CAP_LIST}, +	{\"NEW\", IRCTXT_CAP_NEW}, +	{\"DEL\", IRCTXT_CAP_DEL}, +}; + +static void event_cap(IRC_SERVER_REC *server, char *args, char *nick, char *address) +{ +	int i; +	char *params, *evt, *list, *star; + +	params = event_get_params(args, 4, NULL, &evt, &star, &list); + +	if (params == NULL) { +		return; +	} + +	/* With multiline CAP LS, if the \'*\' parameter isn\'t present, +	 * adjust the parameter pointer to compensate for this */ +	if (strcmp(star, \"*\") != 0 && list[0] == \'\\0\') { +		list = star; +	} + +	for (i = 0; i < G_N_ELEMENTS(fe_cap_messages); i++) { +		if (!g_ascii_strcasecmp(evt, fe_cap_messages[i].command)) { +			printformat(server, NULL, MSGLEVEL_CRAP, fe_cap_messages[i].template, list); +		} +	} + +	g_free(params); +} + +static void sig_server_cap_req(IRC_SERVER_REC *server, char *caps) +{ +	printformat(server, NULL, MSGLEVEL_CRAP, IRCTXT_CAP_REQ, caps); +} + +void fe_cap_init(void) +{ +	signal_add(\"event cap\", (SIGNAL_FUNC) event_cap); +	signal_add(\"server cap req\", (SIGNAL_FUNC) sig_server_cap_req); +} + +void fe_cap_deinit(void) +{ +	signal_remove(\"event cap\", (SIGNAL_FUNC) event_cap); +	signal_remove(\"server cap req\", (SIGNAL_FUNC) sig_server_cap_req); +} '),('irssi','1.1.1','d0151fd5a21cf026afa67239ad79f23bc26f0a10','dequis','Add fe-cap to show messages for CAP-related events in the UI  Replaces cap.pl','fe-common-irc.c','87','2','5','0','MODIFY','@@ -72,6 +72,9 @@ void fe_whois_deinit(void);  void fe_sasl_init(void);  void fe_sasl_deinit(void);   +void fe_cap_init(void); +void fe_cap_deinit(void); +  void irc_completion_init(void);  void irc_completion_deinit(void);   @@ -95,6 +98,7 @@ void fe_common_irc_init(void)  	fe_netjoin_init();          fe_whois_init();  	fe_sasl_init(); +	fe_cap_init();          irc_completion_init();    	settings_check(); @@ -121,6 +125,7 @@ void fe_common_irc_deinit(void)  	fe_netjoin_deinit();          fe_whois_deinit();  	fe_sasl_deinit(); +	fe_cap_deinit();          irc_completion_deinit();    	theme_unregister(); '),('irssi','1.1.1','d0151fd5a21cf026afa67239ad79f23bc26f0a10','dequis','Add fe-cap to show messages for CAP-related events in the UI  Replaces cap.pl','module-formats.c','128','0','7','0','MODIFY','@@ -46,6 +46,13 @@ FORMAT_REC fecommon_irc_formats[] = {  	{ \"setupserver_footer\", \"\", 0 },  	{ \"sasl_success\", \"SASL authentication succeeded\", 0 },  	{ \"sasl_error\", \"Cannot authenticate via SASL ($0)\", 1, { 0 } }, +	{ \"cap_req\",  \"Capabilities requested: $0\", 1, { 0 } }, +	{ \"cap_ls\",   \"Capabilities supported: $0\", 1, { 0 } }, +	{ \"cap_ack\",  \"Capabilities acknowledged: $0\", 1, { 0 } }, +	{ \"cap_nak\",  \"Capabilities refused: $0\", 1, { 0 } }, +	{ \"cap_list\", \"Capabilities currently enabled: $0\", 1, { 0 } }, +	{ \"cap_new\",  \"Capabilities now available: $0\", 1, { 0 } }, +	{ \"cap_del\",  \"Capabilities removed: $0\", 1, { 0 } },    	/* ---- */  	{ NULL, \"Channels\", 0 }, '),('irssi','1.1.1','d0151fd5a21cf026afa67239ad79f23bc26f0a10','dequis','Add fe-cap to show messages for CAP-related events in the UI  Replaces cap.pl','module-formats.h','127','0','7','0','MODIFY','@@ -24,6 +24,13 @@ enum {  	IRCTXT_SETUPSERVER_FOOTER,  	IRCTXT_SASL_SUCCESS,  	IRCTXT_SASL_ERROR, +	IRCTXT_CAP_REQ, +	IRCTXT_CAP_LS, +	IRCTXT_CAP_ACK, +	IRCTXT_CAP_NAK, +	IRCTXT_CAP_LIST, +	IRCTXT_CAP_NEW, +	IRCTXT_CAP_DEL,    	IRCTXT_FILL_2,   '),('irssi','1.1.1','8deb6182c692904733544568479035b31fe1007e','dequis','Make config_write more atomic to prevent truncation when out of space  This is modeled after glib\'s g_file_set_contents. It doesn\'t use that function directly because the writing is done with GIOChannel streaming-like writes and g_file_set_contents expects the whole thing to be in-memory.  Main differences with g_file_set_contents:  - complete lack of win32 special casing (cygwin/WSL should work though) - no fallocate() (linux only, but we don\'t know the size upfront, anyway) - always calls fsync (glib skips it on btrfs or when not overwriting)  Other than that, it\'s the same old mkstemp + fsync + rename.','write.c','264','84','41','4','MODIFY','@@ -301,30 +301,67 @@ int config_write(CONFIG_REC *rec, const char *fname, int create_mode)  {  	int ret;  	int fd; +	int save_errno; +	char *tmp_name; +	const char *dest_name;    	g_return_val_if_fail(rec != NULL, -1);          g_return_val_if_fail(fname != NULL || rec->fname != NULL, -1);          g_return_val_if_fail(create_mode != -1 || rec->create_mode != -1, -1);   -	fd = open(fname != NULL ? fname : rec->fname, +	dest_name = fname != NULL ? fname : rec->fname; +	tmp_name = g_strdup_printf(\"%s.XXXXXX\", dest_name); + +	fd = g_mkstemp_full(tmp_name,  			   O_WRONLY | O_TRUNC | O_CREAT,  			   create_mode != -1 ? create_mode : rec->create_mode); -	if (fd == -1) -		return config_error(rec, g_strerror(errno)); +	if (fd == -1) { +		config_error(rec, g_strerror(errno)); +		ret = -1; +		goto out; +	}    	rec->handle = g_io_channel_unix_new(fd);  	g_io_channel_set_encoding(rec->handle, NULL, NULL);  	g_io_channel_set_close_on_unref(rec->handle, TRUE); +  	rec->tmp_indent_level = 0;  	rec->tmp_last_lf = TRUE;          ret = config_write_block(rec, rec->mainnode, FALSE, TRUE); +	save_errno = errno; +  	if (ret == -1) {  		/* write error */ -		config_error(rec, errno == 0 ? \"bug\" : g_strerror(errno)); +		unlink(tmp_name); +		config_error(rec, save_errno == 0 ? \"bug\" : g_strerror(save_errno)); +		goto out; +	} + +	ret = fsync(fd); +	save_errno = errno; + +	if (ret == -1) { +		unlink(tmp_name); +		config_error(rec, g_strerror(errno)); +		goto out;  	}    	g_io_channel_unref(rec->handle);  	rec->handle = NULL;   +	if (rename(tmp_name, dest_name) == -1) { +		unlink(tmp_name); +		config_error(rec, g_strerror(errno)); +		goto out; +	} + +out: +	if (rec->handle) { +		g_io_channel_unref(rec->handle); +		rec->handle = NULL; +	} + +	g_free(tmp_name); +  	return ret;  } '),('irssi','1.1.1','34b31e8451829e7b515a60cf12183203a059d1c4','ailin-nemui','record line info on empty lines','formats.c','1138','354','9','0','MODIFY','@@ -1233,6 +1233,15 @@ void format_send_to_gui(TEXT_DEST_REC *dest, const char *text)  	dup = str = g_strdup(text);    	flags = 0; fgcolor = theme->default_color; bgcolor = -1; + +	if (*str == \'\\0\') { +		/* empty line, write line info only */ +		signal_emit_id(signal_gui_print_text, 6, dest->window, +			       GINT_TO_POINTER(fgcolor), +			       GINT_TO_POINTER(bgcolor), +			       GINT_TO_POINTER(flags), str, +			       dest); +	}  	while (*str != \'\\0\') {  		type = \'\\0\';  		for (ptr = str; *ptr != \'\\0\'; ptr++) { '),('irssi','1.1.1','34b31e8451829e7b515a60cf12183203a059d1c4','ailin-nemui','record line info on empty lines','textbuffer.c','470','139','0','3','MODIFY','@@ -355,9 +355,6 @@ LINE_REC *textbuffer_insert(TEXT_BUFFER_REC *buffer, LINE_REC *insert_after,  	g_return_val_if_fail(buffer != NULL, NULL);  	g_return_val_if_fail(data != NULL, NULL);   -	if (len == 0) -                return insert_after; -  	line = !buffer->last_eol ? insert_after :  		textbuffer_line_insert(buffer, insert_after);   '),('irssi','1.1.1','3075cbc6fb383024b87563611021f9fb712182fd','ailin-nemui','Fix test compilation on old glib  e.g. Debian 7','test-796.c','103','10','2','0','MODIFY','@@ -165,7 +165,9 @@ int main(int argc, char **argv)  		   server_destroy_flood_set_up, test_server_destroy_flood,  		   server_destroy_flood_tear_down);   +#if GLIB_CHECK_VERSION(2,38,0)  	g_test_set_nonfatal_assertions(); +#endif    	core_preinit(*argv);  	irssi_gui = IRSSI_GUI_NONE; '),('irssi','1.1.1','631650bd8ab467af8347b5d9b51b2b0193adfe63','ailin-nemui','Revert \"record line info on empty lines\"','formats.c','1131','353','0','9','MODIFY','@@ -1233,15 +1233,6 @@ void format_send_to_gui(TEXT_DEST_REC *dest, const char *text)  	dup = str = g_strdup(text);    	flags = 0; fgcolor = theme->default_color; bgcolor = -1; - -	if (*str == \'\\0\') { -		/* empty line, write line info only */ -		signal_emit_id(signal_gui_print_text, 6, dest->window, -			       GINT_TO_POINTER(fgcolor), -			       GINT_TO_POINTER(bgcolor), -			       GINT_TO_POINTER(flags), str, -			       dest); -	}  	while (*str != \'\\0\') {  		type = \'\\0\';  		for (ptr = str; *ptr != \'\\0\'; ptr++) { '),('irssi','1.1.1','631650bd8ab467af8347b5d9b51b2b0193adfe63','ailin-nemui','Revert \"record line info on empty lines\"','textbuffer.c','472',