ore_match_level(IGNORE_REC *rec, int level)  {  	if (level & FLAG_MSGLEVELS) {  		int flaglevel = level & FLAG_MSGLEVELS; -		return (level & rec->level) && (flaglevel & rec->level); +		int msglevel = level & ~FLAG_MSGLEVELS; +		return (msglevel & rec->level) && (flaglevel & rec->level);  	} else if (!(rec->level & FLAG_MSGLEVELS)) {  		return (level & rec->level);  	} else { '),('irssi','1.1.1','10f632664989117afe6d4496a419f712fbb4c3b6','David Phillips','Redraw on SIGWINCH when screen size hasn\'t changed  Some programs and users send SIGWINCH as a request for the client to redraw in the event of session detachment/reattachment (e.g. abduco). A well-formed terminal will only send SIGWINCH when the window size has changed, so there is no need to optimise this case out.','term.c','133','33','3','5','MODIFY','@@ -81,11 +81,9 @@ void term_resize_dirty(void)  	if (!term_get_size(&width, &height))  		width = height = -1;   -	if (height != term_height || width != term_width) { -		term_resize(width, height); -		mainwindows_resize(term_width, term_height); -		term_resize_final(width, height); -	} +	term_resize(width, height); +	mainwindows_resize(term_width, term_height); +	term_resize_final(width, height);  }    #ifdef SIGWINCH '),('irssi','1.1.1','764f8215a9dad09b93832fdbb0c87eb1b0e6e773','Lukas Waymann','Fix `/save` replacing symlinks with regular files  A side-effect of 8deb618 is that `/save` may replace configuration files that are symlinks with regular files.  Fix this by resolving all symlinks before renaming the temporary file.','write.c','272','85','11','1','MODIFY','@@ -304,6 +304,7 @@ int config_write(CONFIG_REC *rec, const char *fname, int create_mode)  	int save_errno;  	char *tmp_name;  	const char *dest_name; +	char *real_dest = NULL;    	g_return_val_if_fail(rec != NULL, -1);          g_return_val_if_fail(fname != NULL || rec->fname != NULL, -1); @@ -349,7 +350,15 @@ int config_write(CONFIG_REC *rec, const char *fname, int create_mode)  	g_io_channel_unref(rec->handle);  	rec->handle = NULL;   -	if (rename(tmp_name, dest_name) == -1) { +	/* expand all symlinks; else we may replace a symlink with a regular file */ +	real_dest = realpath(dest_name, NULL); +	if (real_dest == NULL) { +		unlink(tmp_name); +		config_error(rec, g_strerror(errno)); +		goto out; +	} + +	if (rename(tmp_name, real_dest) == -1) {  		unlink(tmp_name);  		config_error(rec, g_strerror(errno));  		goto out; @@ -362,6 +371,7 @@ out:  	}    	g_free(tmp_name); +	g_free(real_dest);    	return ret;  } '),('irssi','1.1.1','7d3eb47ab51a93defb267912704e84b3241b4db6','Lukas Waymann','Fix potential `rename(3)` across file systems  Make sure the temporary file in the `config_write` function is created on the same file system as the file we `rename` it to later.','write.c','270','85','12','14','MODIFY','@@ -302,15 +302,21 @@ int config_write(CONFIG_REC *rec, const char *fname, int create_mode)  	int ret;  	int fd;  	int save_errno; -	char *tmp_name; -	const char *dest_name; -	char *real_dest = NULL; +	char *tmp_name = NULL; +	char *dest_name = NULL;    	g_return_val_if_fail(rec != NULL, -1);          g_return_val_if_fail(fname != NULL || rec->fname != NULL, -1);          g_return_val_if_fail(create_mode != -1 || rec->create_mode != -1, -1);   -	dest_name = fname != NULL ? fname : rec->fname; +	/* expand all symlinks; else we may replace a symlink with a regular file */ +	dest_name = realpath(fname != NULL ? fname : rec->fname, NULL); +	if (dest_name == NULL) { +		config_error(rec, g_strerror(errno)); +		ret = -1; +		goto out; +	} +  	tmp_name = g_strdup_printf(\"%s.XXXXXX\", dest_name);    	fd = g_mkstemp_full(tmp_name, @@ -350,15 +356,7 @@ int config_write(CONFIG_REC *rec, const char *fname, int create_mode)  	g_io_channel_unref(rec->handle);  	rec->handle = NULL;   -	/* expand all symlinks; else we may replace a symlink with a regular file */ -	real_dest = realpath(dest_name, NULL); -	if (real_dest == NULL) { -		unlink(tmp_name); -		config_error(rec, g_strerror(errno)); -		goto out; -	} - -	if (rename(tmp_name, real_dest) == -1) { +	if (rename(tmp_name, dest_name) == -1) {  		unlink(tmp_name);  		config_error(rec, g_strerror(errno));  		goto out; @@ -371,7 +369,7 @@ out:  	}    	g_free(tmp_name); -	g_free(real_dest); +	g_free(dest_name);    	return ret;  } '),('irssi','1.1.1','817179a7606b616795cf67e24505526dded34381','ailin-nemui','Revert \"Flag topic as unset if it is zero length\"','.gitignore','56','0','0','1','MODIFY','@@ -59,7 +59,6 @@ tests/irc/core/test-irc.trs  *.la  *.lo  *.o -*.swp  *~    *.tar.bz2 '),('irssi','1.1.1','817179a7606b616795cf67e24505526dded34381','ailin-nemui','Revert \"Flag topic as unset if it is zero length\"','channel-events.c','275','68','10','12','MODIFY','@@ -126,25 +126,23 @@ static void channel_change_topic(IRC_SERVER_REC *server, const char *channel,    	chanrec = channel_find(SERVER(server), channel);  	if (chanrec == NULL) return; - -	g_free_and_null(chanrec->topic); -	g_free_and_null(chanrec->topic_by); -	chanrec->topic_time = 0; - -	/* the topic may be sent out encoded, so we need to +	/* the topic may be send out encoded, so we need to  	   recode it back or /topic <tab> will not work properly */  	recoded = recode_in(SERVER(server), topic, channel); -	if (recoded == NULL || *recoded == \'\\0\') { -		signal_emit(\"channel topic changed\", 1, chanrec); -		g_free(recoded); -		return; +	if (topic != NULL) { +		g_free_not_null(chanrec->topic); +		chanrec->topic = recoded == NULL ? NULL : g_strdup(recoded);  	} +	g_free(recoded);   -	chanrec->topic = recoded; +	g_free_not_null(chanrec->topic_by);  	chanrec->topic_by = g_strdup(setby); -	if (chanrec->topic_by != NULL) { + +	if (chanrec->topic_by == NULL) {  		/* ensure invariant topic_time > 0 <=> topic_by != NULL.  		   this could be triggered by a topic command without sender */ +		chanrec->topic_time = 0; +	} else {  		chanrec->topic_time = settime;  	}   '),('irssi','1.1.1','4f5f62ac63697d23e8a4d432f181a85c45a49d5e','Joseph Bisch','Add server-fuzz to fe-fuzz  Based on test-796.c in the tests directory.','Makefile.am','27','0','14','3','MODIFY','@@ -1,6 +1,6 @@  SUBDIRS = irc fe-common   -bin_PROGRAMS = irssi-fuzz +bin_PROGRAMS = irssi-fuzz server-fuzz    # Force link with CXX for libfuzzer support  CCLD=$(CXX) $(CXXFLAGS) @@ -21,7 +21,18 @@ irssi_fuzz_LDADD = \\    irssi_fuzz_SOURCES = \\          irssi.c \\ -	$(top_srcdir)/src/fe-text/module-formats.c +	../fe-text/module-formats.c + +server_fuzz_DEPENDENCIES = @COMMON_LIBS@ + +server_fuzz_LDADD = \\ +	@COMMON_LIBS@ \\ +	@PROG_LIBS@ \\ +	$(FUZZER_LIBS) + +server_fuzz_SOURCES = \\ +	server.c \\ +	../fe-text/module-formats.c    noinst_HEADERS = \\ -	$(top_srcdir)/src/fe-text/module-formats.h +	../fe-text/module-formats.h '),('irssi','1.1.1','4f5f62ac63697d23e8a4d432f181a85c45a49d5e','Joseph Bisch','Add server-fuzz to fe-fuzz  Based on test-796.c in the tests directory.','server.c','108','11','176','0','ADD','@@ -0,0 +1,176 @@ +/* + server.c : irssi + +    Copyright (C) 2018 Joseph Bisch + +    This program is free software; you can redistribute it and/or modify +    it under the terms of the GNU General Public License as published by +    the Free Software Foundation; either version 2 of the License, or +    (at your option) any later version. + +    This program is distributed in the hope that it will be useful, +    but WITHOUT ANY WARRANTY; without even the implied warranty of +    MERCHANTABILITY or FITNESS FOR A PARTICULAR PURPOSE.  See the +    GNU General Public License for more details. + +    You should have received a copy of the GNU General Public License along +    with this program; if not, write to the Free Software Foundation, Inc., +    51 Franklin Street, Fifth Floor, Boston, MA 02110-1301 USA. +*/ + +#include \"module.h\" +#include \"modules-load.h\" +#include \"module-formats.h\" +#include \"levels.h\" +#include \"themes.h\" +#include \"core.h\" +#include \"fe-common-core.h\" +#include \"args.h\" +#include \"printtext.h\" +#include \"misc.h\" +#include \"servers-setup.h\" + +#include <stddef.h> +#include <stdint.h> +#include <stdlib.h> +#include <string.h> +#include <unistd.h> + +#include \"irc.h\" +#include \"irc-servers.h\" +#include \"irc-channels.h\" + +/* irc.c */ +void irc_init(void); +void irc_deinit(void); + +/* irc-session.c */ +void irc_session_init(void); +void irc_session_deinit(void); + +/* fe-common-irc.c */ +void fe_common_irc_init(void); +void fe_common_irc_deinit(void); + +SERVER_REC *server; + +void event_connected(IRC_SERVER_REC *server, const char *data, const char *from) +{ +	char *params, *nick; + +	g_return_if_fail(server != NULL); + +	params = event_get_params(data, 1, &nick); + +	if (g_strcmp0(server->nick, nick) != 0) { +		/* nick changed unexpectedly .. connected via proxy, etc. */ +		g_free(server->nick); +		server->nick = g_strdup(nick); +	} + +	/* set the server address */ +	g_free(server->real_address); +	server->real_address = from == NULL ? +		g_strdup(server->connrec->address) : /* shouldn\'t happen.. */ +		g_strdup(from); + +	/* last welcome message found - commands can be sent to server now. */ +	server->connected = 1; +	server->real_connect_time = time(NULL); + +	/* let the queue send now that we are identified */ +	g_get_current_time(&server->wait_cmd); + +	if (server->connrec->usermode != NULL) { +		/* Send the user mode, before the autosendcmd. +		 * Do not pass this through cmd_mode because it +		 * is not known whether the resulting MODE message +		 * (if any) is the initial umode or a reply to this. +		 */ +		irc_send_cmdv(server, \"MODE %s %s\", server->nick, +				server->connrec->usermode); +		g_free_not_null(server->wanted_usermode); +		server->wanted_usermode = g_strdup(server->connrec->usermode); +	} + +	signal_emit(\"event connected\", 1, server); +	g_free(params); +} + +void irc_server_init_bare_minimum(IRC_SERVER_REC *server) { +	server->isupport = g_hash_table_new((GHashFunc) g_istr_hash, +					    (GCompareFunc) g_istr_equal); + +	/* set the standards */ +	g_hash_table_insert(server->isupport, g_strdup(\"CHANMODES\"), g_strdup(\"beI,k,l,imnpst\")); +	g_hash_table_insert(server->isupport, g_strdup(\"PREFIX\"), g_strdup(\"(ohv)@%+\")); +} + +void test_server() { +	//SERVER_REC *server; /* = g_new0(IRC_SERVER_REC, 1); */ +	CHAT_PROTOCOL_REC *proto; +	SERVER_CONNECT_REC *conn; + +	proto = chat_protocol_find(\"IRC\"); +	conn = server_create_conn(proto->id, \"localhost\", 0, \"\", \"\", \"user\"); +	server = proto->server_init_connect(conn); +	server->session_reconnect = TRUE; +	server->tag = g_strdup(\"testserver\"); + +	/* we skip some initialisations that would try to send data */ +	/* irc_servers_deinit(); */ +	irc_session_deinit(); +	irc_irc_deinit(); + +	server_connect_finished(server); + +	/* make up for the skipped session init */ +	irc_server_init_bare_minimum(IRC_SERVER(server)); + +	irc_irc_init(); +	irc_session_init(); +	/* irc_servers_init(); */ + +	server_connect_unref(conn); +} + +int LLVMFuzzerInitialize(int *argc, char ***argv) { +	core_register_options(); +	fe_common_core_register_options(); +	/* no args */ +	args_execute(0, NULL); +	core_preinit((*argv)[0]); +	core_init(); +	irc_init(); +	fe_common_core_init(); +	fe_common_irc_init(); +	signal_add(\"event 001\", (SIGNAL_FUNC) event_connected); +	module_register(\"core\", \"fe-fuzz\"); +	return 0; +} + +int LLVMFuzzerTestOneInput(const uint8_t* data, size_t size) { +	if (size < 1) return 0; + +	test_server(); +	gboolean prefixedChoice = (gboolean)*data; +	gchar *copy = g_strndup((const gchar *)data+1, size-1); +	gchar **lines = g_strsplit(copy, \"\\r\\n\", -1); +	gchar **head = lines; + +	for (; *lines != NULL; lines++) { +		gchar *prefixedLine; +		if (prefixedChoice) { +			prefixedLine = g_strdup_printf(\":user %s\\n\", *lines); +		} else { +			prefixedLine = g_strdup_printf(\"%s\\n\", *lines); +		} +		signal_emit(\"server incoming\", 2, server, prefixedLine); +		g_free(prefixedLine); +	} + +	g_strfreev(head); +	g_free(copy); +	server_unref(server); +	return 0; +} '),('irssi','1.1.1','a19b503a31fa4fb31c2e6ee79ffdc18bf4a87db0','ailin-nemui','properly record line info on empty lines','formats.c','1135','354','7','0','MODIFY','@@ -1233,6 +1233,13 @@ void format_send_to_gui(TEXT_DEST_REC *dest, const char *text)  	dup = str = g_strdup(text);    	flags = 0; fgcolor = theme->default_color; bgcolor = -1; + +	if (*str == \'\\0\') { +		/* empty line, write line info only */ +		signal_emit_id(signal_gui_print_text, 6, dest->window, GINT_TO_POINTER(fgcolor), +		               GINT_TO_POINTER(bgcolor), GINT_TO_POINTER(flags), str, dest); +	} +  	while (*str != \'\\0\') {  		type = \'\\0\';  		for (ptr = str; *ptr != \'\\0\'; ptr++) { '),('irssi','1.1.1','a19b503a31fa4fb31c2e6ee79ffdc18bf4a87db0','ailin-nemui','properly record line info on empty lines','gui-printtext.c','276','62','9','3','MODIFY','@@ -307,9 +307,15 @@ static void sig_gui_print_text(WINDOW_REC *window, void *fgcolor,  	}  	textbuffer_line_add_colors(view->buffer, &insert_after, fg, bg, flags);   -	insert_after = textbuffer_insert(view->buffer, insert_after, -					 (unsigned char *) str, -					 strlen(str), &lineinfo); +	/* for historical reasons, the \\n will set +	   GUI_PRINT_FLAG_NEWLINE and print an empty string. in this +	   special case, ignore the empty string which would otherwise +	   start another new line */ +	if (~flags & GUI_PRINT_FLAG_NEWLINE || *str != \'\\0\') { +		insert_after = textbuffer_insert(view->buffer, insert_after, (unsigned char *) str, +		                                 strlen(str), &lineinfo); +	} +  	if (gui->use_insert_after)                  gui->insert_after = insert_after;  } '),('irssi','1.1.1','a19b503a31fa4fb31c2e6ee79ffdc18bf4a87db0','ailin-nemui','properly record line info on empty lines','textbuffer.c','471','139','2','4','MODIFY','@@ -333,8 +333,9 @@ void textbuffer_line_add_colors(TEXT_BUFFER_REC *buffer, LINE_REC **line,  		data[pos++] = LINE_CMD_INDENT;  	}   -        if (pos > 0) +	if (pos > 0) {  		*line = textbuffer_insert(buffer, *line, data, pos, NULL); +	}    	buffer->last_flags = flags;  } @@ -355,9 +356,6 @@ LINE_REC *textbuffer_insert(TEXT_BUFFER_REC *buffer, LINE_REC *insert_after,  	g_return_val_if_fail(buffer != NULL, NULL);  	g_return_val_if_fail(data != NULL, NULL);   -	if (len == 0) -                return insert_after; -  	line = !buffer->last_eol ? insert_after :  		textbuffer_line_insert(buffer, insert_after);   '),('irssi','1.1.1','af1e4d6e34317452ac32380bed795aecb00f5d88','ailin-nemui','initialise the line to zeros','textbuffer.c','471','139','1','1','MODIFY','@@ -192,7 +192,7 @@ static LINE_REC *textbuffer_line_create(TEXT_BUFFER_REC *buffer)  	if (buffer->cur_text == NULL)                  text_chunk_create(buffer);   -	rec = g_slice_new(LINE_REC); +	rec = g_slice_new0(LINE_REC);  	rec->text = buffer->cur_text->buffer + buffer->cur_text->pos;    	buffer->cur_text->refcount++; '),('irssi','1.1.1','0d8632943d8fb526fbd4d853c86294d0037c43f6','dequis','Add a wrapper of wcwidth() that picks the best implementation  This adds a i_wcwidth() function that replaces mk_wcwidth(), and a \'wcwidth_implementation\' setting to pick which one it wraps.  Values:  - old: uses our local mk_wcwidth() which implements unicode 5.0 - system: uses the libc-provided wcwidth(), which may be better or worse   than ours depending on how up to date the system is. - auto: tests the system one against two characters that became   fullwidth in unicode 5.2 and 9.0 respectively. If either of them pass,   pick the system implementation, otherwise pick ours.  It defaults to auto.  mk_wcwidth() is still preferable in some cases, since the way it uses ranges for fullwidth characters means most CJK blocks are covered even if their characters didn\'t exist back then.  The \"system\" implementation is also wrapped to never return -1, but to assume those unknown characters use one cell. Quoting the code:      /* Treat all unknown characters as taking one cell. This is      * the reason mk_wcwidth and other outdated implementations      * mostly worked with newer unicode, while glibc\'s wcwidth      * needs updating to recognize new characters.      *      * Instead of relying on that, we keep the behavior of assuming      * one cell even for glibc\'s implementation, which is still      * highly accurate and less of a headache overall.      */','Makefile.am','113','0','1','0','MODIFY','@@ -53,6 +53,7 @@ libcore_a_SOURCES = \\  	utf8.c \\  	$(regex_impl) \\  	wcwidth.c \\ +	wcwidth-wrapper.c \\  	tls.c \\  	write-buffer.c   '),('irssi','1.1.1','0d8632943d8fb526fbd4d853c86294d0037c43f6','dequis','Add a wrapper of wcwidth() that picks the best implementation  This adds a i_wcwidth() function that replaces mk_wcwidth(), and a \'wcwidth_implementation\' setting to pick which one it wraps.  Values:  - old: uses our local mk_wcwidth() which implements unicode 5.0 - system: uses the libc-provided wcwidth(), which may be better or worse   than ours depending on how up to date the system is. - auto: tests the system one against two characters that became   fullwidth in unicode 5.2 and 9.0 respectively. If either of them pass,   pick the system implementation, otherwise pick ours.  It defaults to auto.  mk_wcwidth() is still preferable in some cases, since the way it uses ranges for fullwidth characters means most CJK blocks are covered even if their characters didn\'t exist back then.  The \"system\" implementation is also wrapped to never return -1, but to assume those unknown characters use one cell. Quoting the code:      /* Treat all unknown characters as taking one cell. This is      * the reason mk_wcwidth and other outdated implementations      * mostly worked with newer unicode, while glibc\'s wcwidth      * needs updating to recognize new characters.      *      * Instead of relying on that, we keep the behavior of assuming      * one cell even for glibc\'s implementation, which is still      * highly accurate and less of a headache overall.      */','core.c','228','26','5','0','MODIFY','@@ -60,6 +60,9 @@ void chat_commands_deinit(void);  void log_away_init(void);  void log_away_deinit(void);   +void wcwidth_wrapper_init(void); +void wcwidth_wrapper_deinit(void); +  int irssi_gui;  int irssi_init_finished;  int reload_config; @@ -258,6 +261,7 @@ void core_init(void)  	nicklist_init();    	chat_commands_init(); +	wcwidth_wrapper_init();    	settings_add_str(\"misc\", \"ignore_signals\", \"\");  	settings_add_bool(\"misc\", \"override_coredump_limit\", FALSE); @@ -281,6 +285,7 @@ void core_deinit(void)  	signal_remove(\"setup changed\", (SIGNAL_FUNC) read_settings);  	signal_remove(\"irssi init finished\", (SIGNAL_FUNC) sig_irssi_init_finished);   +	wcwidth_wrapper_deinit();  	chat_commands_deinit();    	nicklist_deinit(); '),('irssi','1.1.1','0d8632943d8fb526fbd4d853c86294d0037c43f6','dequis','Add a wrapper of wcwidth() that picks the best implementation  This adds a i_wcwidth() function that replaces mk_wcwidth(), and a \'wcwidth_implementation\' setting to pick which one it wraps.  Values:  - old: uses our local mk_wcwidth() which implements unicode 5.0 - system: uses the libc-provided wcwidth(), which may be better or worse   than ours depending on how up to date the system is. - auto: tests the system one against two characters that became   fullwidth in unicode 5.2 and 9.0 respectively. If either of them pass,   pick the system implementation, otherwise pick ours.  It defaults to auto.  mk_wcwidth() is still preferable in some cases, since the way it uses ranges for fullwidth characters means most CJK blocks are covered even if their characters didn\'t exist back then.  The \"system\" implementation is also wrapped to never return -1, but to assume those unknown characters use one cell. Quoting the code:      /* Treat all unknown characters as taking one cell. This is      * the reason mk_wcwidth and other outdated implementations      * mostly worked with newer unicode, while glibc\'s wcwidth      * needs updating to recognize new characters.      *      * Instead of relying on that, we keep the behavior of assuming      * one cell even for glibc\'s implementation, which is still      * highly accurate and less of a headache overall.      */','utf8.c','82','20','1','1','MODIFY','@@ -36,7 +36,7 @@ int string_advance(char const **str, int policy)  		c = g_utf8_get_char(*str);  		*str = g_utf8_next_char(*str);   -		return unichar_isprint(c) ? mk_wcwidth(c) : 1; +		return unichar_isprint(c) ? i_wcwidth(c) : 1;  	} else {  		/* Assume TREAT_STRING_AS_BYTES: */  		*str += 1; '),('irssi','1.1.1','0d8632943d8fb526fbd4d853c86294d0037c43f6','dequis','Add a wrapper of wcwidth() that picks the best implementation  This adds a i_wcwidth() function that replaces mk_wcwidth(), and a \'wcwidth_implementation\' setting to pick which one it wraps.  Values:  - old: uses our local mk_wcwidth() which implements unicode 5.0 - system: uses the libc-provided wcwidth(), which may be better or worse   than ours depending on how up to date the system is. - auto: tests the system one against two characters that became   fullwidth in unicode 5.2 and 9.0 respectively. If either of them pass,   pick the system implementation, otherwise pick ours.  It defaults to auto.  mk_wcwidth() is still preferable in some cases, since the way it uses ranges for fullwidth characters means most CJK blocks are covered even if their characters didn\'t exist back then.  The \"system\" implementation is also wrapped to never return -1, but to assume those unknown characters use one cell. Quoting the code:      /* Treat all unknown characters as taking one cell. This is      * the reason mk_wcwidth and other outdated implementations      * mostly worked with newer unicode, while glibc\'s wcwidth      * needs updating to recognize new characters.      *      * Instead of relying on that, we keep the behavior of assuming      * one cell even for glibc\'s implementation, which is still      * highly accurate and less of a headache overall.      */','utf8.h','14','0','6','0','MODIFY','@@ -12,8 +12,14 @@  typedef guint32 unichar;    /* Returns width for character (0-2). */ +int i_wcwidth(unichar c); + +/* Older variant of the above */  int mk_wcwidth(unichar c);   +/* Signature for wcwidth implementations */ +typedef int (*WCWIDTH_FUNC) (unichar ucs); +  /* Advance the str pointer one character further; return the number of columns   * occupied by the skipped character.   */ '),('irssi','1.1.1','0d8632943d8fb526fbd4d853c86294d0037c43f6','dequis','Add a wrapper of wcwidth() that picks the best implementation  This adds a i_wcwidth() function that replaces mk_wcwidth(), and a \'wcwidth_implementation\' setting to pick which one it wraps.  Values:  - old: uses our local mk_wcwidth() which implements unicode 5.0 - system: uses the libc-provided wcwidth(), which may be better or worse   than ours depending on how up to date the system is. - auto: tests the system one against two characters that became   fullwidth in unicode 5.2 and 9.0 respectively. If either of them pass,   pick the system implementation, otherwise pick ours.  It defaults to auto.  mk_wcwidth() is still preferable in some cases, since the way it uses ranges for fullwidth characters means most CJK blocks are covered even if their characters didn\'t exist back then.  The \"system\" implementation is also wrapped to never return -1, but to assume those unknown characters use one cell. Quoting the code:      /* Treat all unknown characters as taking one cell. This is      * the reason mk_wcwidth and other outdated implementations      * mostly worked with newer unicode, while glibc\'s wcwidth      * needs updating to recognize new characters.      *      * Instead of relying on that, we keep the behavior of assuming      * one cell even for glibc\'s implementation, which is still      * highly accurate and less of a headache overall.      */','wcwidth-wrapper.c','59','12','117','0','ADD','@@ -0,0 +1,117 @@ +/* + wcwidth-wrapper.c : irssi + +    Copyright (C) 2018 dequis + +    This program is free software; you can redistribute it and/or modify +    it under the terms of the GNU General Public License as published by +    the Free Software Foundation; either version 2 of the License, or +    (at your option) any later version. + +    This program is distributed in the hope that it will be useful, +    but WITHOUT ANY WARRANTY; without even the implied warranty of +    MERCHANTABILITY or FITNESS FOR A PARTICULAR PURPOSE.  See the +    GNU General Public License for more details. + +    You should have received a copy of the GNU General Public License along +    with this program; if not, write to the Free Software Foundation, Inc., +    51 Franklin Street, Fifth Floor, Boston, MA 02110-1301 USA. +*/ + +#define _XOPEN_SOURCE +#include <wchar.h> + +#include \"module.h\" +#include \"signals.h\" +#include \"settings.h\" +#include \"utf8.h\" + +/* wcwidth=2 since unicode 5.2.0 */ +#define UNICODE_SQUARE_HIRAGANA_HOKA 0x1F200 + +/* wcwidth=2 since unicode 9.0.0 */ +#define UNICODE_IRSSI_LOGO 0x1F525 + +enum { +	WCWIDTH_IMPL_AUTO = 0, +	WCWIDTH_IMPL_OLD, +	WCWIDTH_IMPL_SYSTEM, +}; + +WCWIDTH_FUNC wcwidth_impl_func = mk_wcwidth; + +int i_wcwidth(unichar ucs) +{ +	return (*wcwidth_impl_func)(ucs); +} + +static int system_wcwidth(unichar ucs) +{ +	int retval = wcwidth((wchar_t) ucs); + +	if (retval < 0) { +		/* Treat all unknown characters as taking one cell. This is +		 * the reason mk_wcwidth and other outdated implementations +		 * mostly worked with newer unicode, while glibc\'s wcwidth +		 * needs updating to recognize new characters. +		 * +		 * Instead of relying on that, we keep the behavior of assuming +		 * one cell even for glibc\'s implementation, which is still +		 * highly accurate and less of a headache overall. +		 */ +		return 1; +	} + +	return retval; +} + +static void read_settings(void) +{ +	static int choice = -1; +	int newchoice; + +	newchoice = settings_get_choice(\"wcwidth_implementation\"); + +	if (choice == newchoice) { +		return; +	} + +	choice = newchoice; + +	switch (choice) { +	case WCWIDTH_IMPL_AUTO: +		/* Test against characters that have wcwidth=2 +		 * since unicode 5.2 and 9.0 respectively */ + +		if (system_wcwidth(UNICODE_SQUARE_HIRAGANA_HOKA) == 2 || +		    system_wcwidth(UNICODE_IRSSI_LOGO) == 2) { +			wcwidth_impl_func = &system_wcwidth; +		} else { +			/* Fall back to our own (which implements 5.0) */ +			wcwidth_impl_func = &mk_wcwidth; +		} +		break; + +	case WCWIDTH_IMPL_OLD: +		wcwidth_impl_func = &mk_wcwidth; +		break; + +	case WCWIDTH_IMPL_SYSTEM: +		wcwidth_impl_func = &system_wcwidth; +		break; +	} + +} + +void wcwidth_wrapper_init(void) +{ +	settings_add_choice(\"misc\", \"wcwidth_implementation\", WCWIDTH_IMPL_AUTO, \"auto;old;system\"); + +	read_settings(); +	signal_add(\"setup changed\", (SIGNAL_FUNC) read_settings); +} + +void wcwidth_wrapper_deinit(void) +{ +	signal_remove(\"setup changed\", (SIGNAL_FUNC) read_settings); +} '),('irssi','1.1.1','0d8632943d8fb526fbd4d853c86294d0037c43f6','dequis','Add a wrapper of wcwidth() that picks the best implementation  This adds a i_wcwidth() function that replaces mk_wcwidth(), and a \'wcwidth_implementation\' setting to pick which one it wraps.  Values:  - old: uses our local mk_wcwidth() which implements unicode 5.0 - system: uses the libc-provided wcwidth(), which may be better or worse   than ours depending on how up to date the system is. - auto: tests the system one against two characters that became   fullwidth in unicode 5.2 and 9.0 respectively. If either of them pass,   pick the system implementation, otherwise pick ours.  It defaults to auto.  mk_wcwidth() is still preferable in some cases, since the way it uses ranges for fullwidth characters means most CJK blocks are covered even if their characters didn\'t exist back then.  The \"system\" implementation is also wrapped to never return -1, but to assume those unknown characters use one cell. Quoting the code:      /* Treat all unknown characters as taking one cell. This is      * the reason mk_wcwidth and other outdated implementations      * mostly worked with newer unicode, while glibc\'s wcwidth      * needs updating to recognize new characters.      *      * Instead of relying on that, we keep the behavior of assuming      * one cell even for glibc\'s implementation, which is still      * highly accurate and less of a headache overall.      */','gui-entry.c','1179','370','8','8','MODIFY','@@ -51,7 +51,7 @@ static unichar i_tolower(unichar c)  static int i_isalnum(unichar c)  {  	if (term_type == TERM_TYPE_UTF8) -		return (g_unichar_isalnum(c) || mk_wcwidth(c) == 0); +		return (g_unichar_isalnum(c) || i_wcwidth(c) == 0);  	return c <= 255 ? isalnum(c) : 0;  }   @@ -219,7 +219,7 @@ static int pos2scrpos(GUI_ENTRY_REC *entry, int pos, int cursor)  		if (term_type == TERM_TYPE_BIG5)  			xpos += big5_width(c);  		else if (entry->utf8) -			xpos += unichar_isprint(c) ? mk_wcwidth(c) : 1; +			xpos += unichar_isprint(c) ? i_wcwidth(c) : 1;  		else  			xpos++;   @@ -246,7 +246,7 @@ static int scrpos2pos(GUI_ENTRY_REC *entry, int pos)  		if (term_type == TERM_TYPE_BIG5)  			width = big5_width(c);  		else if (entry->utf8) -			width = unichar_isprint(c) ? mk_wcwidth(c) : 1; +			width = unichar_isprint(c) ? i_wcwidth(c) : 1;  		else  			width = 1;   @@ -373,7 +373,7 @@ static void gui_entry_draw_from(GUI_ENTRY_REC *entry, int pos)  		else if (term_type == TERM_TYPE_BIG5)  			new_xpos += big5_width(c);  		else if (entry->utf8) -			new_xpos += unichar_isprint(c) ? mk_wcwidth(c) : 1; +			new_xpos += unichar_isprint(c) ? i_wcwidth(c) : 1;  		else  			new_xpos++;   @@ -647,7 +647,7 @@ void gui_entry_insert_char(GUI_ENTRY_REC *entry, unichar chr)  	if (chr == 0 || chr == 13 || chr == 10)  		return; /* never insert NUL, CR or LF characters */   -	if (entry->utf8 && entry->pos == 0 && mk_wcwidth(chr) == 0) +	if (entry->utf8 && entry->pos == 0 && i_wcwidth(chr) == 0)  		