g(void)  {          return irssi_config_file;  }   +void set_irssi_config(char *const s) +{ +	irssi_config_file = s; +} +  static void sig_reload_config(int signo)  {          reload_config = TRUE; '),('irssi','1.1.1','2d6033542e0e47072d61ef442430c3014e3b430f','Will Storey','Add functions to override irssi dir and config  Setting up to test is easier this way.','core.h','11','0','3','0','MODIFY','@@ -16,6 +16,9 @@ extern int irssi_init_finished; /* TRUE after \"irssi init finished\" signal is se  extern int reload_config; /* TRUE after received SIGHUP. */  extern time_t client_start_time;   +void set_irssi_dir(char *const); +void set_irssi_config(char *const); +  void core_preinit(const char *path);    void core_register_options(void); '),('irssi','1.1.1','2ccb312b8b56db9585a4801184c064bfd3e3ff90','Will Storey','Expose functions for testing','Makefile.am','58','0','1','0','MODIFY','@@ -40,6 +40,7 @@ pkginc_irc_coredir=$(pkgincludedir)/src/irc/core  pkginc_irc_core_HEADERS = \\  	bans.h \\          ctcp.h \\ +	channel-events.h \\          channel-rejoin.h \\          irc.h \\          irc-channels.h \\ '),('irssi','1.1.1','2ccb312b8b56db9585a4801184c064bfd3e3ff90','Will Storey','Expose functions for testing','channel-events.c','295','71','4','3','MODIFY','@@ -21,6 +21,7 @@  #include \"module.h\"  #include \"signals.h\"  #include \"misc.h\" +#include \"channel-events.h\"  #include \"channels-setup.h\"  #include \"settings.h\"  #include \"recode.h\" @@ -176,7 +177,7 @@ static void set_topic_info(CHANNEL_REC *const chanrec, char const *const setby,  	}  }   -static void event_topic_get(IRC_SERVER_REC *server, const char *data) +void event_topic_get(IRC_SERVER_REC *server, const char *data)  {  	char *params, *channel, *topic;   @@ -187,7 +188,7 @@ static void event_topic_get(IRC_SERVER_REC *server, const char *data)  	g_free(params);  }   -static void event_topic(IRC_SERVER_REC *server, const char *data, +void event_topic(IRC_SERVER_REC *server, const char *data,  			const char *nick, const char *addr)  {  	char *params, *channel, *topic, *mask; @@ -202,7 +203,7 @@ static void event_topic(IRC_SERVER_REC *server, const char *data,  	g_free(params);  }   -static void event_topic_info(IRC_SERVER_REC *server, const char *data) +void event_topic_info(IRC_SERVER_REC *server, const char *data)  {  	char *params, *channel, *topicby, *topictime;  	time_t t; '),('irssi','1.1.1','2ccb312b8b56db9585a4801184c064bfd3e3ff90','Will Storey','Expose functions for testing','channel-events.h','5','0','12','0','ADD','@@ -0,0 +1,12 @@ +#ifndef __CHANNEL_EVENTS_H +#define __CHANNEL_EVENTS_H + +#include \"irc.h\" + +/* Not private for tests. */ +void event_topic_get(IRC_SERVER_REC *, const char *); +void event_topic(IRC_SERVER_REC *, const char *, +			const char *, const char *); +void event_topic_info(IRC_SERVER_REC *, const char *); + +#endif '),('irssi','1.1.1','6f38d67d87f2ef443172cca4d9ee82300d9f5ca4','Will Storey','Add tests for channel change events','Makefile.am','23','0','7','4','MODIFY','@@ -8,21 +8,24 @@ AM_CPPFLAGS = \\  	-DSYSCONFDIR=\\\"\"$(sysconfdir)\"\\\" \\  	$(GLIB_CFLAGS)   -test_programs = test-irc +test_programs = test-channel-events test-irc   -test_irc_CPPFLAGS = \\ +CPPFLAGS = \\  	-I$(top_srcdir)/src/irc/core \\  	$(AM_CPPFLAGS)   -test_irc_DEPENDENCIES = \\ +DEPENDENCIES = \\  	../../../src/core/libcore.a \\  	../../../src/lib-config/libirssi_config.a   -test_irc_LDADD = \\ +LDADD = \\  	../../../src/irc/core/libirc_core.a \\  	../../../src/core/libcore.a \\  	../../../src/lib-config/libirssi_config.a \\  	@PROG_LIBS@   +test_channel_events_SOURCES = \\ +	test-channel-events.c +  test_irc_SOURCES = \\  	test-irc.c '),('irssi','1.1.1','6f38d67d87f2ef443172cca4d9ee82300d9f5ca4','Will Storey','Add tests for channel change events','test-channel-events.c','142','10','202','0','ADD','@@ -0,0 +1,202 @@ +/* + test-irc.c : irssi + +    Copyright (C) 2018 Will Storey + +    This program is free software; you can redistribute it and/or modify +    it under the terms of the GNU General Public License as published by +    the Free Software Foundation; either version 2 of the License, or +    (at your option) any later version. + +    This program is distributed in the hope that it will be useful, +    but WITHOUT ANY WARRANTY; without even the implied warranty of +    MERCHANTABILITY or FITNESS FOR A PARTICULAR PURPOSE.  See the +    GNU General Public License for more details. + +    You should have received a copy of the GNU General Public License along +    with this program; if not, write to the Free Software Foundation, Inc., +    51 Franklin Street, Fifth Floor, Boston, MA 02110-1301 USA. +*/ + +#include <glib.h> + +#include <channel-events.h> +#include <common.h> +#include <core.h> +#include <irc.h> +#include <irc-channels.h> +#include <irc-servers.h> +#include <modules.h> +#include <recode.h> +#include <settings.h> +#include <signals.h> +#include <time.h> + +#define MODULE_NAME \"test-channel-events\" + +typedef struct { +	char const *const description; +	char const *const input; +	char const *const topic; +	char const *const topic_by; +	time_t const topic_time; +} topic_test_case; + +static void test_event_topic_get(topic_test_case const *const); +static void test_event_topic(topic_test_case const *const); +static void test_event_topic_info(topic_test_case const *const); +static void setup(void); +static void teardown(void); + +static IRC_SERVER_REC *server; +static CHANNEL_REC *channel; + +topic_test_case const event_topic_get_test_cases[] = { +	{ +		.description = \"Normal 332 message with a topic with multiple words\", +		.input       = \"testnick #test :new topic\", +		.topic       = \"new topic\", +		.topic_by    = NULL, +		.topic_time  = 0, +	}, +}; + +topic_test_case const event_topic_info_test_cases[] = { +	{ +		.description = \"Normal 333 message\", +		.input       = \"testnick #test newnick!user@example.com 1533866229\", +		.topic       = \"initial topic\", +		.topic_by    = \"newnick!user@example.com\", +		.topic_time  = 1533866229, +	}, +}; + +topic_test_case const event_topic_test_cases[] = { +	{ +		.description = \"Normal TOPIC message\", +		.input       = \"#test :new topic\", +		.topic       = \"new topic\", +		.topic_by    = \"newnick!user@example.com\", +		.topic_time  = 0, /* Dynamic */ +	}, +}; + +int main(int argc, char **argv) +{ +	int i, res; + +	g_test_init(&argc, &argv, NULL); + +	modules_init(); +	signals_init(); +	set_irssi_dir(\"/tmp/irssi\"); +	set_irssi_config(\"/tmp/irssi/config\"); +	settings_init(); +	recode_init(); + +	settings_add_str(\"lookandfeel\", \"term_charset\", \"UTF-8\"); +	recode_update_charset(); + +	for (i = 0; i < G_N_ELEMENTS(event_topic_get_test_cases); i++) { +		char *const name = g_strdup_printf(\"/test/event_topic_get/%d\", i); +		g_test_add_data_func(name, &event_topic_get_test_cases[i], +				(GTestDataFunc)test_event_topic_get); +		g_free(name); +	} + +	for (i = 0; i < G_N_ELEMENTS(event_topic_test_cases); i++) { +		char *const name = g_strdup_printf(\"/test/event_topic/%d\", i); +		g_test_add_data_func(name, &event_topic_test_cases[i], +				(GTestDataFunc)test_event_topic); +		g_free(name); +	} + +	for (i = 0; i < G_N_ELEMENTS(event_topic_info_test_cases); i++) { +		char *const name = g_strdup_printf(\"/test/event_topic_info/%d\", i); +		g_test_add_data_func(name, &event_topic_info_test_cases[i], +				(GTestDataFunc)test_event_topic_info); +		g_free(name); +	} + +#if GLIB_CHECK_VERSION(2,38,0) +	g_test_set_nonfatal_assertions(); +#endif +	res = g_test_run(); + +	recode_deinit(); +	settings_deinit(); +	signals_deinit(); +	modules_deinit(); + +	return res; +} + +static void test_event_topic_get(topic_test_case const *const test) +{ +	setup(); + +	event_topic_get(server, test->input); + +	g_assert_cmpstr(channel->topic,      ==, test->topic); +	g_assert_cmpstr(channel->topic_by,   ==, test->topic_by); +	g_assert_cmpint(channel->topic_time, ==, test->topic_time); + +	teardown(); +} + +static void test_event_topic(topic_test_case const *const test) +{ +	time_t now; + +	setup(); + +	now = time(NULL); +	event_topic(server, test->input, \"newnick\", \"user@example.com\"); + +	g_assert_cmpstr(channel->topic,      ==, test->topic); +	g_assert_cmpstr(channel->topic_by,   ==, test->topic_by); +	g_assert_cmpint(channel->topic_time, >=, now); + +	teardown(); +} + +static void test_event_topic_info(topic_test_case const *const test) +{ +	setup(); + +	event_topic_info(server, test->input); + +	g_assert_cmpstr(channel->topic,      ==, test->topic); +	g_assert_cmpstr(channel->topic_by,   ==, test->topic_by); +	g_assert_cmpint(channel->topic_time, ==, test->topic_time); + +	teardown(); +} + +static void setup(void) +{ +	server = g_new0(IRC_SERVER_REC, 1); +	MODULE_DATA_INIT(server); +	server->type = module_get_uniq_id(\"SERVER\", 0); + +	channel = g_new0(CHANNEL_REC, 1); +	channel->name = \"#test\"; +	server->channels = g_slist_append(server->channels, channel); + +	g_assert_nonnull(channel_find(SERVER(server), \"#test\")); + +	channel->topic = g_strdup(\"initial topic\"); +	channel->topic_by = g_strdup(\"initialnick!user@example.com\"); +	channel->topic_time = 123; +} + +static void teardown(void) +{ +	g_slist_free(server->channels); +	MODULE_DATA_DEINIT(server); +	g_free(server); + +	g_free(channel->topic); +	g_free(channel->topic_by); +	g_free(channel); +} '),('irssi','1.1.1','0edb64c4dc731065705f3303307f20b3ccb5674a','Will Storey','Add test files to .gitignore','.gitignore','72','0','17','2','MODIFY','@@ -51,8 +51,23 @@ src/perl/*/Makefile.old  src/fe-fuzz/crash-*  src/fe-fuzz/oom-*   -tests/irc/core/test-irc -tests/irc/core/test-irc.trs +/core +/tests/fe-common/core/test-formats +/tests/fe-common/core/test-formats.log +/tests/fe-common/core/test-formats.trs +/tests/fe-common/core/test-suite.log +/tests/irc/core/core +/tests/irc/core/test-channel-events +/tests/irc/core/test-channel-events.log +/tests/irc/core/test-channel-events.trs +/tests/irc/core/test-irc +/tests/irc/core/test-irc.log +/tests/irc/core/test-irc.trs +/tests/irc/core/test-suite.log +/tests/irc/flood/test-796 +/tests/irc/flood/test-796.log +/tests/irc/flood/test-796.trs +/tests/irc/flood/test-suite.log    *.a  *.bs '),('irssi','1.1.1','b11932d24f01703d07eb0f7a0de6c226ebcde195','Will Storey','Make channel event functions static again','channel-events.c','295','71','3','3','MODIFY','@@ -177,7 +177,7 @@ static void set_topic_info(CHANNEL_REC *const chanrec, char const *const setby,  	}  }   -void event_topic_get(IRC_SERVER_REC *server, const char *data) +static void event_topic_get(IRC_SERVER_REC *server, const char *data)  {  	char *params, *channel, *topic;   @@ -188,7 +188,7 @@ void event_topic_get(IRC_SERVER_REC *server, const char *data)  	g_free(params);  }   -void event_topic(IRC_SERVER_REC *server, const char *data, +static void event_topic(IRC_SERVER_REC *server, const char *data,  			const char *nick, const char *addr)  {  	char *params, *channel, *topic, *mask; @@ -203,7 +203,7 @@ void event_topic(IRC_SERVER_REC *server, const char *data,  	g_free(params);  }   -void event_topic_info(IRC_SERVER_REC *server, const char *data) +static void event_topic_info(IRC_SERVER_REC *server, const char *data)  {  	char *params, *channel, *topicby, *topictime;  	time_t t; '),('irssi','1.1.1','b11932d24f01703d07eb0f7a0de6c226ebcde195','Will Storey','Make channel event functions static again','channel-events.h','3','0','2','5','MODIFY','@@ -3,10 +3,7 @@    #include \"irc.h\"   -/* Not private for tests. */ -void event_topic_get(IRC_SERVER_REC *, const char *); -void event_topic(IRC_SERVER_REC *, const char *, -			const char *, const char *); -void event_topic_info(IRC_SERVER_REC *, const char *); +void channel_events_init(void); +void channel_events_deinit(void);    #endif '),('irssi','1.1.1','7b09cac9ec0ce7e549ae8fecf4db8b8d811c18b1','Will Storey','Call functions via signals rather than directly  This allows us to leave functions static.','test-channel-events.c','145','10','6','3','MODIFY','@@ -93,6 +93,7 @@ int main(int argc, char **argv)  	set_irssi_config(\"/tmp/irssi/config\");  	settings_init();  	recode_init(); +	channel_events_init();    	settings_add_str(\"lookandfeel\", \"term_charset\", \"UTF-8\");  	recode_update_charset(); @@ -123,6 +124,7 @@ int main(int argc, char **argv)  #endif  	res = g_test_run();   +	channel_events_deinit();  	recode_deinit();  	settings_deinit();  	signals_deinit(); @@ -135,7 +137,7 @@ static void test_event_topic_get(topic_test_case const *const test)  {  	setup();   -	event_topic_get(server, test->input); +	signal_emit(\"event 332\", 2, server, test->input);    	g_assert_cmpstr(channel->topic,      ==, test->topic);  	g_assert_cmpstr(channel->topic_by,   ==, test->topic_by); @@ -151,7 +153,8 @@ static void test_event_topic(topic_test_case const *const test)  	setup();    	now = time(NULL); -	event_topic(server, test->input, \"newnick\", \"user@example.com\"); +	signal_emit(\"event topic\", 4, server, test->input, \"newnick\", +			\"user@example.com\");    	g_assert_cmpstr(channel->topic,      ==, test->topic);  	g_assert_cmpstr(channel->topic_by,   ==, test->topic_by); @@ -164,7 +167,7 @@ static void test_event_topic_info(topic_test_case const *const test)  {  	setup();   -	event_topic_info(server, test->input); +	signal_emit(\"event 333\", 2, server, test->input);    	g_assert_cmpstr(channel->topic,      ==, test->topic);  	g_assert_cmpstr(channel->topic_by,   ==, test->topic_by); '),('irssi','1.1.1','82702de047237550d1299cb9b7461a952e6cc74d','ailin-nemui','Add information about crash on unload to perl.txt  reported by urchlay: http://urchlay.naptime.net/repos/misc-scripts/tree/selfunload.pl','perl.txt','1062','0','1','0','MODIFY','@@ -1192,6 +1192,7 @@ Client->{}   Bugs and Limitations   --------------------  * Calling die in \'script error\' handler causes segfault (#101) +* Calling \"script unload\" from your own script causes segfault  * Storing and later using any Irssi object may result in use-after-free related crash    - Workaround: always acquire fresh objects  * Calling $dcc->close from the \"dcc created\" signal will cause unstable behaviour and crashes (#386) '),('irssi','1.1.1','3351c54a2f8ac2add41ba7ce829c215a65d3c923','dequis','Fix /save not working if the config didn\'t previously exist  realpath() was supposed to resolve symlinks but it also fails with ENOENT (no such file or directory) if the file just isn\'t there.','write.c','277','86','13','4','MODIFY','@@ -302,6 +302,7 @@ int config_write(CONFIG_REC *rec, const char *fname, int create_mode)  	int ret;  	int fd;  	int save_errno; +	const char *base_name;  	char *tmp_name = NULL;  	char *dest_name = NULL;   @@ -309,12 +310,20 @@ int config_write(CONFIG_REC *rec, const char *fname, int create_mode)          g_return_val_if_fail(fname != NULL || rec->fname != NULL, -1);          g_return_val_if_fail(create_mode != -1 || rec->create_mode != -1, -1);   +	base_name = fname != NULL ? fname : rec->fname; +  	/* expand all symlinks; else we may replace a symlink with a regular file */ -	dest_name = realpath(fname != NULL ? fname : rec->fname, NULL); +	dest_name = realpath(base_name, NULL); +  	if (dest_name == NULL) { -		config_error(rec, g_strerror(errno)); -		ret = -1; -		goto out; +		if (errno == ENOENT) { +			dest_name = g_strdup(base_name); +			errno = 0; +		} else { +			config_error(rec, g_strerror(errno)); +			ret = -1; +			goto out; +		}  	}    	tmp_name = g_strdup_printf(\"%s.XXXXXX\", dest_name); '),('irssi','1.1.1','e2e27d1809684a6745d7b449561aeb5f30a42a74','dequis','Improve error message when failing to create the config dir','settings.c','694','161','2','1','MODIFY','@@ -762,7 +762,8 @@ static void init_configfile(void)  	if (stat(get_irssi_dir(), &statbuf) != 0) {  		/* ~/.irssi not found, create it. */  		if (g_mkdir_with_parents(get_irssi_dir(), 0700) != 0) { -			g_error(\"Couldn\'t create %s directory\", get_irssi_dir()); +			g_error(\"Couldn\'t create %s directory: %s\", +			        get_irssi_dir(), g_strerror(errno));  		}  	} else if (!S_ISDIR(statbuf.st_mode)) {  		g_error(\"%s is not a directory.\\n\" '),('irssi','1.1.1','04497ed69d35cf44b0700437bf427f48dbc42871','Dominyk Tiller','configure: fix Perl detection on macOS Mojave  Apple has significantly changed the way `perl` is structured in macOS Mojave/10.14, which is due to ship stable this month. The `perl` restructuring has been an issue for a while but I recently obtained confirmation from Apple the changes were intentional & consequently not something that was going to be walked back before Mojave reaches its stable release.  As of 10.14 the Perl headers are moving inside the SDK, instead of residing in `/System` directly. There\'s a flag to tell Clang to look inside the SDK without projects having to explicitly locate the SDK & fiddle with the location themselves, which is `-iwithsysroot`, and that\'s what `perl` outputs now when `configure` checks `perl -MExtUtils::Embed -e ccopts`:  ```   -g -pipe -fno-common -DPERL_DARWIN -fno-strict-aliasing -fstack-protector  -iwithsysroot /System/Library/Perl/5.18/darwin-thread-multi-2level/CORE ```  The latter bit of that was previously `-I/System/Library/Perl/5.18/darwin-thread-multi-2level/CORE`. The problem here is that `configure` filters out flags that start with a lowercase `i` and consequently the Perl elements fail to build. This tiny patch fixes that issue, restoring Perl support to `irssi` when built on macOS 10.14.','configure.ac','779','4','2','2','MODIFY','@@ -388,8 +388,8 @@ if test \"$want_perl\" != \"no\"; then  		dnl * fix those command line options a bit so GCC won\'t  		dnl * complain about them. Normally there\'s only few options  		dnl * that we want to keep: -		dnl * -Ddefine -Uundef -I/path -fopt -mopt -		PERL_CFLAGS=`echo $PERL_CFLAGS | $perlpath -pe \'s/^(.* )?-@<:@^DUIfm@:>@@<:@^ @:>@+/\\1/g; s/^(.* )?\\+@<:@^ @:>@+/\\1/g\'` +		dnl * -Ddefine -Uundef -I/path -fopt -mopt -iwithsysroot +		PERL_CFLAGS=`echo $PERL_CFLAGS | $perlpath -pe \'s/^(.* )?-@<:@^DUIifm@:>@@<:@^ @:>@+/\\1/g; s/^(.* )?\\+@<:@^ @:>@+/\\1/g\'`    		PERL_EXTRA_OPTS=\"CCCDLFLAGS=\\\"-fPIC\\\"\"  		AC_SUBST(PERL_EXTRA_OPTS) '),('irssi','1.1.1','329448939ba8f9cc9803d4b3045d517b0ab0f9aa','ailin-nemui','Fix gnu wcwidth','wcwidth-wrapper.c','67','15','1','1','MODIFY','@@ -18,7 +18,7 @@      51 Franklin Street, Fifth Floor, Boston, MA 02110-1301 USA.  */   -#define _XOPEN_SOURCE +#define _GNU_SOURCE  #include <wchar.h>    #include \"module.h\" '),('irssi','1.1.1','3f0a4ef1970a164d7bce64e2fa887036fd162236','ailin-nemui','Remove incorrectly copied automake commands from fuzzer','Makefile.am','27','0','0','13','MODIFY','@@ -10,19 +10,6 @@ AM_CPPFLAGS = \\  	-I$(top_srcdir)/src/fe-common/core/ \\  	$(GLIB_CFLAGS)   -AM_DEPENDENCIES = \\ -	../../../core/libcore.a \\ -	../../../lib-config/libirssi_config.a \\ -	../../../irc/libirc.a \\ -	../../../irc/core/libirc_core.a \\ -	../../../irc/dcc/libirc_dcc.a \\ -	../../../irc/flood/libirc_flood.a \\ -	../../../irc/notifylist/libirc_notifylist.a \\ -	../../../fe-common/core/libfe_common_core.a \\ -	../../../fe-common/irc/libfe_common_irc.a \\ -	../../../fe-common/irc/dcc/libfe_irc_dcc.a \\ -	../../../fe-common/irc/notifylist/libfe_irc_notifylist.a -  LDADD = \\  	../../../irc/libirc.a \\  	../../../irc/core/libirc_core.a \\ '),('irssi','1.1.1','3f0a4ef1970a164d7bce64e2fa887036fd162236','ailin-nemui','Remove incorrectly copied automake commands from fuzzer','Makefile.am','27','0','0','13','MODIFY','@@ -10,19 +10,6 @@ AM_CPPFLAGS = \\  	-I$(top_srcdir)/src/fe-common/core/ \\  	$(GLIB_CFLAGS)   -AM_DEPENDENCIES = \\ -	../../../core/libcore.a \\ -	../../../lib-config/libirssi_config.a \\ -	../../../irc/libirc.a \\ -	../../../irc/core/libirc_core.a \\ -	../../../irc/dcc/libirc_dcc.a \\ -	../../../irc/flood/libirc_flood.a \\ -	../../../irc/notifylist/libirc_notifylist.a \\ -	../../../fe-common/core/libfe_common_core.a \\ -	../../../fe-common/irc/libfe_common_irc.a \\ -	../../../fe-co