st_lf && isblank(arr[i])) {  			/* whitespace, ignore */ -		} else if (arr[i] == \'\\n\') { +		} else if (isnewline(arr[i])) {  			if (!last_lf && i+1 != buf->len &&  			    isblank(arr[i+1])) {  				last_lf_pos = dest; -				*dest++ = \' \'; +				if (i != 0 && !isblank(arr[i-1])) +					*dest++ = \' \';  			} else {  				*dest++ = \'\\n\'; /* double-LF */  				line_len = 0; @@ -287,7 +289,7 @@ static void paste_send(void)  		/* first line has to be kludged kind of to get pasting in the  		   middle of line right.. */  		for (i = 0; i < paste_buffer->len; i++) { -			if (arr[i] == \'\\r\' || arr[i] == \'\\n\') { +			if (isnewline(arr[i])) {  				i++;  				break;  			} @@ -303,7 +305,7 @@ static void paste_send(void)  	/* rest of the lines */  	str = g_string_new(NULL);  	for (; i < paste_buffer->len; i++) { -		if (arr[i] == \'\\r\' || arr[i] == \'\\n\') { +		if (isnewline(arr[i])) {  			paste_send_line(str->str);  			g_string_truncate(str, 0);  		} else if (active_entry->utf8) { @@ -769,7 +771,7 @@ static void paste_bracketed_end(int i, gboolean rest)    	last_char = g_array_index(paste_buffer, unichar, i - 1);   -	if (paste_line_count > 0 && last_char != \'\\n\' && last_char != \'\\r\') { +	if (paste_line_count > 0 && !isnewline(last_char)) {  		/* there are newlines, but there\'s also stuff after the newline  		 * adjust line count to reflect this */  		paste_line_count++; '),('irssi','1.1.1','60d2364989b50ed8b76076022dde39690dc95416','ailin-nemui','run syncscripts.sh  sync buf.pl to version 3.00 sync usercount.pl to version 1.20','buf.pl','109','10','58','61','MODIFY','@@ -1,22 +1,24 @@  use strict;  use vars qw($VERSION %IRSSI); +use Storable; +use 5.014000;    use Irssi qw(command signal_add signal_add_first active_win               settings_get_str settings_get_bool channels windows -	     settings_add_str settings_add_bool get_irssi_dir -	     window_find_refnum signal_stop); -$VERSION = \'2.20\'; +             settings_add_str settings_add_bool get_irssi_dir + 